INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(1, 13, '达州市召开2016年经济形势新闻发布会', '<p class="MsoNormal" align="left"><span lang="EN-US">2017</span><span>年<span lang="EN-US">1</span>月<span lang="EN-US">23</span>日下午，达州市政府新闻办召开<span lang="EN-US">2016</span>年经济形势新闻发布会，邀请市统计局党组书记、局长李大兵向省、市媒体发布达州市<span lang="EN-US">2016</span>年经济形势相关情况，新闻发布会由市政府副秘书长黄中平主持。</span></p><p class="MsoNormal" align="left"><span><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017012409122422.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017012409122422.jpg" width="800" height="533"></a></span></p><p class="MsoNormal" align="left"><span><span lang="EN-US"><o:p></o:p></span></span>&nbsp;</p><p class="MsoNormal" align="left"><span lang="EN-US"><a href="http://10.51.136.10/Article/UploadFiles/201701/2017012317201621.jpg" target="_blank"><span>&nbsp;</span></a>&nbsp;&nbsp; &nbsp;</span><span>会上，李大兵发布了<span lang="EN-US">2016</span>年达州经济形势，<span lang="EN-US">2016</span>年面对错综复杂的宏观经济环境，市委、市政府认真贯彻落实党中央、国务院、省委、省政府各项决策部署和要求，坚持稳中求进的总基调，全面做好稳增长、调结构、促改革、惠民生、防风险等各项工作，全市经济呈现出“稳中有进、稳中向好”的发展态势，实现了“十三五”的良好开局。全年达州实现地区生产总值<span lang="EN-US">1447.08</span>亿元，按可比价格计算，同比增长<span lang="EN-US">7.5%</span>。其中，第一产业增加值<span lang="EN-US">310.02</span>亿元，增长<span lang="EN-US">4.1%</span>；第二产业增加值<span lang="EN-US">606.2</span>亿元，增长<span lang="EN-US">7.6%</span>；第三产业增加值<span lang="EN-US">530.87</span>亿元，增长<span lang="EN-US">9.3%</span>。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>随后，李大兵还就全市经济运行特点、经济结构转型升级进展、经济实现较快增长民众获得感等问题回答了记者提问和交流解读。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>黄中平对进一步做好经济数据发布和新闻宣传工作提出要求。一是统计部门继续发挥统计“数据库”作用，为保持全市经济平稳较快发展提供准确的统计数据、科学的判断和政策依据；二是省、市新闻媒体要对发布的数据和提供的信息，进行大力采写、宣传、策划，做好深度宣传报道，最大限度地发挥统计数据的社会效用。</span></p><p class="MsoNormal" align="left"><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span>省、市<span lang="EN-US">13</span>家新闻媒体和市统计局全体干部职工共<span lang="EN-US">50</span>余人参加会议。</span></p>', 1488045217, 0, 1, NULL, '/2017-02-26/58b1c49d66851.jpg', '', 0, '政治部', '本站', 0),
(2, 13, '达州市委副书记、市长郭亨孝率队到省统计局汇报工作', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>近日，市委副书记、市长郭亨孝带队到省统计局汇报工作。省统计局党组书记、局长陈炜等领导听取汇报。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">会上</font>, 何政副市长就达州2016年经济运行情况、市第四次党代会确定的“十三五”“12335”发展思路和今年以来经济社会发展状况及统计工作开展等情况进行了汇报。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>郭亨孝市长对省统计局给予达州的大力支持表示衷心感谢，重点就当前达州经济运行情况交换意见。他希望，省统计局的各位领导和专家多到达州实地调研，对达州的发展给予更多的关注、指导和支持。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>陈炜局长对达州党委政府对统计工作的重视、关心与支持表示感谢。他指出，面对严峻复杂的宏观形势，达州抵住了经济下行压力，统计部门在抓工作、抓发展，执行力强，很有精气神，同时要在原有的基础上进一步推进工作，不断深化统计改革。他表示，省统计局将积极支持达州做好科学统计工作，组织相关处室和专家进行研究审定，高度关注达州的发展，支持达州工作。省统计局巡视员黄国芹及相关处室负责人就有关方面的情况发表了意见和建议。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>市政府分管副市长、市政府秘书长，市统计局、市经信委、市商务局主要负责人参加了汇报会。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911203996.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911203996.jpg" width="480" height="319"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911204939.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911204939.jpg" width="480" height="319"></a></o:p></span></p>', 1488045186, 0, 1, NULL, '/2017-02-26/58b1c47f029f6.jpg', '', 0, '政治部', '本站', 0),
(3, 13, '达州市全面开展农业普查正式入户登记工作', '<p class="MsoNormal"><span>根据国家方案规定和上级要求，达州市在<span lang="EN-US">2017</span>年<span lang="EN-US">1</span>月<span lang="EN-US">1</span>日正式开展了农业普查入户登记工作。广大普查人员放弃元旦假日休息，不畏严寒，全力奋战在农普第一线：大竹县、万源市、经开区<span lang="EN-US">3</span>个县（市、区）政府领导莅临登记现场，慰问普查人员、查看登记工作、提出工作要求；渠县举行了正式登记启动仪式，营造了浓厚的登记氛围；<span lang="EN-US">8</span>个县（市、区）统计局、农普办领导和工作人员全部到一线巡查，指导登记业务、解决登记问题、确保登记质量；普查人员手持电子终端（<span lang="EN-US">PDA</span>）走村窜户访农问农，查“三农”实情，为国家全面建设小康社会、脱贫攻坚需要的“三农”基础数据而努力工作着。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>根据市农普办的统一安排部署，凡是已经进行了<span lang="EN-US">PDA</span>业务培训并完成了<span lang="EN-US">PDA</span>网上注册的普查人员，从<span lang="EN-US">1</span>月<span lang="EN-US">1</span>日起即可开展入户登记工作，登记按照“先慢后快，总结完善，整体推进，按期完成”的原则进行。元旦假日期间的登记带有熟悉<span lang="EN-US">PDA</span>操作技能和程序、发现问题、总结经验的试验登记性质，<span lang="EN-US">1</span>月<span lang="EN-US">1</span>日—<span lang="EN-US">3</span>日，全市共完成了<span lang="EN-US">2144</span>户农户的登记工作，后期将逐渐加快进度，确保<span lang="EN-US">3</span>月底前全面完成登记工作任务。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412542202.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412542202.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412544060.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412544060.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412551355.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412551355.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412553203.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412553203.jpg" width="600" height="450"></a></o:p></span></p>', 1488045149, 0, 1, NULL, '/2017-02-26/58b1c45995f71.jpg', '', 0, '政治部', '本站', 0),
(4, 13, '达州市统计局召开全市建设领域统计方法制度培训会', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">近日，达州市统计局召开了</font>2016年全市建设领域统计年报和2017年定期报表培训会。市统计局副局长赵英出席会议并作重要讲话。会议由工交投资科科长屈纪勇主持。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>会上，市统计局专业人员分别传达了全省建设领域统计年报会及全省建设领域统计数据处理程序培训会会议精神；总结了今年全市投资、房地产与建筑业统计工作；就建设领域统计报表制度和项目入库管理等重点工作进行了详细讲解培训；并听取了各县（市、区）统计局分管领导对完成全年目标任务的情况汇报。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">最后，市统计局副局长赵英作了总结讲话，肯定了全市建设领域统计工作取得的成绩，对进一步做好建设领域统计工作提出要求：一是用</font>“三统”（即统领、统筹、统计）理念推进建设领域统计工作再上新台阶；二是全力确保全年目标任务的完成；三是及时向主要领导、主管部门传达汇报省市年报会精神。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">各县（市、区）分管领导和专业人员共</font>30人参会。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911243518.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911243518.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911244878.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911244878.jpg" width="600" height="450"></a></o:p></span></p>', 1488045114, 0, 0, NULL, '/2017-02-26/58b1c437b2935.jpg', '', 0, '政治部', '本站', 0),
(79, 12, '达州成功举办全市统计系统“农普杯”第二届职工运动会', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">为进一步推动达州市统计文化建设进程，发扬求真奉献、团结创新的统计精神，培育积极向上、活泼健康的统计文化，达州市统计局机关工会于</font>10月23日至24日在宣汉县组织开展了全市统计系统“农普杯”第二届职工运动会。市统计局机关党委书记王树江主持开幕式，宣汉县县委常委、工会主席马春霞致开幕词，裁判员代表、运动员代表分别进行了宣誓，最后市统计局常务副局长顾万东宣布职工运动会开幕。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">本次运动会得到了达州市统计系统干部职工的热烈欢迎和积极参与。运动会共设置三人制篮球、羽毛球团体赛、乒乓球团体赛等项目，市级机关、七个县（市、区）和经开区共</font>8支代表队近150余名运动员参与了比赛。赛场上，运动员挥汗如雨、奋勇争先，展现了更高、更快、更强的体育精神。此次比赛赛出了成绩、赛出了水平，集中展示了统计系统团结拼搏、勇于争先的良好精神风貌；裁判员兢兢业业、一丝不苟，充分展示了统计系统求真务实、严谨细致的工作作风；加油助威的啦啦队激情澎湃、喊声震天，展示了统计系统昂扬饱满、积极向上的工作热情。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>本次活动丰富了职工业余文体活动，加强了广大干部、职工感情沟通和团结协作意识培养，进一步增强了统计系统凝聚力战斗力。也将促进大家运动健身常态化，进一步促进机关文化建设，以更加奋发有为的精神状态，为达州统计事业再上新台阶作出更大的贡献。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201610/2016103110234261.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201610/2016103110234261.jpg" width="600" height="400"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201610/2016103110241492.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201610/2016103110241492.jpg" width="600" height="400"></a></o:p></span></p>', 1488009629, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(80, 12, '达州市人民政府批准发布农业普查公告', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>近日，达州市政府批准发布了达州市第三次全国农业普查公告。公告包括农业普查的登记对象、普查内容、普查时点、登记原则、普查对象和普查人员的义务和责任、咨询监督电话等内容。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">达州市农业普查办公室将统一印刷市政府批准发布的农业普查公告，发至县（市、区）、乡镇（街道）、村（社区）、村（居</font>)民小组，由各地广泛张贴，进一步宣传动员，让广大普查对象知晓并支持配合普查工作，对农业普查正在开的清查摸底底和2017年的正式登记将起到有力的推动作用。</span></p>', 1488009698, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(81, 12, '四川省统计局人口处抽查达州市人口变动调查工作', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>11月9日至11日，四川省统计局人口就业处副调研员康兰一行莅临达州抽查和指导我市人口变动调查工作。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>检查组一行在听取了市、县工作情况汇报后，深入达州大竹县姚市乡建福村、宣汉县清溪镇龙伏村实地查看调查员入户登记情况，与调查指导员、调查员及调查对象亲切交谈，对调查过程中遇到的问题，现场加以指导解决。检查组一行在充分肯定达州市人口变动调查工作的同时，提出三点要求：一是要加快工作进度，查漏补缺，做好调查登记工作；二是加强数据审核，确保源头数据质量，重点核实空户、流动人口以及出生死亡人口等情况；三是严格按照国家局、省局统一工作部署，按时按质完成此次人口变动调查。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>达州</span><span>市统计局局长李大兵同志、副局长柏丰同志、总统计师刘玲同志及相关业务科室负责人陪同检查。</span></p>', 1488009717, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(82, 12, '四川省建设领域统计年报会在达州顺利召开', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>11月17日，</span><span>四川</span><span><font face="宋体">省投资建设领域统计年报会在达州宾馆召开。会上传达了全国建设领域统计年报会议精神，全面总结了</font>2016年全省建设领域统计开展情况，并对2016年年报和2017年定期报表制度修订情况及投资、房地产、建筑业报表存在的问题进行了讲解和规范。下午，各市州就如何提高建设领域统计数据质量进行了交流发言；最后，省统计局巡视员黄国芹作了重要总结讲话，并就提高数据质量提出了严格要求。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>参加会议的有各市（州）统计局</span><span>建设领域</span><span>统计工作的分管领导及负责</span><span>建设领域</span><span><font face="宋体">统计工作的业务骨干人员，</font>&nbsp;会议由投资处副处长杜川东主持。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201611/2016112211384963.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201611/2016112211384963.jpg" width="960" height="720"></a></o:p></span></p>', 1488009735, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(83, 12, '黄国芹巡视员带队对达州规范统计工作严肃统计工作纪律进行督查', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>11月16—18日，省统计局巡视员黄国芹带领省统计局贸经处、法规处、投资处人员一行4人，对达州市贯彻落实中央、省委省政府领导批示精神以及规范统计工作严肃统计工作纪律情况进行了督查。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>16日，督查组深入大竹县东柳镇查看乡镇规范化建设和基层统计工作开展情况，接着前往大竹先吉鑫汽车销售有限公司、大竹县杨家中心供销合作社、国道318和210饶大竹城区段过境改线工程、四川顺驰房地产开发有限公司检查统计工作，查看相关资料。17日，督查组到市统计局查看了相关资料和文件，听取市统计局规范统计工作严肃统计工作纪律情况的汇报。市统计局党组书记、局长李大兵全程陪同。　　</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>18日上午，督查组到达州市人民政府会议室听取汇报。达州市人民政府副市长何政代表市政府重点从四个方面进行了工作汇报。会上，黄国芹巡视员通报了督查情况，对达州的工作从16个方面给予了充分肯定，认为达州及时贯彻中央、省委省政府领导批示和川委办30号文件精神，及时组织全市七个县市区规范统计工作严肃统计纪律情况进行了专项督查。贯彻有力，行动迅速，措施到位。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>黄国芹巡视员对达州统计工作提出七点要求：一是进一步深入贯彻落实中央</span><span>、</span><span>省委、省政府和十八大会议精神；二是进一步增强防范统计数据造假的，三是加强领导，进一步关心支持统计工作，四是进一步夯实统计基础，提高源头统计数据质量，五是进一步加大农业普查各项准备工作，六是进一步加强部门统计，七是加强队伍建设，配强统计执法力量。　　</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>何政副市长代表市政府做了表态发言。何政副市长对省统计局对达州的关心表示衷心感谢，恳请省统计局一如既往的多支持指导达州发展，表示认真落实好督查组反馈的意见，按照黄国芹巡视员提出的工作要求，及时梳理，制定措施，整改落实到位。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201611/2016112211421268.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201611/2016112211421268.jpg" width="1024" height="682"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201611/2016112211422387.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201611/2016112211422387.jpg" width="1024" height="682"></a></o:p></span></p>', 1488009754, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(84, 12, '全省建设领域统计年报会在达州顺利召开', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>11月17日，全省投资建设领域统计年报会在达州宾馆召开。会上传达了全国建设领域统计年报会议精神，全面总结了2016年全省建设领域统计开展情况，并对2016年年报和2017年定期报表制度修订情况及投资、房地产、建筑业报表存在的问题进行了讲解和规范。下午，各市州就如何提高建设领域统计数据质量进行了交流发言；最后，省统计局巡视员黄国芹作了重要总结讲话，并就提高数据质量提出了严格要求。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121514492056.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121514492056.jpg" width="600" height="450"></a></o:p></span></p>', 1488009810, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(85, 12, '达州市开展农业普查综合业务培训', '<p style="text-align: justify;">2015年11月14日—18日，达州市举办农业普查综合业务培训。全市纳入农业普查的乡镇（街道）和县级农普办业务骨干共350余人参加培训，培训分三期进行。</p><p style="text-align: justify;">为搞好此次培训，市农普办根据参训人员以乡镇农普办业务骨干为主的特点，对培训内容和方式进行了精心安排。内容包括农户、规模农户、农业经营单位3张普查表填报方法和指标解释、普查员入户调查技巧、特殊问题处理办法，培训方式以实际操作方法为主、兼顾理论讲解，体现“接地气”，结束后还进行了现场测试，检验了参训人员农普业务的掌握、熟悉程度。</p><p style="text-align: justify;">通过此次培训，进一步加深了市县乡农业普查业务骨干对普查方案的理解，提高了全市的农普业务水平，为目前正在开展的清查摸底及以后的正式登记提供了坚实的业务技术保障。</p>', 1488009886, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(86, 12, '达州市局开展“12.4”国家宪法日宣传活动', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">今年是</font>“七五”普法开局之年，市统计局坚持深入学习贯彻党的十八大和十八届四中、五中、六中全会精神，大力弘扬宪法精神，广泛宣传统计法，全面推进法治统计。2016年12月2日上午，市统计局副局长石卫东带队参加全市 “12.4”国家宪法日暨全国法制宣传日广场宣传活动，活动地点在市中心广场。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">市统计局领导高度重视，精心准备此次宣传活动，整个宣传活动高潮迭起，亮点纷呈。亮点一：宣传载体形式多样。既有宣传气球，又有宣传展板；既有宣传画册，又有宣传年画；既有宣传手册，又有宣传彩单。亮点二：宣传内容丰富多彩，既宣传当前重中之重的农业普查，又宣传统计法律法规；既有知识问答，又有统计法律条款解读。亮点三：宣传气氛热烈，热潮不断。统计局宣传点人头攒动，领取资料的人群络绎不绝，给整个宣传活动增添了一抹亮丽的色彩。宣传活动共发出宣传资料</font>1000多份，现场接受咨询20余人次。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515084749.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515084749.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515085953.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515085953.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515091147.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515091147.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515092152.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515092152.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515093481.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515093481.jpg" width="600" height="450"></a></o:p></span></p>', 1488009906, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(87, 12, '达州市区联动开展“12•8”送法下乡统计法治宣传活动', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>12月8日是《中华人民共和国统计法》颁布实施33周年纪念日，达州市统计局紧紧围绕“大力弘扬统计法治精神、确保农业普查数据质量”这一主题，联合市农普办、通川区统计局、区农普办人员一道前往魏兴乡开展“12·　8”　送法下乡统计法治宣传活动。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">当天上午，正值赶场天，市区宣传人员精心准备，在魏兴乡最主要街道搭起了宣传展台，四副宣传标语在阳光下迎风招展，高清宽屏电视播放着统计法治和农业普查宣传片，高音喇叭播送着农业普查公告和市区领导重要讲话，十几张宣传桌上摆放着各式各样的宣传资料和宣传品，有年画、布袋、统计法律知识彩单、农普手册、法律知识汇编本、致农业普查对象的一封信、农普公告等等，展点周围环绕着</font>11块统计法律法规、农业普查宣传展板。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>市区统计局高度重视此次宣传活动，市统计局党组书记、局长李大兵、通川区副区长陈军、通川区统计局、党组书记局长邱宇参加了宣传活动，亲自为前来咨询的群众答疑解惑、分发宣传资料和宣传品，与群众共话第三次全国农业普查。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">此次活动市区共抽调</font>20多人，向群众发送宣传资料和宣传品1500多份，接受咨询100多人次。此次送法下乡“12·8”统计法治宣传活动让依法开展农业普查更深入地走进基层，走进农村、走进农民。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515244963.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515244963.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515250177.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515250177.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515251247.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515251247.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515252417.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515252417.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515254292.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515254292.jpg" width="600" height="450"></a></o:p></span></p>', 1488009931, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(89, 12, '达州市召开贸易外经、旅游监测暨服务业统计工作培训会', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">为贯彻落实全省贸易外经、旅游监测统计年报会议和服务业统计年报会议精神，安排部署全市</font>2016年年报及2017年定期报表工作，12月7日至8日，达州市召开了全市贸易外经、旅游监测暨服务业统计工作年报培训会，各县（市、区）统计局分管贸易外经、旅游监测统计、服务业统计的局领导及统计业务人员参加了会议，会议由局机关党委书记王树江主持，市局总统计师刘玲出席会议并作重要讲话。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">会上，传达了全省服务业、贸易外经、旅游监测统计工作年报会议精神；</font>“以会代训”对2016年年报和2017年各项定期报表进行了业务培训；安排部署了明年贸易外经、旅游监测统计和服务业统计专业工作。刘玲总统计师总结了全市2016年贸易外经、旅游监测统计、服务业统计工作取得的成绩和存在的问题。并对下阶段及明年的工作，提出了具体要求。关于下阶段的工作重点主要有两方面：一是动态监测评估好平台数据。各县市区要认真对本辖区规上（限上）联网直报单位的数据预警监测工作，及时发现数据异动情况，跟踪分析异动原因，防止数据大起大落现象，确保源头数据质量。二是科学预测明年目标任务。认真研判宏观经济走势，结合县域经济发展阶段特点，以科学的态度和尊重客观经济规律的原则对经济社会发展统计指标的目标任务提出合理的统计建议。对于明年的重点工作要从五个方面抓好抓落实：一要进一步加大规（限）上单位清理入库工作力度，切实做到“应统尽统”；二要继续深化企业统计基层基础工作规范化建设工作，提高源头统计数据质量；三要加强月度、季度以及年度统计数据质量管控，提升统计数据公信力；四要加强统计分析调研，提升统计服务水平；五要加强部门联动，整合统计资源，构建大统计工作格局。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>培训会结束后，与会代表现场参观了宣汉县宏帆巴人广场、金鼓广场两个城市综合体、电子商务园区，并实地调研学习企业统计基层基础工作规范化建设开展情况。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515305289.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515305289.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515310821.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515310821.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515312518.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515312518.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515313903.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016121515313903.jpg" width="600" height="450"></a></o:p></span></p>', 1488010433, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(90, 12, '达州市统计系统多形式开展统计法治宣传活动', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>12月8日是《中华人民共和国统计法》颁布实施33周年纪念日，今年又是全国第三次农业普查年，达州市统计系统紧扣“大力弘扬统计法治精神，确保农业普查数据质量”主题，全市上下齐动开展了形式多样的“12·　8”　统计法治宣传活动。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><b>达州市、通川区</b><span>&nbsp;&nbsp;<font face="宋体">　</font>12月8日　，在通川区魏兴乡设立宣传点，市、区两级统计局、农普办联合开展“12·8”送法下乡活动。市、区领导高度重视，市统计局党组书记、局长李大兵、通川区副区长陈军、通川区统计局、党组书记局长邱宇参加了宣传活动，亲自为前来咨询的群众答疑解惑、分发宣传资料和宣传品，与群众共话第三次全国农业普查。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><b>开江县</b><span>&nbsp;<font face="宋体">　</font>12月8日上午　，开江县统计局在副局长卢寒梅和支部书记代静的带领下，一行十余人在开江县新宁镇清河广场开展“12.8”《统计法》颁布日暨“三农普”宣传活动。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><b>大竹县</b><span>&nbsp;<font face="宋体">大竹县统计局高度重视，精心安排，</font>&nbsp;<font face="宋体">　</font>12月8日上午　在竹北乡设立宣传点进行集中宣传。活动现场，通过设立咨询台、摆放宣传展板、悬挂宣传横幅、发放宣传资料等形式，向广大群众宣传《统计法》、《四川省统计管理条例》、《全国农业普查条例》等统计法律法规。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><b>渠县</b><span>&nbsp;<font face="宋体">　</font>12月8日　，渠县统计局在万兴广场设立《统计法》法律知识和第三次全国农业普查宣传台，通过宣传展板、发放宣传资料、现场咨询等方式进行宣传。宣传活动共发放统计法律法规和“三农普”宣传资料1000余份。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><b>万源市</b><span><font face="宋体">　</font>12月8日，万源市统计局、农普办走进白沙镇，围绕“农业普查福到农家、依法普查利国利家”主题，开展《统计法》暨第三次全国农业普查集中宣传活动。宣传活动共发放宣传资料5000余份。同时通过万源统计信息网、万源电视台、万源政府门户网、达州统计信息网、达州网、四川新闻网、达州日报网、达州日报等媒体对统计相关法律法规和第三次全国农业普查进行宣传，并同步在辖区其余51个乡镇开展了类似的集中宣传活动。</span></p>', 1488010458, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(91, 12, '达州市统计局召开农村经济统计工作年报会', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>2016年12月19-20日，达州市统计局召开全市农村经济统计工作年报会，传达了省农村统计年报工作会议精神，全面总结了2016年全市农村统计工作开展情况，并对2017年农村统计工作做出安排。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">会议对农村统计报表制度、</font>sarp程序和报表工作中应注意的问题还进行了系统培训讲解，同时就做好2016年农村统计工作年报及2017年农村统计工作提出四点要求:一是高度重视，精心组织。农业年报时间紧、任务重，加之面临三农普工作，要求县（市、区）加强对农村统计年报工作的领导，高度重视年报工作，精心组织，统筹安排好农村统计年报和三农普工作，做到两不误。二是加强学习，执行制度。要求县（市、区）统计局业务人员加强对农村统计年报制度的学习，学懂吃透制度，搞好对基层统计人员的业务培训，切实提高基层人员业务水平。三是深入调研，服务大局。农村统计业务人员要多深入农村、田间地头搞好调研，了解农村生产实情，掌握好农村经济发展中出现的新情况、新问题，积极撰写统计分析信息，服务发展大局。四是实事求是，依法统计。坚持依法统计，坚持实事求是，严格执行农村统计年报制度，严防统计造假弄虚作假。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>各县（市、区）统计局、经开区经发局分管农村经济统计工作的局领导和负责农业核算专业人员等参加培训。</span></p>', 1488010477, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(92, 12, '达州市统计局党员领导干部赴巴山大剧院参观“清廉四川文艺创作工程优秀作品展”', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>12月22日，市统计局纪检组组织市局党员领导干部赴巴山大剧院参观“清廉四川文艺创作工程优秀作品展”，开展党员领导干部廉政教育活动。通过观看廉政作品，欣赏廉政文化，广大党员领导干部受到了一次廉政洗礼，增强了廉洁自律感，廉洁水平进一步提高。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122315531812.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122315531812.jpg" width="400" height="300"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122315532866.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122315532866.jpg" width="400" height="266"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122315533891.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122315533891.jpg" width="400" height="266"></a></o:p></span></p>', 1488010499, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(93, 12, '达州市委副书记、市长郭亨孝率队到省统计局汇报工作', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>近日，市委副书记、市长郭亨孝带队到省统计局汇报工作。省统计局党组书记、局长陈炜等领导听取汇报。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">会上</font>, 何政副市长就达州2016年经济运行情况、市第四次党代会确定的“十三五”“12335”发展思路和今年以来经济社会发展状况及统计工作开展等情况进行了汇报。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>郭亨孝市长对省统计局给予达州的大力支持表示衷心感谢，重点就当前达州经济运行情况交换意见。他希望，省统计局的各位领导和专家多到达州实地调研，对达州的发展给予更多的关注、指导和支持。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>陈炜局长对达州党委政府对统计工作的重视、关心与支持表示感谢。他指出，面对严峻复杂的宏观形势，达州抵住了经济下行压力，统计部门在抓工作、抓发展，执行力强，很有精气神，同时要在原有的基础上进一步推进工作，不断深化统计改革。他表示，省统计局将积极支持达州做好科学统计工作，组织相关处室和专家进行研究审定，高度关注达州的发展，支持达州工作。省统计局巡视员黄国芹及相关处室负责人就有关方面的情况发表了意见和建议。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>市政府分管副市长、市政府秘书长，市统计局、市经信委、市商务局主要负责人参加了汇报会。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911203996.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911203996.jpg" width="480" height="319"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911204939.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911204939.jpg" width="480" height="319"></a></o:p></span></p>', 1488010517, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(94, 12, '达州市统计局召开全市建设领域统计方法制度培训会', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">近日，达州市统计局召开了</font>2016年全市建设领域统计年报和2017年定期报表培训会。市统计局副局长赵英出席会议并作重要讲话。会议由工交投资科科长屈纪勇主持。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>会上，市统计局专业人员分别传达了全省建设领域统计年报会及全省建设领域统计数据处理程序培训会会议精神；总结了今年全市投资、房地产与建筑业统计工作；就建设领域统计报表制度和项目入库管理等重点工作进行了详细讲解培训；并听取了各县（市、区）统计局分管领导对完成全年目标任务的情况汇报。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">最后，市统计局副局长赵英作了总结讲话，肯定了全市建设领域统计工作取得的成绩，对进一步做好建设领域统计工作提出要求：一是用</font>“三统”（即统领、统筹、统计）理念推进建设领域统计工作再上新台阶；二是全力确保全年目标任务的完成；三是及时向主要领导、主管部门传达汇报省市年报会精神。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">各县（市、区）分管领导和专业人员共</font>30人参会。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911243518.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911243518.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911244878.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201612/2016122911244878.jpg" width="600" height="450"></a></o:p></span></p>', 1488010533, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(95, 12, '达州市全面开展农业普查正式入户登记工作', '<p class="MsoNormal"><span>根据国家方案规定和上级要求，达州市在<span lang="EN-US">2017</span>年<span lang="EN-US">1</span>月<span lang="EN-US">1</span>日正式开展了农业普查入户登记工作。广大普查人员放弃元旦假日休息，不畏严寒，全力奋战在农普第一线：大竹县、万源市、经开区<span lang="EN-US">3</span>个县（市、区）政府领导莅临登记现场，慰问普查人员、查看登记工作、提出工作要求；渠县举行了正式登记启动仪式，营造了浓厚的登记氛围；<span lang="EN-US">8</span>个县（市、区）统计局、农普办领导和工作人员全部到一线巡查，指导登记业务、解决登记问题、确保登记质量；普查人员手持电子终端（<span lang="EN-US">PDA</span>）走村窜户访农问农，查“三农”实情，为国家全面建设小康社会、脱贫攻坚需要的“三农”基础数据而努力工作着。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>根据市农普办的统一安排部署，凡是已经进行了<span lang="EN-US">PDA</span>业务培训并完成了<span lang="EN-US">PDA</span>网上注册的普查人员，从<span lang="EN-US">1</span>月<span lang="EN-US">1</span>日起即可开展入户登记工作，登记按照“先慢后快，总结完善，整体推进，按期完成”的原则进行。元旦假日期间的登记带有熟悉<span lang="EN-US">PDA</span>操作技能和程序、发现问题、总结经验的试验登记性质，<span lang="EN-US">1</span>月<span lang="EN-US">1</span>日—<span lang="EN-US">3</span>日，全市共完成了<span lang="EN-US">2144</span>户农户的登记工作，后期将逐渐加快进度，确保<span lang="EN-US">3</span>月底前全面完成登记工作任务。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412542202.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412542202.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412544060.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412544060.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412551355.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412551355.jpg" width="600" height="450"></a></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p>&nbsp;<a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412553203.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017010412553203.jpg" width="600" height="450"></a></o:p></span></p>', 1488010549, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(96, 12, '达州市召开2016年经济形势新闻发布会', '<p class="MsoNormal" align="left"><span lang="EN-US">2017</span><span>年<span lang="EN-US">1</span>月<span lang="EN-US">23</span>日下午，达州市政府新闻办召开<span lang="EN-US">2016</span>年经济形势新闻发布会，邀请市统计局党组书记、局长李大兵向省、市媒体发布达州市<span lang="EN-US">2016</span>年经济形势相关情况，新闻发布会由市政府副秘书长黄中平主持。</span></p><p class="MsoNormal" align="left"><span><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017012409122422.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201701/2017012409122422.jpg" width="800" height="533"></a></span></p><p class="MsoNormal" align="left"><span><span lang="EN-US"><o:p></o:p></span></span>&nbsp;</p><p class="MsoNormal" align="left"><span lang="EN-US"><a href="http://10.51.136.10/Article/UploadFiles/201701/2017012317201621.jpg" target="_blank"><span>&nbsp;</span></a>&nbsp;&nbsp; &nbsp;</span><span>会上，李大兵发布了<span lang="EN-US">2016</span>年达州经济形势，<span lang="EN-US">2016</span>年面对错综复杂的宏观经济环境，市委、市政府认真贯彻落实党中央、国务院、省委、省政府各项决策部署和要求，坚持稳中求进的总基调，全面做好稳增长、调结构、促改革、惠民生、防风险等各项工作，全市经济呈现出“稳中有进、稳中向好”的发展态势，实现了“十三五”的良好开局。全年达州实现地区生产总值<span lang="EN-US">1447.08</span>亿元，按可比价格计算，同比增长<span lang="EN-US">7.5%</span>。其中，第一产业增加值<span lang="EN-US">310.02</span>亿元，增长<span lang="EN-US">4.1%</span>；第二产业增加值<span lang="EN-US">606.2</span>亿元，增长<span lang="EN-US">7.6%</span>；第三产业增加值<span lang="EN-US">530.87</span>亿元，增长<span lang="EN-US">9.3%</span>。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>随后，李大兵还就全市经济运行特点、经济结构转型升级进展、经济实现较快增长民众获得感等问题回答了记者提问和交流解读。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>黄中平对进一步做好经济数据发布和新闻宣传工作提出要求。一是统计部门继续发挥统计“数据库”作用，为保持全市经济平稳较快发展提供准确的统计数据、科学的判断和政策依据；二是省、市新闻媒体要对发布的数据和提供的信息，进行大力采写、宣传、策划，做好深度宣传报道，最大限度地发挥统计数据的社会效用。</span></p><p class="MsoNormal" align="left"><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span>省、市<span lang="EN-US">13</span>家新闻媒体和市统计局全体干部职工共<span lang="EN-US">50</span>余人参加会议。</span></p>', 1488010567, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(97, 12, '达州市委书记包惠对统计工作作出肯定性批示', '<p><span style="text-align: justify;">2月6日，达州市书记包惠在市统计局上报的统计专报《达州追赶进位的速度测算与路径分析》</span><span style="text-align: justify;"><font face="宋体">上作出</font></span><span style="text-align: justify;"><font face="宋体">肯定性</font></span><span style="text-align: justify;"><font face="宋体">批示</font></span><span style="text-align: justify;"><font face="宋体">：</font>“送市委常委、各县（市、区）党政主要领导研阅；市统计局认真履职，用心谋事的工作态度值得肯定！”</span></p>', 1488010584, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(98, 12, '达州市政府发文部署新经济统计调查工作', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span><font face="宋体">为认真贯彻落实《四川省人民政府办公厅关于开展新经济统计调查工作的通知》（川办函〔</font>2016〕100号）精神，全面推进</span><span>达州</span><span>新经济统计调查工作，准确、及时反映新产业、新业态、新商业模式的发展状况</span><span>，</span><span>2月17日，达州市政府办公室印发《关于开展新经济统计调查工作的通知》（达市府办函〔2017〕24号，以下简称《通知》），《通知》结合达州实际完善细化了工作措施，具有很强的指导性和可操作性。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span>《通知》指出开展新经济统计调查工作是全面深化统计改革创新的重要内容，是贯彻落实中央五大发展理念的重要体现。要求各地、各有关部门要高度重视新经济统计调查工作，坚决贯彻落实党中央、国务院工作部署和市政府工作决定，以科学的态度、严实的作风，积极探索、创新实践，切实把新经济统计调查工作抓紧、抓实、抓好。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span><font face="宋体">《通知》具体从四个方面对各级各部门开展好新经济统计调查工作提出明确要求：一是明确职责，加强配合。要按照</font>“统一制度、分级实施、部门分工、各负其责”的原则，形成政府综合统计与部门统计相结合、互补共享的工作机制</span><span>；</span><span>二是严肃纪律，确保质量。要严格执行统计调查项目审批制度，搞好质量审核评估，确保数据客观真实</span><span>；</span><span>三是深入分析，做好服务。要加强新经济统计调查数据的深度开发和综合利用，深入分析新经济统计数据，全面反映新经济发展规模、效益、结构、水平、效果</span><span>；</span><span>四是加强领导，强化保障。要求各地、各有关部门切实加强组织领导，组建工作机构、加强宣传与业务培训、强化工作保障，统筹协调和有序推进各项工作落实到位。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span>《通知》的发布实施，对于</span><span>大力</span><span>开展新经济统计调查工作，推动达州新经济统计工作在新常态下取得新的成绩具有十分重要的意义。达州市局每年将与市政府督查室会同相关部门对各地、各部门对新经济调查工作的组织实施情况进行督查，并将督查情况专题向市政府报告。</span></p>', 1488010601, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(99, 12, '市统计局深入经开区斌郎乡何家村开展脱贫攻坚专项督导暨“机关+支部”连心共建党员活动', '<p class="MsoNormal" align="justify" style="text-align: justify;"><span>2017年2月8日，达州市统计局党组书记、局长李大兵率国家统计局达州调查队、达州中医校、达州市国资经营管理有限公司、达州河市机场等5家帮扶单位负责人和工作人员一行30人，深入经开区斌郎乡何家村开展脱贫攻坚专项督导暨“机关+支部”连心共建党员活动，斌郎乡政府副乡长何仙山陪同。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span><font face="宋体">李大兵一行在何家村委会党员活动室，围绕何家村</font>2017年村级发展大计，与村民代表、党员、村组干部、驻村干部、乡干部进行座谈，会商何家村发展事宜，推动目标早明确、任务早细化、责任早落实。何家村党组织第一书记首先汇报了何家村2017年脱贫稳富工作计划，主要是巩固贫困户持续增收、集体增效的脱贫成果，确保何家村80户227人脱贫不返贫，并继续落实帮扶措施，力促余下2户5人2017脱贫，着力加强果园产业优化升级，完善基础设施建设，继续跟踪做好教育、医疗、社会扶贫、技能培训等相关工作；村支部书记、斌郎乡副乡长分别就2016年脱贫攻坚工作开展情况和存在的问题及2017年整村安排计划进行了详细汇报。随后，参会党员、村组干部纷纷就如何促进全村发展提出看法，各参扶单位负责人对2017年继续深入开展结对帮扶以及落实措施进行表态发言。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span><font face="宋体">在面对面了解村级发展需求、听取了基层干部群众意见建议后，李大兵指出，</font>2016年通过帮扶单位大力支持、乡村干部共同努力、群众高度参与下，何家村完成了脱贫摘帽任务，2017年更要再鼓干劲，提档升级，力保脱贫稳富工作再出成效。一是抓好村级组织建设，锤炼干部队伍。以村支两委换届为契机，配齐配全村支两委，进一步提高村委的整体战斗力，充分发挥党员先锋模范作用，以“两学一做”学习教育活动为统领为脱贫攻坚工作提供坚强的组织保障。二是实施产业优化升级，抓好脱贫“回头看”工作，巩固贫困户增收、集体增效脱贫成果，将脆李、桃园产业做大做优作为重中之重，从根本上解决脱贫后返贫的问题。三是提升基础设施建设工程。修建好小型水利设施，拿出具体方案，确保群众生产用水灌溉有保障。四是抓好易地搬迁附属设施建设及搬迁入住工作，改善群众居住条件。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>&nbsp;&nbsp;&nbsp;&nbsp;</span><span><font face="宋体">李大兵对何家村脱贫稳富工作提出建议：一是抓好班子、带好队伍促进村级发展。村</font>“两委”班子是发展的主心骨，其抓落实、促发展、自身作风怎样，直接关系到村上的经济发展。当前正值村支两委换届选举，乡、村两级要选好、用好村组干部，要求村级班子每名成员都能在工作作风、干事魄力、工作执行力、能力水平上“过得硬”，更能有效促进村级事业发展。二是做大做优特色产业发展。何家村离城近郊是最大优势，阳光充裕，围绕产业做文章大有潜力，要坚持大力发展“四季果园”特色产业不放松，把特色产业做大做强，依托“四季果园”产业发展采摘体验、近郊旅游产业，为稳定脱贫奠定坚实基础。三是抓紧时间完成易地搬迁工程。要紧扣问题导向，列举问题清单，克难攻坚，决战决胜。要抢抓时间节点，经开区经发局要切实牵头，分析不足，补齐短板，着力加强统筹解决、区级部门协调、乡村两级实施，及时解决工程问题，确保易地扶贫搬迁住房装修工作、附属基础设施建设工程的稳步快速推进。同时，统筹考虑并妥善解决好贫困群众后续生产生活问题。按照“五个一批”，做到“挪穷窝”与“换穷业”并举，安居与乐业并重、搬迁与脱贫同步。积极整合各项资金，做到搬迁户的后续发展有产业，确保扶贫搬迁对象能“搬得出、稳得住、能致富”，提升推进精准扶贫工作成效。四是多方出力解决村级遗留问题。建议乡、村两级梳理出遗留问题，提出初步解决办法，建议从按照“谁受益、谁负担”受益农户筹集一点、向上争取一点、部门帮扶一点等方面逐步解决。</span><span><o:p></o:p></span></p><p class="MsoNormal" align="justify" style="text-align: justify;"><span>&nbsp;&nbsp;&nbsp;&nbsp;<font face="宋体">会后，督导组查阅了何家村脱贫攻坚档案资料、党建工作资料、贫苦户痕迹化管理台账盒，核实了驻村帮扶工作台账落实到位情况，先后到何俊、何治伟、何治恩等</font>5户贫困对象家进行走访，详细了解他们的生产、生活情况和帮扶需求，结对子、拉家常，出主意，想办法，为他们讲解当前党的脱贫不脱钩的政策，并为他们送上慰问物资、帮扶金500元，用于购买果树苗、化肥，帮扶他们发展生产，并祝他们新年吉祥，生活幸福。随后，李大兵一行实地查看了何家村易地扶贫搬迁安置点建设进度、发展种植产业情况，鼓励村支两委鼓足信心和干劲，继续为建设幸福美丽新村而奋斗。</span></p>', 1488010624, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(100, 11, '达州市统计局祝全市广大统计工作者元旦快乐！！！', '<p><span style="text-align: -webkit-center;">达州市统计局祝全市广大统计工作者元旦快乐！！！</span></p>', 1488010733, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(101, 11, '达州市统计信息外网网站改版成功', '<p class="MsoNormal"><span>为认真贯彻落实《国务院办公厅关于进一步加强政府网站管理工作的通知》以及《四川省政府办公厅关于进一步加强政府网站管理工作的通知》精神，达州市统计局领导对达州统计外网网站建设，信息发布、在线服务与互动交流等方面提出了明确要求，指出办好统计网站的关键在于及时、准确公开统计信息，突出统计特色，切实解决网站管理中存在的问题，不断提升统计网站的管理水平。按照局领导的指示精神，并结合达州市政府两年一次的网站绩效评估要求，信息化处于<span lang="EN-US">3</span>月就统计外网网站进行了整体改造。经过一个多月的辛勤努力，达川区人民法院外网网站改版成功。从</span><span lang="EN-US">“</span><span>政务公开</span><span lang="EN-US">”</span><span>、</span><span lang="EN-US">“</span><span>工作动态</span><span lang="EN-US">”</span><span>、</span><span lang="EN-US">“</span><span>政府动态</span><span lang="EN-US">”</span><span>、</span><span lang="EN-US">“</span><span>权威发布</span><span lang="EN-US">”</span><span>、</span><span lang="EN-US">“</span><span>数据发布</span><span lang="EN-US">”</span><span>和</span><span lang="EN-US">“</span><span>统计分析</span><span lang="EN-US">”</span><span>等方面重新规划网站模版，强化了政务公开、统计服务、公众参与等特色栏目，设置了</span><span lang="EN-US">“</span><span>公共服务</span><span lang="EN-US">”</span><span>、</span><span lang="EN-US">“</span><span>政务公开</span><span lang="EN-US">”</span><span>等对外公开栏目，丰富了</span><span lang="EN-US">“</span><span>最新公告</span><span lang="EN-US">”</span><span>、</span><span lang="EN-US">“</span><span>数据发布</span><span lang="EN-US">”</span><span>等统计服务栏目，建立了</span><span lang="EN-US">“</span><span>达州概况</span><span lang="EN-US">”</span><span>、</span><span lang="EN-US">“</span><span>图片精选</span><span lang="EN-US">”</span><span>等宣传栏目，进一步丰富了外网网站的统计信息量。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal"><span>由于目前达州市统计信息外网网站还处于试运行阶段，特别是一些新增栏目、特色栏目的内容、指标、数据以及软件等都有待进一步完善，如果您在使用过程中发现什么问题，有什么意见和建议，可以及时反馈给我们，本网站将随着达州市统计工作的发展和社会各界的关注而逐步完善。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal"><span>欢迎广大用户使用达州统计信息网！</span></p>', 1488010847, 0, 0, NULL, '', '', 0, '信息部', '本站', 0),
(102, 11, '2016年统计专业技术资格考试报名时间', '<p><span>通知：2016年统计专业技术资格考试报名指南已放“公共服务栏目”，请大家注意阅览！</span></p>', 1488010778, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(103, 11, '第三次全国农业普查登记时间', '<p><span>达州市人民政府第三次全国农业普查正式登记时间为2017年1月1日零点。</span></p>', 1488010795, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(104, 11, '达州市2015年城镇全部单位就业人员平均工资已公告', '<p><span style="text-align: -webkit-center;">达州市2015年城镇全部单位就业人员平均工资已公告</span></p>', 1488010809, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(105, 11, '达州市2015年城镇全部单位就业人员平均工资已公告', '<p><span>达州市</span><span lang="EN-US">2015</span><span>年城镇全部单位就业人员平均工资标准已经在“数据发布”栏目中发布，欢迎大家查阅！</span></p>', 1488010831, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(106, 14, '达州市统计局机构职能', '<p>根据中共四川省委、四川省人民政府批复的《达州市人民政府机构改革方案》（川委厅〔2010〕40号），设立达州市统计局（简称市统计局），为市政府工作部门。</p><p>&nbsp;&nbsp;<strong>&nbsp; 达州市统计局的主要职责是：</strong></p><p>&nbsp;&nbsp;&nbsp; （一）贯彻执行国家、省统计工作的方针、政策和法律、法规以及统计制度和统计标准，拟订本市统计工作规划和统计调查计划，审批本地区、各部门的地方统计调查项目，承担组织领导和协调全市统计工作，确保统计数据真实、准确、及时的责任。</p><p>&nbsp;&nbsp;&nbsp; （二）建立健全全市国民经济核算体系，组织实施全市国民经济核算制度和投入产出调查，核算全市及审核各县（市、区）地区生产总值，汇编提供国民经济核算资料，开展分析研究，指导、监督县（市、区）国民经济核算工作，参与全市经济社会目标责任考核。</p><p>&nbsp;&nbsp;&nbsp; （三）按照国家和省的统一部署，组织实施人口、经济、农业等普查和重大国情国力以及社情民意专项调查，汇总、整理和提供有关国情国力方面的统计数据。</p><p>&nbsp;&nbsp;&nbsp; （四）组织实施农林牧渔业、工业、建筑业、批发和零售业、住宿和餐饮业、房地产业等国民经济行业以及能源、投资、人口、收入、科技、社会发展基本情况、环境基本状况等领域的统计调查，建立全市经济社会发展监测评价制度及指标体系，对重点区域和重要领域实施监测评价，牵头综合整理和提供资源、房屋、旅游、教育、卫生、邮电、交通运输、社会保障、公用事业、对外贸易、对外经济等全市基本统计资料。</p><p>&nbsp;&nbsp;&nbsp; （五）建立健全全市统计数据质量审核、监控和评估制度，依法对县（市、区）、市直部门重要统计数据进行审核、监控和评估，组织指导统计基层基础建设。</p><p>&nbsp;&nbsp;&nbsp; （六）对国民经济、社会发展、科技进步和资源环境等情况进行统计分析和预测，定期发布全市经济社会发展情况的统计信息，向市委、市政府及有关部门提供统计咨询建议，向社会公众提供统计信息服务。</p><p>&nbsp;&nbsp;&nbsp; （七）协助管理县（市、区）统计局正副局长，指导全市统计专业人才队伍建设，会同有关部门组织管理全市统计专业技术资格考试、职务评聘、从业资格认定和统计人员继续教育工作，监督管理地方政府统计部门由中央、省和市级财政提供的统计经费和专项基本建设投资。</p><p>&nbsp;&nbsp;&nbsp; （八）组织制订县（市、区）、市直部门统计数据库和网络的基本标准和运行规则，建立健全全市统计数据库系统和统计信息自动化系统，建立健全市直部门统计信息共享制度，指导地方统计信息化建设。<br>&nbsp;<br>&nbsp;&nbsp;&nbsp; （九）承担市政府公布的行政审批事项。</p><p>&nbsp;&nbsp;&nbsp; （十）承办市政府交办的其他事项。</p>', 1491810153, 0, 0, NULL, '', '', 0, '信息部', '本站', 0),
(107, 15, '达州市统计局局领导班子成员工作分工', '<p>根据工作需要，经局党组会议研究同意，对局领导班子成员工作分工调整如下：<br>&nbsp;</p><p align="center"><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201607/2016071115574641.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201607/2016071115574641.jpg" width="360" height="504">&nbsp;</a><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111073385.jpg" target="_blank">&nbsp;</a></p><p align="center">李大兵（党组书记、局长）：主持统计局全面工作。<br><br><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111010820.jpg" target="_blank">&nbsp;</a><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111074734.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111074734.jpg" width="303" height="433">&nbsp;</a><br><br>顾万东（党组成员、副局长）：负责工业统计、建筑业统计、投资统计、统计设计管理和统计信息化建设工作，分管工交投资科、计算机科。</p><p align="center"><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111015501.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111015501.jpg" width="321" height="463">&nbsp;</a></p><p align="center">唐友国（党组成员、纪检组长）：负责党风廉政建设和反腐败工作的组织协调和监督检查，负责干部作风及统计行风建设工作。</p><p align="center"><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111025016.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111025016.jpg" width="346" height="433">&nbsp;</a></p><p align="center">柏&nbsp; 丰 （党组成员、副局长）：负责统计分析研究和综合运用、国民经济核算和普查工作，分管综合核算科、普查中心。</p><p align="center"><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111031628.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111031628.jpg" width="303" height="389">&nbsp;</a></p><p align="center">石卫东（党组成员、副局长）：负责综合协调、人事、教育、法规、农村经济统计、城乡居民收入调查、后勤保障工作，分管办公室、人事法规科、城乡社会经济调查队。</p><p align="center"><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111033564.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111033564.jpg" width="295" height="437">&nbsp;</a></p><p align="center">马春霞（党组成员、机关党委书记）：负责机关党建、群团、能源统计、社情民意工作，分管能源和环境统计科、社情民意调查中心。</p><p align="center"><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111040624.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111040624.jpg" width="295" height="424">&nbsp;</a></p><p align="center">刘玲（总统计师）：负责商贸、社会、服务业统计工作，分管商贸社会科、服务业统计科。</p><p align="center"><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111042217.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111042217.jpg" width="225" height="312"></a></p><p align="center">蒋宣平（副调研员）：协助能源统计、社情民意、统计执法工作。&nbsp;</p><p align="center"><a href="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111045211.jpg" target="_blank"><img border="0" src="http://www.dztjw.gov.cn/Article/UploadFiles/201605/2016053111045211.jpg" width="303" height="389">&nbsp;</a></p><p align="center">陈传喜（副调研员）：协助统计分析研究和综合运用、国民经济核算和普查工作。</p>', 1488011020, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(108, 40, '达州市统计局内设机构', '<p>根据中共四川省委、四川省人民政府批复的《达州市人民政府机构改革方案》（川委厅〔2010〕40号），设立达州市统计局（简称市统计局），为市政府工作部门。</p><p><strong>&nbsp;&nbsp; 达州市统计局设8个内设机构：</strong></p><p><strong>&nbsp;&nbsp;&nbsp; （一）办公室</strong></p><p>&nbsp;&nbsp;&nbsp; 负责协调机关政务和后勤管理；负责行政效能建设、政务督办和绩效管理工作，承担财务、文秘、会务、政务信息、文件收发、档案管理、目标管理、安全保密、综治、扶贫、信访和应急处理等工作。协助局领导统一管理局机关经费和全市县以上统计机构的统计事业费以及由各类经费形成的基建投资和国有资产。对全市政府统计系统的财务工作进行指导和审计。</p><p><strong>&nbsp;&nbsp;&nbsp; （二）人事法规科（达州市统计执法大队）</strong></p><p>&nbsp;&nbsp;&nbsp; 负责对本局起草的规范性文件进行合法性审核；组织统计法律、法规的宣传贯彻和监督检查工作；依法查处统计违法案件；承办行政复议、行政应诉和其他法律性事务；依法审批本地区、各部门的地方统计调查项目；组织协调统计从业资格认定和统计人员继续教育工作，会同有关部门组织管理统计专业技术资格和职务评聘工作；承担局机关和直属事业单位的机构编制、人事、培训、离退休人员管理工作；协助管理县（市、区）统计局正副局长；指导全市统计专业人员队伍建设，开展统计交流与合作。</p><p><strong>&nbsp;&nbsp;&nbsp; （三）综合核算科</strong></p><p>&nbsp;&nbsp;&nbsp; 组织实施全市国民经济运行的监测预警，整理、提供和发布综合性统计数据，开展分析研究，提出宏观调控咨询建议；负责编辑、出版本局综合性统计资料并承办本市统计新闻发布会；组织实施国民经济核算、投入产出调查；核算全市地区生产总值；整理提供国民经济核算资料，开展分析研究；建立健全全市统计数据质量的审核、监控和评估制度，对有关统计数据质量进行检查和评估；拟订地方统计制度改革规划和国民经济核算制度，组织实施全市统计调查业务设计和监督管理，检查指导有关专业统计基础工作。</p><p>&nbsp;&nbsp;<strong>&nbsp; （四）工交投资科</strong></p><p>&nbsp;&nbsp;&nbsp; 组织实施全市工业、固定资产投资、建筑业、房地产业的统计调查。对产业园区、大企业（大集团）、优势产业、政府性资金投资项目、房地产市场运行情况等进行统计监测；收集、整理和提供有关统计调查数据，对有关数据质量进行检查和评估，检查指导统计基础工作；进行统计分析；负责全市工业经济考核。</p><p><strong>&nbsp;&nbsp;&nbsp; （五）服务业科</strong></p><p>&nbsp;&nbsp;&nbsp; 组织有关部门实施全市交通运输、仓储和邮政业、信息传输、计算机服务和软件业、金融业、教育、卫生、文化、体育和娱乐业等服务领域的统计调查；收集、整理和提供有关统计调查数据，对有关数据质量进行检查和评估;检查指导服务业统计基础工作；对服务业的发展情况进行统计预警、监测、分析、评价、考核等。</p><p>&nbsp;&nbsp;<strong>&nbsp; （六）商贸社会科</strong></p><p>&nbsp;&nbsp;&nbsp; 组织实施全市批发和零售业、住宿和餐饮业、商品市场运行状况、社会发展基本状况、科技进步与创新、劳动力和城镇居民收支、人口、劳动工资的统计调查；组织实施对妇女儿童事业发展、劳动力就业状况进行统计监测；会同有关部门开展社会发展和社会管理统计监测评价工作；组织有关部门评估全市旅游产业、对外贸易、对外经济等统计数据；收集、整理和提供有关统计调查数据，对有关数据质量进行检查和评估，检查指导统计基础工作；进行统计分析。</p><p><strong>&nbsp;&nbsp;&nbsp; （七）计算机科</strong></p><p>&nbsp;&nbsp;&nbsp; 拟订全市统计信息自动化建设规划并组织实施。管理、维护本局以及由统计系统配置的计算机和相关设备，负责全市统计系统内、外网络建设维护、网络管理及网络安全监测工作，指导县市区网络建设维护和管理。统一管理网上邮件的收发工作。编制、开发全市县级以上统计系统所需的软件。承担各项普查和专项调查的数据处理工作。建立健全本市统计数据库和单位名录库。对全市统计人员进行计算机业务培训。</p><p><strong>&nbsp;&nbsp;&nbsp; （八）能源和环境统计科</strong></p><p>&nbsp;&nbsp;&nbsp; 组织实施全市能源、环境统计调查，对节能减排、单位GDP能耗、资源循环利用、环境基本状况等进行统计监测；承担能源经济和循环经济综合评价工作；会同有关部门开展节能降耗和主要污染物减排目标考核；收集、整理和提供有关统计调查数据，对有关数据质量进行检查和评估，检查指导统计基础工作；进行统计分析。负责全市社情民意调查工作并进行调查分析。<br><strong>机关党委</strong>&nbsp; 负责机关及其直属事业单位党群工作。</p>', 1488010983, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(109, 14, '四川省人口和计划生育违法违纪行为责任追究办法（试行）', '<p class="MsoNormal"><b>第一条&nbsp;</b><span>为认真贯彻执行计划生育基本国策，严肃处理违反人口和计划生育政策法规的行为，依据《中华人民共和国人口与计划生育法》等法律法规和《中国共产党纪律处分条例》等党内法规及有关政策规定，结合我省实际，制定本办法。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第二条&nbsp;</b><span>实行计划生育是国家的基本国策，是每个公民和全社会的共同责任。国家机关、社会团体实行人口和计划生育工作责任制，企事业单位及其他社会经济组织实行人口和计划生育工作法定代表人责任制。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>共产党员、干部和社会公众人物应带头模范遵守人口和计划生育法律法规和政策，有配合调查、如实说明自身婚育情况的义务，有教育、督促家庭成员自觉实行计划生育的责任。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第三条</b><span>&nbsp;任何组织和个人都有权对违反人口和计划生育政策法规的行为进行检举、控告，有关机关和部门应当认真受理、调查和处理。对检举有功人员，应当给予奖励。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><b>第四条</b><span>&nbsp;有下列行为之一的，给予责任人员中的共产党员（下同）党内严重警告至开除党籍处分；对其中的党和国家机关工作人员，从重处理：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）不符合法定再生育条件生育子女的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）对本人的超生子女实行假“收养”的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>有配偶者与他人生育子女或者再次违法生育的，给予开除党籍处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第五条</b><span>&nbsp;有下列行为之一的，给予批评教育或者诫勉处理；情节严重的，给予党内警告或者严重警告处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）夫妻符合再生育的法定条件但未经批准生育的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）收养他人子女满六个月未依法办理收养手续又没有正当理由的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<o:p></o:p></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）双方皆无配偶和子女的男女未履行婚姻登记手续第一次生育，三个月内未补办婚姻登记手续的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）双方皆无配偶，一方有一个子女、另一方无子女的男女未履行婚姻登记手续生育的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第六条&nbsp;</b><span>有下列行为之一的，给予撤销党内职务至开除党籍处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）非法为他人施行计划生育手术的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）利用超声技术或者其他技术手段为他人进行非医学需要的胎儿性别鉴定的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）进行非医学需要的选择性别的人工终止妊娠的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）实施假节育手术、进行假医学鉴定、出具假节育手术证明的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）组织、介绍妊娠妇女进行非法鉴定胎儿性别或者施行选择性别的终止妊娠手术的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（六）违法将终止妊娠药品销售给未获准施行终止妊娠的机构、个人或者药品零售企业的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第七条</b><span>&nbsp;有下列行为之一的，给予党内警告或者严重警告处分；情节严重的，给予撤销党内职务至开除党籍处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）以不正当手段取得或者伪造、变造、买卖生育服务证、生育证、出生医学证明、独生子女父母光荣证、奖励扶助证明、流动人口计划生育婚育证明等计划生育证明材料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）阻挠、干涉他人实行计划生育的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）协助、包庇、纵容他人违法生育的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）拒绝、阻碍有关机关和部门依法调查取证、征收社会抚养费等执法行为的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）以编造、散播谣言和封建迷信活动等形式，干扰、破坏人口和计划生育工作开展的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><b>第八条</b><span>&nbsp;有下列行为之一的，对直接负责的主管人员和其他直接责任人员，给予党内警告或者严重警告处分；情节严重的，给予撤销党内职务至开除党籍处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）违法审批人口和计划生育有关申请的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）出具计划生育证明材料有过错的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）违反社会抚养费征收、管理、使用规定的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）截留、挪用、骗取、贪污奖励经费、免费计划生育技术服务经费等计划生育经费以及倒卖、变卖政府免费提供的避孕药具的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）虚报、瞒报、伪造、篡改或者拒报人口和计划生育统计数据，或者有其他弄虚作假行为的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（六）不落实人口和计划生育工作财政保障政策的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（七）拒不落实独生子女父母奖励和优待政策的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（八）有其他滥用职权、玩忽职守、徇私舞弊行为的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><b>第九条</b><span>&nbsp;有下列行为之一的，对直接负责的主管人员和其他直接责任人员，给予党内警告或者严重警告处分；情节严重的，给予撤销党内职务至开除党籍处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）计划生育免费技术服务定点机构拒绝按规定提供免费技术服务的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）在婚姻登记、孕产期保健、户口登记、物业管理、房屋出租管理、资质准入、营业执照办理等工作中，不履行法定查验职责，或者不按规定向人口和计划生育行政管理部门提供常规信息并及时通报特定情况的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）拒不提供违法生育当事人的收入证明、纳税证明等相关证明材料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）在流动人口计划生育管理工作中不履行相关法定职责的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）拒不配合有关人口和计划生育工作的考核、评估、检查、督查、督办和案件查处的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（六）有其他不依法履行有关法定职责行为的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十条</b><span>&nbsp;有下列情形之一的，对该地区、部门（单位）实行“一票否决”：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）未完成年度人口和计划生育工作目标任务的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）本单位人员（含聘用人员）违法生育的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）在人口和计划生育工作中因违法行政引发恶性案件或者造成恶劣影响的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）在人口和计划生育技术服务工作中发生重大责任事故的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）拒不履行人口和计划生育法定职责，引发群体性事件的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十一条</b><span>&nbsp;对受到“一票否决”的地区、部门（单位），取消其当年综合性先进、荣誉称号的评选资格。对市（州）和纳入省委、省政府目标管理的省直部门，在年度综合目标考核时，按照目标管理的有关规定降低一个获奖等次。各地、各部门参照上述原则对受到“一票否决”的单位给予相应处理。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对受到“一票否决”的地区、部门（单位）的主要负责人、人口和计划生育工作的分管负责人和直接责任人，当年年度考核不得确定为优秀等次，并取消其各类先进、荣誉称号的评选资格；任期内被否决两次以上的，责令辞职或者免职；其中出现重大失误的，三年内不得提拔和晋升职务。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;<b>&nbsp;&nbsp;</b></span><b>第十二条</b><span>&nbsp;对地方党委、政府实行“一票否决”，由上一级人口和计划生育行政管理部门提出否决建议方案，报请同级党委、政府决定，有关部门执行；对有关部门（单位）实行“一票否决”，由本级人口和计划生育行政管理部门提出否决建议方案，报请同级党委、政府决定，有关部门执行；对人口和计划生育行政管理部门实行“一票否决”，由同级党委、政府决定，有关部门执行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>发现有关责任人员调任、转任后五年内有应予“一票否决”情形的，由有管辖权的人口和计划生育行政管理部门对其提出否决建议，并按前款规定执行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十三条</b><span>&nbsp;把遵守人口和计划生育法律法规和政策作为提拔任用干部、推荐各级中国共产党代表大会代表、人大代表、政协委员、青联委员候选人、工商联执委和评选各类劳动模范、先进个人的一项基本要求。党代表、人大代表、政协委员、青联委员、工商联执委、劳动模范和先进个人违法生育的，按照规定程序罢免其代表职务或者撤销其委员资格、荣誉称号。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对违法生育人员，视为不具备参选村（居）民委员会主任或者委员，以及录用、聘用为财政供养人员、国有及国有控股企业领导人员的条件。在任村（居）民委员会主任或委员违法生育的，责令辞职。在任国有及国有控股企业领导人员违法生育的，予以免职。企业法定代表人或主要负责人违法生育的，其所在企业不得评为先进、模范企业。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对违法生育造成恶劣影响的行为，可予以公开揭露。有关部门对查处的违法生育典型案件，可予以通报或者向社会公布。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十四条&nbsp;</b><span>依照本办法规定给予党纪处分的，可视情节同时给予相应的组织处理。情节较轻的，责令作出书面检查、给予通报批评、诫勉等处理；情节较重的，予以责令辞职、免职等处理；情节严重的，予以辞退、解聘等处理。不是共产党员的，应当单独给予组织处理。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对违反本办法规定的责任人员，应当给予政纪处分的，行政机关公务员按照《行政机关公务员处分条例》处理；事业单位工作人员，在国家对事业单位及其工作人员的专门管理规定出台前，可参照《行政机关公务员处分条例》处理；其他人员，应参照本办法规定的党纪处分档次给予相应的政纪处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>涉嫌犯罪的，依法移送司法机关处理。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十五条</b><span>&nbsp;对违反本办法规定的责任人员，应当给予纪律处分的，由纪检监察机关、任免机关按照党组织隶属关系或者人事管理权限实施；组织处理由组织人事部门、任免机关按照人事管理权限实施。需按本办法第十三条进行处理的，由人口和计划生育行政管理部门提出建议。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对违反本办法的行为，有关机关或部门在职权范围内作出处理后，认为需要给予其他处理的，应当及时提出建议，被建议机关或部门无正当理由不得拒绝。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十六条</b><span>&nbsp;各级纪检监察机关、组织人事部门，以及人口和计划生育行政管理部门等职能部门应当对本办法执行情况进行监督检查，加强协调配合，建立健全情况通报、联合执法、资格审查、案件移送等工作机制。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><b>第十七条&nbsp;</b><span>本办法由中共四川省纪委、四川省监察厅商四川省人口和计划生育委员会负责解释。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十八条&nbsp;</b><span>本办法自发布之日起施行。本办法实施前发生的人口和计划生育违法违纪行为，尚未处理的，按本办法的规定处理；已经处理的，不再重新处理。中共四川省纪委、四川省监察厅《关于共产党员和国家机关工作人员违反计划生育法规纪律处分规定》（川纪发〔<span lang="EN-US">1995</span>〕<span lang="EN-US">31</span>号）、《关于修改〈共产党员和国家机关工作人员违反计划生育法规纪律处分规定〉部分条款的通知》（川纪发〔<span lang="EN-US">1998</span>〕<span lang="EN-US">4</span>号）同时废止。</span></p>', 1488014937, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(110, 32, '1212', '212', 1488015799, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(116, 41, '达州概况', '<p class="p"><span>达州，地处四川东部，辖</span><span>4</span><span>县</span><span>2</span><span>区</span><span>1</span><span>市，幅员面积</span><span>1.66</span><span>万平方公里，总人口</span><span>690</span><span>万，是人口大市、资源富市、工业重镇、交通枢纽和革命老区，享有</span><span>“</span><span>巴人故里、中国气都</span><span>”</span><span>之称。</span><span><o:p></o:p></span></p><p class="p"><span>&nbsp;</span><span><o:p></o:p></span></p><p class="p"><b><span>达州历史悠久，人杰地灵。</span></b><span>达州历为州、郡、府、县所在地，至今已有</span><span>1900</span><span>多年的建城史。达州曾是</span><span>4000</span><span>多年前古巴人繁衍生息的沃土，媲美三星堆的罗家坝巴人文化遗址，见证了远古巴人文明的辉煌历史。达州汉阙存量占全国的四分之一，是全国最大的汉阙群。达州是川陕革命根据地的重要组成部分，徐向前、李先念、许世友等老一辈无产阶级革命家曾在这里浴血奋战，当年</span><span>8</span><span>万多人参加红军，</span><span>2</span><span>万多人为国捐躯，孕育了张爱萍、陈伯钧、向守志、魏传统等</span><span>50</span><span>多位共和国将军。</span><span><o:p></o:p></span></p><p class="p"><span>&nbsp;</span><span><o:p></o:p></span></p><p class="p"><b><span>达州区位独特，交通便捷。</span></b><span>达州地处中国版图中心，是名符其实的中国圆心城，是成渝、关中</span><span>-</span><span>天水、大武汉三大经济区的重要连接带，是四川对外开放的东大门和通江达海的东通道，历为秦巴地区物资集散地和商贸中心。达州是全国公路运输</span><span>179</span><span>个主枢纽城市和四川</span><span>12</span><span>个次级综合交通枢纽之一，是国家连南贯北承东启西</span><span>“</span><span>十字</span><span>”</span><span>铁路公路交通网的节点城市，达州火车站是西南第四大火车站，达州机场直航北上广深等</span><span>6</span><span>大城市，渠江航运经重庆可直达上海，立体交通体系全面构建。</span><span>&nbsp;</span><span><o:p></o:p></span></p><p class="p"><span>&nbsp;</span></p><p class="p"><b><span>达州资源富集，物产丰饶。</span></b><span>达州是全国三大气田之一和川气东送工程的起点，是国家重要的能源资源战略基地。境内已探明可开发利用矿产资源</span><span>28</span><span>种，其中，天然气资源总量</span><span>3.8</span><span>万亿立方米，探明储量</span><span>7000</span><span>亿立方米，年外输天然气</span><span>100</span><span>亿立方米以上，天然气净化附产硫磺</span><span>400</span><span>万吨，是亚洲最大的硫磺生产基地。达州是国家商品粮生产基地、生猪调出大市和国家农业综合开发的重点地区，是中国苎麻之乡、中国黄花之乡、中国乌梅之乡、中国糯米之乡、中国油橄榄之都、中国富硒茶之都、中国醪糟之都。</span><span><o:p></o:p></span></p><p class="p"><span>&nbsp;</span><span><o:p></o:p></span></p><p class="p"><b><span>达州产业完备，发展迅速。</span></b><span>达州曾是国家</span><span>“</span><span>三线</span><span>”</span><span>建设的重点地区，已形成能源、化工、冶金、建材、机电、食品、医药、纺织、商贸、物流等为主体的产业体系。全市</span><span>&nbsp;“1+7”</span><span>产业园区加速发展，达州经开区成功入选国家新型工业化产业示范园区、国家循环化改造示范试点园区和全省唯一的首批国家低碳工业试点园区，达州农产品加工集中区获全国农产品加工基地称号，宣汉、大竹被列为全省首批</span><span>14</span><span>个工业强县示范县。</span><span>2014</span><span>年，全市实现地区生产总值</span><span>1347.8</span><span>亿元，位居全省第</span><span>6</span><span>位，</span><span>7</span><span>个县市区经济总量均突破</span><span>100</span><span>亿元，</span><span>3</span><span>个县区超过</span><span>200</span><span>亿元。</span><span><o:p></o:p></span></p><p class="p"><span>&nbsp;</span><span><o:p></o:p></span></p><p class="p"><b><span>达州山川秀美，生态宜人。</span></b><span>达州是中国南北气候的分界线，境内森林覆盖率</span><span>41.5%</span><span>，年均降雨量</span><span>1200</span><span>毫米左右，空气质量优良天数年均</span><span>300</span><span>天以上。达州自然、人文景观异彩纷呈，被明代著名地理学家徐霞客赞为</span><span>“</span><span>西南奇胜</span><span>”</span><span>，现有国家</span><span>4A</span><span>级旅游景区</span><span>3</span><span>个，国家级自然保护区</span><span>1</span><span>个，国家级地质公园</span><span>1</span><span>个，国家级森林公园</span><span>3</span><span>个。近年来，达州大力实施城市拓展、交通畅通和环境治理三大工程，人居环境显著改善，先后荣获省级卫生城市、省级环保模范城市、省级园林城市、省级森林城市和省级旅游标准化示范市等荣誉称号。</span><span><o:p></o:p></span></p><p class="p"><span>&nbsp;</span><span><o:p></o:p></span></p><p class="p"><b><span>达州未来发展，前景广阔。</span></b><span>达州先后被纳入成渝经济区区域规划和国家一带一路发展规划，受惠于秦巴山片区连片扶贫攻坚规划和川陕革命老区发展规划，迎来加快发展的重要战略机遇期。在同步全面小康的新征程中，达州市委、市政府正深入贯彻落实党的十八大、十八届三中、四中全会精神，严格按照省委、省政府的部署，全面践行</span><span>“</span><span>通达开放、诚义创新</span><span>”</span><span>的城市精神，扎实推进</span><span>“</span><span>三大总体战略工程</span><span>”</span><span>，着力开展</span><span>“</span><span>五个年</span><span>”</span><span>活动，朝着建设川渝鄂陕结合部区域中心城市和川东北经济区核心增长极目标奋力迈进。</span><span><o:p></o:p></span></p><p class="p"><span>&nbsp;</span><span><o:p></o:p></span></p><p class="p"><span>让达州走向世界，让世界拥抱达州！中国达州，一座更加文明开放、繁荣幸福、朝气蓬勃的现代化城市，正以崭新的姿态欢迎您的到来！</span><span>&nbsp;</span></p>', 1488044777, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(118, 45, '中华人民共和国统计法', '<p align="CENTER"><font color="#333333"><font size="4"><b>中华人民共和国统计法</b></font></font></p><p><font color="#333333"><font size="4">　　（</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1983</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">12</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">8</font></font></span></font></font><font color="#333333"><font size="4">日第六届全国人民代表大会常务委员会第三次会议通过　根据</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1996</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">5</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">15</font></font></span></font></font><font color="#333333"><font size="4">日第八届全国人民代表大会常务委员会第十九次会议《关于修改〈中华人民共和国统计法〉的决定》修正　</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2009</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">6</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">27</font></font></span></font></font><font color="#333333"><font size="4">日第十一届全国人民代表大会常务委员会第九次会议修订）</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第一章　总则</font></font></p><p><font color="#333333"><font size="4"><b>　　第一条　</b></font></font><font color="#333333"><font size="4">为了科学、有效地组织统计工作，保障统计资料的真实性、准确性、完整性和及时性，发挥统计在了解国情国力、服务经济社会发展中的重要作用，促进社会主义现代化建设事业发展，制定本法。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二条　</b></font></font><font color="#333333"><font size="4">本法适用于各级人民政府、县级以上人民政府统计机构和有关部门组织实施的统计活动。</font></font></p><p><font color="#333333"><font size="4">　　统计的基本任务是对经济社会发展情况进行统计调查、统计分析，提供统计资料和统计咨询意见，实行统计监督。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三条　</b></font></font><font color="#333333"><font size="4">国家建立集中统一的统计系统，实行统一领导、分级负责的统计管理体制。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四条　</b></font></font><font color="#333333"><font size="4">国务院和地方各级人民政府、各有关部门应当加强对统计工作的组织领导，为统计工作提供必要的保障。</font></font></p><p><font color="#333333"><font size="4"><b>　　第五条　</b></font></font><font color="#333333"><font size="4">国家加强统计科学研究，健全科学的统计指标体系，不断改进统计调查方法，提高统计的科学性。</font></font></p><p><font color="#333333"><font size="4">　　国家有计划地加强统计信息化建设，推进统计信息搜集、处理、传输、共享、存储技术和统计数据库体系的现代化。</font></font></p><p><font color="#333333"><font size="4"><b>　　第六条　</b></font></font><font color="#333333"><font size="4">统计机构和统计人员依照本法规定独立行使统计调查、统计报告、统计监督的职权，不受侵犯。</font></font></p><p><font color="#333333"><font size="4">　　地方各级人民政府、政府统计机构和有关部门以及各单位的负责人，不得自行修改统计机构和统计人员依法搜集、整理的统计资料，不得以任何方式要求统计机构、统计人员及其他机构、人员伪造、篡改统计资料，不得对依法履行职责或者拒绝、抵制统计违法行为的统计人员打击报复。</font></font></p><p><font color="#333333"><font size="4"><b>　　第七条　</b></font></font><font color="#333333"><font size="4">国家机关、企业事业单位和其他组织以及个体工商户和个人等统计调查对象，必须依照本法和国家有关规定，真实、准确、完整、及时地提供统计调查所需的资料，不得提供不真实或者不完整的统计资料，不得迟报、拒报统计资料。</font></font></p><p><font color="#333333"><font size="4"><b>　　第八条　</b></font></font><font color="#333333"><font size="4">统计工作应当接受社会公众的监督。任何单位和个人有权检举统计中弄虚作假等违法行为。对检举有功的单位和个人应当给予表彰和奖励。</font></font></p><p><font color="#333333"><font size="4"><b>　　第九条　</b></font></font><font color="#333333"><font size="4">统计机构和统计人员对在统计工作中知悉的国家秘密、商业秘密和个人信息，应当予以保密。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十条　</b></font></font><font color="#333333"><font size="4">任何单位和个人不得利用虚假统计资料骗取荣誉称号、物质利益或者职务晋升。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第二章　统计调查管理</font></font></p><p><font color="#333333"><font size="4"><b>　　第十一条　</b></font></font><font color="#333333"><font size="4">统计调查项目包括国家统计调查项目、部门统计调查项目和地方统计调查项目。</font></font></p><p><font color="#333333"><font size="4">　　国家统计调查项目是指全国性基本情况的统计调查项目。部门统计调查项目是指国务院有关部门的专业性统计调查项目。地方统计调查项目是指县级以上地方人民政府及其部门的地方性统计调查项目。</font></font></p><p><font color="#333333"><font size="4">　　国家统计调查项目、部门统计调查项目、地方统计调查项目应当明确分工，互相衔接，不得重复。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十二条　</b></font></font><font color="#333333"><font size="4">国家统计调查项目由国家统计局制定，或者由国家统计局和国务院有关部门共同制定，报国务院备案；重大的国家统计调查项目报国务院审批。</font></font></p><p><font color="#333333"><font size="4">　　部门统计调查项目由国务院有关部门制定。统计调查对象属于本部门管辖系统的，报国家统计局备案；统计调查对象超出本部门管辖系统的，报国家统计局审批。</font></font></p><p><font color="#333333"><font size="4">　　地方统计调查项目由县级以上地方人民政府统计机构和有关部门分别制定或者共同制定。其中，由省级人民政府统计机构单独制定或者和有关部门共同制定的，报国家统计局审批；由省级以下人民政府统计机构单独制定或者和有关部门共同制定的，报省级人民政府统计机构审批；由县级以上地方人民政府有关部门制定的，报本级人民政府统计机构审批。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十三条　</b></font></font><font color="#333333"><font size="4">统计调查项目的审批机关应当对调查项目的必要性、可行性、科学性进行审查，对符合法定条件的，作出予以批准的书面决定，并公布；对不符合法定条件的，作出不予批准的书面决定，并说明理由。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十四条　</b></font></font><font color="#333333"><font size="4">制定统计调查项目，应当同时制定该项目的统计调查制度，并依照本法第十二条的规定一并报经审批或者备案。</font></font></p><p><font color="#333333"><font size="4">　　统计调查制度应当对调查目的、调查内容、调查方法、调查对象、调查组织方式、调查表式、统计资料的报送和公布等作出规定。</font></font></p><p><font color="#333333"><font size="4">　　统计调查应当按照统计调查制度组织实施。变更统计调查制度的内容，应当报经原审批机关批准或者原备案机关备案。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十五条　</b></font></font><font color="#333333"><font size="4">统计调查表应当标明表号、制定机关、批准或者备案文号、有效期限等标志。</font></font></p><p><font color="#333333"><font size="4">　　对未标明前款规定的标志或者超过有效期限的统计调查表，统计调查对象有权拒绝填报；县级以上人民政府统计机构应当依法责令停止有关统计调查活动。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十六条　</b></font></font><font color="#333333"><font size="4">搜集、整理统计资料，应当以周期性普查为基础，以经常性抽样调查为主体，综合运用全面调查、重点调查等方法，并充分利用行政记录等资料。</font></font></p><p><font color="#333333"><font size="4">　　重大国情国力普查由国务院统一领导，国务院和地方人民政府组织统计机构和有关部门共同实施。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十七条　</b></font></font><font color="#333333"><font size="4">国家制定统一的统计标准，保障统计调查采用的指标涵义、计算方法、分类目录、调查表式和统计编码等的标准化。</font></font></p><p><font color="#333333"><font size="4">　　国家统计标准由国家统计局制定，或者由国家统计局和国务院标准化主管部门共同制定。</font></font></p><p><font color="#333333"><font size="4">　　国务院有关部门可以制定补充性的部门统计标准，报国家统计局审批。部门统计标准不得与国家统计标准相抵触。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十八条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构根据统计任务的需要，可以在统计调查对象中推广使用计算机网络报送统计资料。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十九条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府应当将统计工作所需经费列入财政预算。</font></font></p><p><font color="#333333"><font size="4">　　重大国情国力普查所需经费，由国务院和地方人民政府共同负担，列入相应年度的财政预算，按时拨付，确保到位。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第三章　统计资料的管理和公布</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构和有关部门以及乡、镇人民政府，应当按照国家有关规定建立统计资料的保存、管理制度，建立健全统计信息共享机制。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十一条　</b></font></font><font color="#333333"><font size="4">国家机关、企业事业单位和其他组织等统计调查对象，应当按照国家有关规定设置原始记录、统计台账，建立健全统计资料的审核、签署、交接、归档等管理制度。</font></font></p><p><font color="#333333"><font size="4">　　统计资料的审核、签署人员应当对其审核、签署的统计资料的真实性、准确性和完整性负责。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十二条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府有关部门应当及时向本级人民政府统计机构提供统计所需的行政记录资料和国民经济核算所需的财务资料、财政资料及其他资料，并按照统计调查制度的规定及时向本级人民政府统计机构报送其组织实施统计调查取得的有关资料。</font></font></p><p><font color="#333333"><font size="4">　　县级以上人民政府统计机构应当及时向本级人民政府有关部门提供有关统计资料。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十三条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构按照国家有关规定，定期公布统计资料。</font></font></p><p><font color="#333333"><font size="4">　　国家统计数据以国家统计局公布的数据为准。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十四条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府有关部门统计调查取得的统计资料，由本部门按照国家有关规定公布。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十五条　</b></font></font><font color="#333333"><font size="4">统计调查中获得的能够识别或者推断单个统计调查对象身份的资料，任何单位和个人不得对外提供、泄露，不得用于统计以外的目的。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十六条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构和有关部门统计调查取得的统计资料，除依法应当保密的外，应当及时公开，供社会公众查询。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第四章　统计机构和统计人员</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十七条　</b></font></font><font color="#333333"><font size="4">国务院设立国家统计局，依法组织领导和协调全国的统计工作。</font></font></p><p><font color="#333333"><font size="4">　　国家统计局根据工作需要设立的派出调查机构，承担国家统计局布置的统计调查等任务。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方人民政府设立独立的统计机构，乡、镇人民政府设置统计工作岗位，配备专职或者兼职统计人员，依法管理、开展统计工作，实施统计调查。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十八条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府有关部门根据统计任务的需要设立统计机构，或者在有关机构中设置统计人员，并指定统计负责人，依法组织、管理本部门职责范围内的统计工作，实施统计调查，在统计业务上受本级人民政府统计机构的指导。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十九条　</b></font></font><font color="#333333"><font size="4">统计机构、统计人员应当依法履行职责，如实搜集、报送统计资料，不得伪造、篡改统计资料，不得以任何方式要求任何单位和个人提供不真实的统计资料，不得有其他违反本法规定的行为。</font></font></p><p><font color="#333333"><font size="4">　　统计人员应当坚持实事求是，恪守职业道德，对其负责搜集、审核、录入的统计资料与统计调查对象报送的统计资料的一致性负责。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十条　</b></font></font><font color="#333333"><font size="4">统计人员进行统计调查时，有权就与统计有关的问题询问有关人员，要求其如实提供有关情况、资料并改正不真实、不准确的资料。</font></font></p><p><font color="#333333"><font size="4">　　统计人员进行统计调查时，应当出示县级以上人民政府统计机构或者有关部门颁发的工作证件；未出示的，统计调查对象有权拒绝调查。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十一条　</b></font></font><font color="#333333"><font size="4">国家实行统计专业技术职务资格考试、评聘制度，提高统计人员的专业素质，保障统计队伍的稳定性。</font></font></p><p><font color="#333333"><font size="4">　　统计人员应当具备与其从事的统计工作相适应的专业知识和业务能力。</font></font></p><p><font color="#333333"> </font></p><p><font color="#333333"><font size="4">　　县级以上人民政府统计机构和有关部门应当加强对统计人员的专业培训和职业道德教育。</font></font></p><p><font color="#333333"><font size="4">第五章　监督检查</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十二条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府及其监察机关对下级人民政府、本级人民政府统计机构和有关部门执行本法的情况，实施监督。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十三条　</b></font></font><font color="#333333"><font size="4">国家统计局组织管理全国统计工作的监督检查，查处重大统计违法行为。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方人民政府统计机构依法查处本行政区域内发生的统计违法行为。但是，国家统计局派出的调查机构组织实施的统计调查活动中发生的统计违法行为，由组织实施该项统计调查的调查机构负责查处。</font></font></p><p><font color="#333333"><font size="4">　　法律、行政法规对有关部门查处统计违法行为另有规定的，从其规定。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十四条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府有关部门应当积极协助本级人民政府统计机构查处统计违法行为，及时向本级人民政府统计机构移送有关统计违法案件材料。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十五条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构在调查统计违法行为或者核查统计数据时，有权采取下列措施：</font></font></p><p><font color="#333333"><font size="4">　　（一）发出统计检查查询书，向检查对象查询有关事项；</font></font></p><p><font color="#333333"><font size="4">　　（二）要求检查对象提供有关原始记录和凭证、统计台账、统计调查表、会计资料及其他相关证明和资料；</font></font></p><p><font color="#333333"><font size="4">　　（三）就与检查有关的事项询问有关人员；</font></font></p><p><font color="#333333"><font size="4">　　（四）进入检查对象的业务场所和统计数据处理信息系统进行检查、核对；</font></font></p><p><font color="#333333"><font size="4">　　（五）经本机构负责人批准，登记保存检查对象的有关原始记录和凭证、统计台账、统计调查表、会计资料及其他相关证明和资料；</font></font></p><p><font color="#333333"><font size="4">　　（六）对与检查事项有关的情况和资料进行记录、录音、录像、照相和复制。</font></font></p><p><font color="#333333"><font size="4">　　县级以上人民政府统计机构进行监督检查时，监督检查人员不得少于二人，并应当出示执法证件；未出示的，有关单位和个人有权拒绝检查。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十六条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构履行监督检查职责时，有关单位和个人应当如实反映情况，提供相关证明和资料，不得拒绝、阻碍检查，不得转移、隐匿、篡改、毁弃原始记录和凭证、统计台账、统计调查表、会计资料及其他相关证明和资料。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第六章　法律责任</font></font></p><p><font color="#333333"> </font></p><p><font color="#333333"><font size="4"><b>　　第三十七条　</b></font></font><font color="#333333"><font size="4">地方人民政府、政府统计机构或者有关部门、单位的负责人有下列行为之一的，由任免机关或者监察机关依法给予处分，并由县级以上人民政府统计机构予以通报：</font></font></p><p><font color="#333333"><font size="4">　　（一）自行修改统计资料、编造虚假统计数据的；</font></font></p><p><font color="#333333"><font size="4">　　（二）要求统计机构、统计人员或者其他机构、人员伪造、篡改统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（三）对依法履行职责或者拒绝、抵制统计违法行为的统计人员打击报复的；</font></font></p><p><font color="#333333"><font size="4">　　（四）对本地方、本部门、本单位发生的严重统计违法行为失察的。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十八条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构或者有关部门在组织实施统计调查活动中有下列行为之一的，由本级人民政府、上级人民政府统计机构或者本级人民政府统计机构责令改正，予以通报；对直接负责的主管人员和其他直接责任人员，由任免机关或者监察机关依法给予处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）未经批准擅自组织实施统计调查的；</font></font></p><p><font color="#333333"><font size="4">　　（二）未经批准擅自变更统计调查制度的内容的；</font></font></p><p><font color="#333333"><font size="4">　　（三）伪造、篡改统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（四）要求统计调查对象或者其他机构、人员提供不真实的统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（五）未按照统计调查制度的规定报送有关资料的。</font></font></p><p><font color="#333333"><font size="4">　　统计人员有前款第三项至第五项所列行为之一的，责令改正，依法给予处分。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十九条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构或者有关部门有下列行为之一的，对直接负责的主管人员和其他直接责任人员由任免机关或者监察机关依法给予处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）违法公布统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（二）泄露统计调查对象的商业秘密、个人信息或者提供、泄露在统计调查中获得的能够识别或者推断单个统计调查对象身份的资料的；</font></font></p><p><font color="#333333"><font size="4">　　（三）违反国家有关规定，造成统计资料毁损、灭失的。</font></font></p><p><font color="#333333"><font size="4">　　统计人员有前款所列行为之一的，依法给予处分。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十条　</b></font></font><font color="#333333"><font size="4">统计机构、统计人员泄露国家秘密的，依法追究法律责任。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十一条　</b></font></font><font color="#333333"><font size="4">作为统计调查对象的国家机关、企业事业单位或者其他组织有下列行为之一的，由县级以上人民政府统计机构责令改正，给予警告，可以予以通报；其直接负责的主管人员和其他直接责任人员属于国家工作人员的，由任免机关或者监察机关依法给予处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）拒绝提供统计资料或者经催报后仍未按时提供统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（二）提供不真实或者不完整的统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（三）拒绝答复或者</font></font></p><p><font color="#333333"><font size="4">如实答复统计检查查询书的；</font></font></p><p><font color="#333333"><font size="4">　　（四）拒绝、阻碍统计调查、统计检查的；</font></font></p><p><font color="#333333"><font size="4">　　（五）转移、隐匿、篡改、毁弃或者拒绝提供原始记录和凭证、统计台账、统计调查表及其他相关证明和资料的。</font></font></p><p><font color="#333333"><font size="4">　　企业事业单位或者其他组织有前款所列行为之一的，可以并处五万元以下的罚款；情节严重的，并处五万元以上二十万元以下的罚款。</font></font></p><p><font color="#333333"><font size="4">　　个体工商户有本条第一款所列行为之一的，由县级以上人民政府统计机构责令改正，给予警告，可以并处一万元以下的罚款。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十二条　</b></font></font><font color="#333333"><font size="4">作为统计调查对象的国家机关、企业事业单位或者其他组织迟报统计资料，或者未按照国家有关规定设置原始记录、统计台账的，由县级以上人民政府统计机构责令改正，给予警告。</font></font></p><p><font color="#333333"><font size="4">　　企业事业单位或者其他组织有前款所列行为之一的，可以并处一万元以下的罚款。</font></font></p><p><font color="#333333"><font size="4">　　个体工商户迟报统计资料的，由县级以上人民政府统计机构责令改正，给予警告，可以并处一千元以下的罚款。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十三条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府统计机构查处统计违法行为时，认为对有关国家工作人员依法应当给予处分的，应当提出给予处分的建议；该国家工作人员的任免机关或者监察机关应当依法及时作出决定，并将结果书面通知县级以上人民政府统计机构。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十四条　</b></font></font><font color="#333333"><font size="4">作为统计调查对象的个人在重大国情国力普查活动中拒绝、阻碍统计调查，或者提供不真实或者不完整的普查资料的，由县级以上人民政府统计机构责令改正，予以批评教育。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十五条　</b></font></font><font color="#333333"><font size="4">违反本法规定，利用虚假统计资料骗取荣誉称号、物质利益或者职务晋升的，除对其编造虚假统计资料或者要求他人编造虚假统计资料的行为依法追究法律责任外，由作出有关决定的单位或者其上级单位、监察机关取消其荣誉称号，追缴获得的物质利益，撤销晋升的职务。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十六条　</b></font></font><font color="#333333"><font size="4">当事人对县级以上人民政府统计机构作出的行政处罚决定不服的，可以依法申请行政复议或者提起行政诉讼。其中，对国家统计局在省、自治区、直辖市派出的调查机构作出的行政处罚决定不服的，向国家统计局申请行政复议；对国家统计局派出的其他调查机构作出的行政处罚决定不服的，向国家统计局在该派出机构所在的省、自治区、直辖市派出的调查机构申请行政复议。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十七条　</b></font></font><font color="#333333"><font size="4">违反本法规定，构成犯罪的，依法追究刑事责任。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第七章　附则</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十八条　</b></font></font><font color="#333333"><font size="4">本法所称县级以上人民政府统计机构，是指国家统计局及其派出的调查机构、县级以上地方人民政府统计机构。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十九条　</b></font></font><font color="#333333"><font size="4">民间统计调查活动的管理办法，由国务院制定。</font></font></p><p><font color="#333333"><font size="4">　　中华人民共和国境外的组织、个人需要在中华人民共和国境内进行统计调查活动的，应当按照国务院的规定报请审批。</font></font></p><p><font color="#333333"><font size="4">　　利用统计调查危害国家安全、损害社会公共利益或者进行欺诈活动的，依法追究法律责任。</font></font></p><p><font color="#333333"><font size="4"><b>　　第五十条　</b></font></font><font color="#333333"><font size="4">本法自</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2010</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font></font><font color="#333333"><font size="4">日起施行</font></font></p>', 1488045954, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(119, 45, '中华人民共和国统计法实施细则', '<p><font face="Calibri, serif"><span lang="en-US">2</span></font>．<font face="Calibri, serif"><span lang="en-US">1</span></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>中华人民共和国统计法实施细则</b></font></font></p><p><font color="#333333"><font size="4">　　（</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1987</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">19</font></font></span></font></font><font color="#333333"><font size="4">日国务院批准　</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1987</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">15</font></font></span></font></font><font color="#333333"><font size="4">日国家统计局发布　</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2000</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">6</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2</font></font></span></font></font><font color="#333333"><font size="4">日国务院批准修订　</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2000</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">6</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">15</font></font></span></font></font><font color="#333333"><font size="4">日国家统计局发布　根据</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2005</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">12</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">16</font></font></span></font></font><font color="#333333"><font size="4">日《国务院关于修改〈中华人民共和国统计法实施细则〉的决定》修订）</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第一章　总　则</font></font></p><p><font color="#333333"><font size="4"><b>　　第一条　</b></font></font><font color="#333333"><font size="4">根据《中华人民共和国统计法》（以下简称《统计法》）的规定，制定本细则。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二条　</b></font></font><font color="#333333"><font size="4">《统计法》所指的统计，是指运用各种统计方法对国民经济和社会发展情况进行统计调查、统计分析，提供统计资料和统计咨询意见，实行统计监督等活动的总称。</font></font></p><p><font color="#333333"><font size="4">　　国民经济和社会发展的统计项目分类，由国家统计局规定、调整。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三条　</b></font></font><font color="#333333"><font size="4">国家有计划地用现代信息技术装备各级人民政府统计机构，建立健全国家统计信息自动化系统。国务院各部门根据工作需要，有计划地用现代信息技术装备本部门及其管辖系统的统计机构。</font></font></p><p><font color="#333333"><font size="4">　　县级以上各级人民政府应当将国家统计信息工程建设列入发展计划。国家统计信息工程建设，由国家统计局统一领导，县级以上地方各级人民政府统计机构分级负责。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四条　</b></font></font><font color="#333333"><font size="4">统计机构和统计人员实行工作责任制，实行考核和奖惩制度，不断提高工作质量和工作效率。</font></font></p><p><font color="#333333"><font size="4">　　统计机构和统计人员依法独立行使下列职权：</font></font></p><p><font color="#333333"><font size="4">　　（一）统计调查权——调查、搜集有关资料，召开有关调查会议，检查与统计资料有关的原始记录和凭证。统计调查对象应当依照《统计法》和国家有关规定，如实提供统计资料和情况，不得虚报、瞒报、拒报、迟报，不得伪造、篡改。</font></font></p><p><font color="#333333"><font size="4">　　（二）统计报告权——将统计调查取得的统计资料和情况加以整理、分析，向上级领导机关和有关部门提出统计报告。任何单位或者个人不得阻挠和扣压统计报告，不得篡改统计资料。</font></font></p><p><font color="#333333"><font size="4">　　（三）统计监督权——根据统计调查和统计分析，对国民经济和社会发展情况进行统计监督，检查国家政策和计划的实施，考核经济效益、社会效益和工作成绩，检查和揭露存在的问题，检查虚报、瞒报、伪造、篡改统计资料的行为，提出改进工作的建议。有关部门和单位对统计机构、统计人员反映、揭露的问题和提出的建议，应当及时处理，作出答复。</font></font></p><p><font color="#333333"><font size="4"><b>　　第五条　</b></font></font><font color="#333333"><font size="4">县级以上地方各级人民政府、各部门、各企业事业组织，应当根据国家统计任务和本地区、本部门、本单位的需要，在下列方面加强对统计工作的领导和监督：</font></font></p><p><font color="#333333"><font size="4">　　（一）领导和支持统计机构、统计人员和其他有关人员执行统计法规和统计制度，准确、及时地完成统计工作任务，加强统计工作现代化建设；</font></font></p><p><font color="#333333"><font size="4">　　（二）吸收和组织统计人员参加讨论有关政策和计划、研究经济和社会发展问题的会议，发挥统计的服务和监督作用；</font></font></p><p><font color="#333333"><font size="4">　　（三）根据国家统一部署，组织实施重大的国情国力普查；</font></font></p><p><font color="#333333"><font size="4">　　（四）按照规定审批统计调查计划，切实解决经批准的统计调查需要的人员和经费。</font></font></p><p><font color="#333333"><font size="4"><b>　　第六条　</b></font></font><font color="#333333"><font size="4">国家统计局及其派出的调查队、县级以上地方各级人民政府统计机构是国家执行统计法规和统计制度的机关，负责监督检查统计法规和统计制度的实施，维护统计机构和统计人员的职权，依法查处违反统计法规和统计制度的行为。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方各级人民政府统计机构依法查处本行政区域内发生的统计违法行为；在国家统计局派出的调查队组织实施的统计调查中发生的统计违法行为，由组织实施该项统计调查的调查队负责查处。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第二章　统计调查计划和统计制度</font></font></p><p><font color="#333333"><font size="4"><b>　　第七条　</b></font></font><font color="#333333"><font size="4">县级以上各级人民政府统计机构和有关部门按照下列三类情况，分别建立统计制度，编制统计调查计划，按照规定经审查机关批准后实施：</font></font></p><p><font color="#333333"><font size="4">　　（一）国家统计调查，是指全国性基本情况的统计调查，包括国家统计局单独拟订的和国家统计局与国务院有关部门共同拟订的统计调查项目。国家统计调查计划中新的、重大的统计调查项目，由国家统计局报国务院审批；经常性的、一般性的统计调查项目，由国家统计局审批。</font></font></p><p><font color="#333333"><font size="4">　　各地方、各部门、各单位必须严格按照国家统计调查方案实施国家统计调查。</font></font></p><p><font color="#333333"><font size="4">　　（二）部门统计调查，是指各部门的专业性统计调查。部门统计调查计划和统计调查方案，由该部门的统计机构组织本部门各有关职能机构编制。其中，统计调查对象属于本部门管辖系统内的，由本部门领导人审批，报国家统计局或者本级地方人民政府统计机构备案；统计调查对象超出本部门管辖系统的，报国家统计局或者本级地方人民政府统计机构审批，其中重要的，报国务院或者本级地方人民政府审批。各部门统计调查管辖系统的划分办法，由国家统计局会同国务院有关部门提出，报国务院批准后实施。</font></font></p><p><font color="#333333"><font size="4">　　（三）地方统计调查，是指地方人民政府需要的地方性的统计调查。地方统计调查计划和统计调查方案的报批办法，由省、自治区、直辖市人民政府统计机构规定，报国家统计局备案。</font></font></p><p><font color="#333333"><font size="4"><b>　　第八条　</b></font></font><font color="#333333"><font size="4">部门统计调查和地方统计调查不得与国家统计调查重复、矛盾。</font></font></p><p><font color="#333333"><font size="4">　　国家统计调查与部门统计调查、地方统计调查的分工，由国家统计局会同国务院有关部门和省、自治区、直辖市人民政府统计机构具体商定。</font></font></p><p><font color="#333333"><font size="4"><b>　　第九条　</b></font></font><font color="#333333"><font size="4">县级以上各级人民政府的综合协调部门需要的统计资料，应当从本级人民政府统计机构和有关部门搜集；确实需要直接进行统计调查的，应当编制统计调查计划和统计调查方案，依照《统计法》和本细则的有关规定，经批准后实施。</font></font></p><p><font color="#333333"><font size="4">　　县级以上各级人民政府有关部门组织实施的部门统计调查，应当及时向本级人民政府统计机构报送基本统计资料或者综合统计资料。</font></font></p><p><font color="#333333"><font size="4">　　国家统计局和县级以上地方各级人民政府统计机构，应当定期、无偿地向本级人民政府部门提供有关综合统计资料。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十条　</b></font></font><font color="#333333"><font size="4">统计调查计划按照统计调查项目编制。统计调查项目，是指一定时期内为实现特定统计调查目的而组织实施的统计调查。统计调查项目的计划应当列明：项目名称、调查机关、调查目的、调查范围、调查对象、调查方式、调查时间、调查的主要内容。</font></font></p><p><font color="#333333"><font size="4">　　编制统计调查计划，必须同时编制统计调查方案。统计调查方案应当包括下列内容：</font></font></p><p><font color="#333333"><font size="4">　　（一）供统计调查对象填报用的统计调查表和说明书；</font></font></p><p><font color="#333333"><font size="4">　　（二）供整理上报用的统计综合表和说明书；</font></font></p><p><font color="#333333"><font size="4">　　（三）统计调查需要的人员和经费及其来源。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十一条　</b></font></font><font color="#333333"><font size="4">县级以上各级人民政府统计机构、各部门统计机构对送审的统计调查计划和统计调查方案的必要性、可行性、科学性应当进行严格审查；对不符合本细则规定的，应当退回修改或者不予批准。编制和审查统计调查方案，应当遵循下列原则：</font></font></p><p><font color="#333333"><font size="4">　　（一）在已经批准实施的各种统计调查中能够搜集到资料的，不得重复调查；</font></font></p><p><font color="#333333"><font size="4">　　（二）抽样调查、重点调查或者行政记录可以满足需要的，不得制发全面统计调查表；一次性统计调查可以满足需要的，不得进行经常性统计调查；按年统计调查可以满足需要的，不得按季统计调查；按季统计调查可以满足需要的，不得按月统计调查；月以下的进度统计调查必须从严控制；</font></font></p><p><font color="#333333"><font size="4">　　（三）编制新的统计调查方案，必须事先试点或者征求有关地方、部门和基层单位的意见，进行可行性论证，保证切实可行，注重调查效益；</font></font></p><p><font color="#333333"><font size="4">　　（四）统计调查需要的人员和经费应当有保证。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十二条　</b></font></font><font color="#333333"><font size="4">国家建立周期性的普查制度。周期性普查由国务院和地方各级人民政府统一领导，组织统计机构和有关部门共同实施，所需要的经费由中央和地方财政共同负担。</font></font></p><p><font color="#333333"><font size="4">　　进行经常性抽样调查，应当通过基本统计单位普查和行政记录的方式，查明基本统计单位及其分布情况，建立科学的抽样框，按照随机原则在调查总体中选取足以代表总体的样本单位，减少抽样误差。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十三条　</b></font></font><font color="#333333"><font size="4">按照规定程序批准的统计调查表，必须在右上角标明表号、制表机关、批准或者备案机关、批准或者备案文号、有效期限。</font></font></p><p><font color="#333333"><font size="4">　　对未标明前款所列内容或者超过有效期限的统计调查表，有关统计调查对象有权拒绝填报，统计机构有权废止。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十四条　</b></font></font><font color="#333333"><font size="4">统计调查方案所规定的指标涵义、调查范围、计算方法、分类目录、调查表式、统计编码等，未经批准该统计调查方案的机关同意，任何单位或者个人不得修改。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第三章　统计资料的管理和公布</font></font></p><p><font color="#333333"><font size="4"><b>　　第十五条　</b></font></font><font color="#333333"><font size="4">各地方、各部门、各单位应当健全统计资料的审核制度，保障统计资料的准确性和及时性。</font></font></p><p><font color="#333333"><font size="4">　　各部门、各企业事业组织提供的统计资料，由本部门、本单位领导人或者统计负责人审核、签署或者盖章后上报。有关财务统计资料由财务会计机构或者会计人员提供，并经财务会计负责人审核、签署或者盖章。县级以上各级人民政府统计机构和乡、镇统计员提供的统计资料，由本级人民政府统计机构负责人或者乡、镇统计员审核、签署或者盖章后上报。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十六条　</b></font></font><font color="#333333"><font size="4">各级领导机关制定政策、计划，检查政策、计划执行情况，考核经济效益、社会效益和工作成绩，进行奖励和惩罚等，需要使用统计资料的，必须依照《统计法》第十三条的规定，以统计机构或者统计负责人签署或者盖章的统计资料为准。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十七条　</b></font></font><font color="#333333"><font size="4">县级以上各级人民政府统计机构必须做好统计信息咨询服务工作，充分利用可以公开的社会经济信息为社会公众服务。</font></font></p><p><font color="#333333"><font size="4">　　符合国家有关规定，在《统计法》和统计制度规定之外提供统计信息咨询，实行有偿服务。具体办法由国家统计局会同国务院价格主管部门制定。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十八条　</b></font></font><font color="#333333"><font size="4">各地方、各部门、各单位必须执行国家有关统计资料保密管理的规定，加强对统计资料的保密管理。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十九条　</b></font></font><font color="#333333"><font size="4">各地方、各部门、各单位必须建立统计资料档案制度。统计资料档案的保管、调用和移交，应当遵守国家有关档案管理的规定。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十条　</b></font></font><font color="#333333"><font size="4">国家建立健全统计资料定期公布制度。</font></font></p><p><font color="#333333"><font size="4">　　国家统计局统计调查取得的统计数据，由国家统计局公布。国务院有关部门统计调查取得的统计数据，由国务院有关部门公布；其中，与国家统计局统计调查取得的统计数据有重复、交叉的，应当在同国家统计局协商后，由国务院有关部门公布。国务院有关部门公布统计数据，应当自公布之日起</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">10</font></font></span></font></font><font color="#333333"><font size="4">日内报国家统计局备案。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方各级人民政府统计机构和有关部门公布其统计调查取得的地方统计数据，比照前款规定执行。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十一条　</b></font></font><font color="#333333"><font size="4">国家建立健全统计数据质量监控和评估的制度，加强对各省、自治区、直辖市重要统计数据的监控和评估。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第四章　统计机构和统计人员</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十二条　</b></font></font><font color="#333333"><font size="4">国家统计局履行下列职责：</font></font></p><p><font color="#333333"><font size="4">　　（一）根据有关法律、行政法规和国家有关政策和计划，制定统计工作规章，制订统计工作现代化规划和国家统计调查计划，组织领导和协调全国统计工作，监督检查统计法规和统计制度的实施；</font></font></p><p><font color="#333333"><font size="4">　　（二）健全国民经济核算制度和统计指标体系，制定全国统一的基本统计报表制度；制定或者与有关部门共同制定国家统计标准，审定部门统计标准；</font></font></p><p><font color="#333333"><font size="4">　　（三）在国务院领导下，会同有关部门组织重大的国情国力普查，组织、协调全国社会经济抽样调查；</font></font></p><p><font color="#333333"><font size="4">　　（四）根据国家制定政策、计划和进行管理的需要，搜集、整理、提供全国性的基本统计资料，对国民经济和社会发展情况进行统计分析、统计预测和统计监督；</font></font></p><p><font color="#333333"><font size="4">　　（五）审查国务院各部门编制的统计调查计划和统计调查方案，管理国务院各部门制发的统计调查表；</font></font></p><p><font color="#333333"><font size="4">　　（六）检查、审定、管理、公布、出版全国性的基本统计资料，定期发布全国国民经济和社会发展情况的统计公报；</font></font></p><p><font color="#333333"><font size="4">　　（七）统一领导和管理国家统计局派出的调查队；</font></font></p><p><font color="#333333"><font size="4">　　（八）组织指导全国统计科学研究、统计教育、统计干部培训和统计书刊出版工作；</font></font></p><p><font color="#333333"><font size="4">　　（九）开展统计工作和统计科学的国际交流。</font></font></p><p><font color="#333333"><font size="4">　　国家统计局派出的调查队承担国家统计局布置的各项调查任务，依法独立开展统计调查，独立上报统计资料。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十三条　</b></font></font><font color="#333333"><font size="4">县级以上地方各级人民政府统计机构履行下列职责：</font></font></p><p><font color="#333333"><font size="4">　　（一）完成国家统计调查任务，执行国家统计标准，执行全国统一的基本统计报表制度；</font></font></p><p><font color="#333333"><font size="4">　　（二）制订本行政区域内的统计工作现代化规划、统计调查计划和统计调查方案，统一领导和协调本行政区域内包括中央和地方单位的统计工作，监督检查统计法规和统计制度的实施；</font></font></p><p><font color="#333333"><font size="4">　　（三）根据本行政区域内制定计划和进行管理的需要，搜集、整理、提供基本统计资料，对本行政区域内国民经济和社会发展情况进行统计分析、统计预测和统计监督；</font></font></p><p><font color="#333333"><font size="4">　　（四）审查本行政区域内各部门的统计调查计划和统计调查方案，管理本行政区域内各部门制发的统计调查表；</font></font></p><p><font color="#333333"><font size="4">　　（五）按照国家有关规定，检查、审定、管理、公布、出版本行政区域内的基本统计资料；省、自治区、直辖市人民政府统计机构定期发布本行政区域内国民经济和社会发展情况的统计公报；自治州、县、自治县、市、市辖区人民政府统计机构按照本级人民政府的决定，发布本行政区域内国民经济和社会发展情况的统计公报；</font></font></p><p><font color="#333333"><font size="4">　　（六）组织指导本行政区域内各部门、各单位加强统计基础工作建设，加强统计教育、统计干部培训和统计科学研究工作；对本行政区域内人民政府统计机构干部和乡、镇统计员进行考核和奖励。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方各级人民政府统计机构受本级人民政府和上级人民政府统计机构的双重领导，在统计业务上以上级人民政府统计机构的领导为主。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十四条　</b></font></font><font color="#333333"><font size="4">乡、镇统计员执行乡、镇综合统计的职能，履行下列职责：</font></font></p><p><font color="#333333"><font size="4">　　（一）完成国家统计调查和地方统计调查任务，执行国家统计标准，执行全国统一的基本统计报表制度，执行统计法规和统计制度，监督检查统计法规和统计制度的实施；</font></font></p><p><font color="#333333"><font size="4">　　（二）按照国家有关规定，搜集、整理、分析、提供和管理本乡、镇的基本统计资料；</font></font></p><p><font color="#333333"><font size="4">　　（三）组织指导本乡、镇各有关单位、人员加强农村统计基础工作建设，健全本乡、镇的统计台账制度和统计档案制度，组织乡、镇以下的统计业务工作。</font></font></p><p><font color="#333333"><font size="4">　　乡、镇人民政府应当根据《统计法》等有关规定和统计工作的需要，设置专职的或者兼职的统计员，建立健全乡、镇统计信息网络。乡、镇统计员和乡、镇统计信息网络在统计业务上受县级人民政府统计机构的领导。</font></font></p><p><font color="#333333"><font size="4">　　村的统计工作，由村民委员会指定专人负责，其在统计业务上受乡、镇统计员的领导。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十五条　</b></font></font><font color="#333333"><font size="4">县级以上各级人民政府有关部门的统计机构或者统计负责人执行本部门综合统计的职能，履行下列职责：</font></font></p><p><font color="#333333"><font size="4">　　（一）组织指导、综合协调本部门各职能机构（包括生产、供销、基建、劳动人事、财务会计等机构）的统计工作，共同完成国家统计调查、部门统计调查和地方统计调查任务，执行统计法规和统计制度，监督检查统计法规和统计制度的实施；</font></font></p><p><font color="#333333"><font size="4">　　（二）制订本部门的统计工作现代化规划、统计调查计划和统计调查方案，组织指导本部门及其管辖系统内企业事业组织的统计工作，加强统计队伍和统计基础工作建设；</font></font></p><p><font color="#333333"><font size="4">　　（三）按照国家有关规定，向上级领导机关和本级人民政府统计机构报送和提供本部门的基本统计资料，会同计划和其他有关职能机构对本部门执行政策、计划和经营管理效益的情况，进行统计分析、统计预测和统计监督；</font></font></p><p><font color="#333333"><font size="4">　　（四）管理本部门制发的统计调查表和基本统计资料；</font></font></p><p><font color="#333333"><font size="4">　　（五）会同本部门的人事教育机构，组织指导本部门的统计教育和统计干部培训；对本部门统计人员进行考核和奖励；加强本部门统计科学研究工作。</font></font></p><p><font color="#333333"><font size="4">　　县级以上各级人民政府有关部门统计机构的设置，应当根据实际需要，本着精简、效能的原则，依照《统计法》的规定执行。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十六条　</b></font></font><font color="#333333"><font size="4">企业事业组织的统计机构或者统计负责人执行本单位综合统计的职能，履行下列职责：</font></font></p><p><font color="#333333"><font size="4">　　（一）组织指导、综合协调本单位各职能机构和下属机构的统计工作，共同完成国家统计调查、部门统计调查和地方统计调查任务，制订、实施本单位的统计工作计划和统计制度，执行统计法规和统计制度，监督检查统计法规和统计制度的实施；</font></font></p><p><font color="#333333"><font size="4">　　（二）按照国家有关规定，报送和提供统计资料，对本单位计划的执行情况和经营管理的效益，进行统计分析和统计监督；</font></font></p><p><font color="#333333"><font size="4">　　（三）管理本单位的统计调查表和基本统计资料；</font></font></p><p><font color="#333333"><font size="4">　　（四）会同本单位有关职能机构完善计量、检测制度，建立健全原始记录、统计台账和核算制度。</font></font></p><p><font color="#333333"><font size="4">　　企业事业组织的统计机构或者统计负责人在统计业务上，受所在地人民政府统计机构或者乡、镇统计员的指导。</font></font></p><p><font color="#333333"><font size="4">　　中小型企业事业组织不单设统计人员的，可以指定人员专门负责统计工作。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十七条　</b></font></font><font color="#333333"><font size="4">统计负责人，是指代表本部门或者本单位履行《统计法》规定职责的主要责任人员。不设统计机构的，一般应当由具备相当统计专业技术职务条件的人员担任统计负责人。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十八条　</b></font></font><font color="#333333"><font size="4">各地方、各部门、各单位应当根据国家有关规定和工作需要，设置统计专业技术职务。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十九条　</b></font></font><font color="#333333"><font size="4">具有统计专业技术职务的人员的调动，应当分别征求本地区、本部门、本单位统计机构或者统计负责人的意见；其中，具有中级以上统计专业技术职务的人员的调动，应当征得上级统计机构的同意。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方各级人民政府统计机构主要负责人的调动，应当征得上一级人民政府统计机构的同意。乡、镇统计员的调动，应当征得县级人民政府统计机构的同意。</font></font></p><p><font color="#333333"><font size="4">　　各部门和企业事业组织统计负责人的调动，应当征求上级主管部门和所在地人民政府统计机构的意见。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十条　</b></font></font><font color="#333333"><font size="4">国家统计局和县级以上地方各级人民政府统计机构，应当有计划地对统计人员进行培训，加强对统计人员的职业道德教育，提高统计人员的业务素质。</font></font></p><p><font color="#333333"><font size="4">　　国家统计局和县级以上地方各级人民政府统计机构增加和补充统计人员，应当从具备统计专业知识的人员中选调。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第五章　奖励和惩罚</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十一条　</b></font></font><font color="#333333"><font size="4">县级以上各级人民政府统计机构、各部门、各企业事业组织，应当依照国家或者企业事业组织的规定，对有下列表现之一的统计人员或者集体，定期评比，给予奖励：</font></font></p><p><font color="#333333"><font size="4">　　（一）在改革和完善统计制度、统计方法等方面，做出重要贡献的；</font></font></p><p><font color="#333333"><font size="4">　　（二）在完成规定的统计调查任务，保障统计资料的准确性、及时性方面，做出显著成绩的；</font></font></p><p><font color="#333333"><font size="4">　　（三）在进行统计分析、统计预测和统计监督方面，有所创新，取得重要成绩的；</font></font></p><p><font color="#333333"><font size="4">　　（四）在运用和推广现代信息技术方面，取得显著效果的；</font></font></p><p><font color="#333333"><font size="4">　　（五）在改进统计教育和统计专业培训，进行统计科学研究，提高统计科学水平方面，做出重要贡献的；</font></font></p><p><font color="#333333"><font size="4">　　（六）坚持实事求是，依法办事，同违反统计法规和统计制度的行为作斗争，表现突出的；</font></font></p><p><font color="#333333"><font size="4">　　（七）揭发、检举统计违法行为有功的。</font></font></p><p><font color="#333333"><font size="4">　　奖励分为：通令嘉奖、记功、记大功、晋级、升职、授予荣誉称号，并可以发给奖品、奖金。奖金按照国家或者企业事业组织的规定在有关经费中开支。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十二条　</b></font></font><font color="#333333"><font size="4">下列行为，属于《统计法》第二十七条第一款所称情节较重的违法行为：</font></font></p><p><font color="#333333"><font size="4">　　（一）虚报、瞒报、伪造、篡改统计资料数额较大或者占应报数额的份额较多的；</font></font></p><p><font color="#333333"><font size="4">　　（二）虚报、瞒报、伪造、篡改或者拒报统计资料，二年内再次发生的；</font></font></p><p><font color="#333333"><font size="4">　　（三）虚报、瞒报、伪造、篡改、拒报或者屡次迟报统计资料，被责令改正而拒不改正的；</font></font></p><p><font color="#333333"><font size="4">　　（四）虚报、瞒报、伪造、篡改、拒报或者屡次迟报统计资料，造成严重后果或者恶劣影响的；</font></font></p><p><font color="#333333"><font size="4">　　（五）在接受统计检查时，拒绝提供情况、提供虚假情况或者转移、隐匿、毁弃原始统计记录、统计台账、统计报表以及与统计有关的其他资料的；</font></font></p><p><font color="#333333"><font size="4">　　（六）使用暴力或者威胁的方法阻挠、抗拒统计检查的；</font></font></p><p><font color="#333333"><font size="4">　　（七）国家统计局依法认定的其他行为。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十三条　</b></font></font><font color="#333333"><font size="4">企业事业组织有《统计法》第二十七条第一款所列违法行为之一的，由县级以上人民政府统计机构或者国家统计局派出的调查队予以警告，并可以处</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">5</font></font></span></font></font><font color="#333333"><font size="4">万元以下的罚款。</font></font></p><p><font color="#333333"><font size="4">　　个体工商户有《统计法》第二十七条第一款所列违法行为之一的，由县级以上人民政府统计机构或者国家统计局派出的调查队予以警告，并可以处</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font></font><font color="#333333"><font size="4">万元以下的罚款。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十四条　</b></font></font><font color="#333333"><font size="4">任何单位或者个人有《统计法》第二十九条第二款所列违法行为的，由县级以上人民政府统计机构责令改正，没收违法所得，并可以处违法所得</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font></font><font color="#333333"><font size="4">倍以上</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">3</font></font></span></font></font><font color="#333333"><font size="4">倍以下的罚款；没有违法所得的，可以处</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">3</font></font></span></font></font><font color="#333333"><font size="4">万元以下的罚款。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第六章　附　则</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十五条　</b></font></font><font color="#333333"><font size="4">中华人民共和国境外的组织、个人需要在中华人民共和国境内进行统计调查活动的，应当委托中华人民共和国境内具有涉外统计调查资格的机构进行。</font></font></p><p><font color="#333333"><font size="4">　　统计调查范围限于省、自治区、直辖市行政区域内的，应当持有关证明文件和统计调查方案，向省、自治区、直辖市人民政府统计机构提出申请，由省、自治区、直辖市人民政府统计机构审批；统计调查范围跨省、自治区、直辖市行政区域的，应当持有关证明文件和统计调查方案，向国家统计局提出申请，由国家统计局审批。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十六条　</b></font></font><font color="#333333"><font size="4">本细则自发布之日起施行。</font></font></p>', 1488045987, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(120, 45, '全国经济普查条例', '<p align="CENTER"><font color="#333333"><font size="4"><b>中华人民共和国国务院令</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">415</font></font></span></font></font><font color="#333333"><font size="4">号</font></font></p><p><font color="#333333"><font size="4">　　现公布《全国经济普查条例》，自公布之日起施行。</font></font></p><p><font color="#333333"><font size="4">总理　温家宝    </font></font></p><p><font color="#333333"><font size="4">二〇〇四年九月五日</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>全国经济普查条例</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">第一章　总 则</font></font></p><p><font color="#333333"><font size="4"><b>　　第一条　</b></font></font><font color="#333333"><font size="4">为了科学、有效地组织实施全国经济普查，保障经济普查数据的准确性和及时性，根据《中华人民共和国统计法》，制定本条例。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二条　</b></font></font><font color="#333333"><font size="4">经济普查的目的，是为了全面掌握我国第二产业、第三产业的发展规模、结构和效益等情况，建立健全基本单位名录库及其数据库系统，为研究制定国民经济和社会发展规划，提高决策和管理水平奠定基础。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三条　</b></font></font><font color="#333333"><font size="4">经济普查工作按照全国统一领导、部门分工协作、地方分级负责、各方共同参与的原则组织实施。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四条　</b></font></font><font color="#333333"><font size="4">国家机关、社会团体、企业事业单位、其他组织和个体经营户应当依照《中华人民共和国统计法》和本条例的规定，积极参与并密切配合经济普查工作。</font></font></p><p><font color="#333333"><font size="4"><b>　　第五条　</b></font></font><font color="#333333"><font size="4">各级宣传部门应当充分利用报刊、广播、电视、互联网和户外广告等媒体，认真做好经济普查的社会宣传、动员工作。</font></font></p><p><font color="#333333"><font size="4"><b>　　第六条　</b></font></font><font color="#333333"><font size="4">经济普查所需经费，由中央和地方各级人民政府共同负担，并列入相应年度的财政预算，按时拨付，确保到位。</font></font></p><p><font color="#333333"><font size="4">　　经济普查经费应当统一管理、专款专用，从严控制支出。</font></font></p><p><font color="#333333"><font size="4"><b>　　第七条　</b></font></font><font color="#333333"><font size="4">经济普查每</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">5</font></font></span></font></font><font color="#333333"><font size="4">年进行一次，标准时点为普查年份的</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">12</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">31</font></font></span></font></font><font color="#333333"><font size="4">日。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第二章　经济普查对象、范围和方法</font></font></p><p><font color="#333333"><font size="4"><b>　　第八条　</b></font></font><font color="#333333"><font size="4">经济普查对象是在中华人民共和国境内从事第二产业、第三产业活动的全部法人单位、产业活动单位和个体经营户。</font></font></p><p><font color="#333333"><font size="4"><b>　　第九条　</b></font></font><font color="#333333"><font size="4">经济普查对象有义务接受经济普查机构和经普查人员依法进行的调查。</font></font></p><p><font color="#333333"><font size="4">　　经济普查对象应当如实、按时填报经济普查表，不得虚报、瞒报、拒报和迟报经济普查数据。</font></font></p><p><font color="#333333"><font size="4">　　经济普查对象应当按照经济普查机构和经济普查人员的要求，及时提供与经济普查有关的资料。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十条　</b></font></font><font color="#333333"><font size="4">经济普查的行业范围包括：</font></font></p><p><font color="#333333"><font size="4">　　（一）采矿业；</font></font></p><p><font color="#333333"><font size="4">　　（二）制造业；</font></font></p><p><font color="#333333"><font size="4">　　（三）电力、燃气及水的生产和供应业；</font></font></p><p><font color="#333333"><font size="4">　　（四）建筑业；</font></font></p><p><font color="#333333"><font size="4">　　（五）交通运输、仓储和邮政业；</font></font></p><p><font color="#333333"><font size="4">　　（六）信息传输、计算机服务和软件业；</font></font></p><p><font color="#333333"><font size="4">　　（七）批发和零售业；</font></font></p><p><font color="#333333"><font size="4">　　（八）住宿和餐饮业；</font></font></p><p><font color="#333333"><font size="4">　　（九）金融业；</font></font></p><p><font color="#333333"><font size="4">　　（十）房地产业；</font></font></p><p><font color="#333333"><font size="4">　　（十一）租赁和商务服务业；</font></font></p><p><font color="#333333"><font size="4">　　（十二）科学研究、技术服务和地质勘查业；</font></font></p><p><font color="#333333"><font size="4">　　（十三）水利、环境和公共设施管理业；</font></font></p><p><font color="#333333"><font size="4">　　（十四）居民服务和其他服务业；</font></font></p><p><font color="#333333"><font size="4">　　（十五）教育；</font></font></p><p><font color="#333333"><font size="4">　　（十六）卫生、社会保障和社会福利业；</font></font></p><p><font color="#333333"><font size="4">　　（十七）文化、体育和娱乐业；</font></font></p><p><font color="#333333"><font size="4">　　（十八）公共管理和社会组织等。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十一条　</b></font></font><font color="#333333"><font size="4">经济普查采用全面调查的方法，但对个体经营户的生产经营情况可以采用抽样调查的方法。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第三章　经济普查表式、主要内容和标准</font></font></p><p><font color="#333333"><font size="4"><b>　　第十二条　</b></font></font><font color="#333333"><font size="4">经济普查按照对象的不同类型，设置法人单位调查表、产业活动单位调查表和个体经营户调查表。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十三条　</b></font></font><font color="#333333"><font size="4">经济普查的主要内容包括：单位基本属性、从业人员、财务状况、生产经营情况、生产能力、原材料和能源消耗、科技活动情况等。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十四条　</b></font></font><font color="#333333"><font size="4">经济普查采用国家规定的统计分类标准和目录。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第四章　经济普查的组织实施</font></font></p><p><font color="#333333"><font size="4"><b>　　第十五条　</b></font></font><font color="#333333"><font size="4">国务院设立经济普查领导小组及其办公室。国务院经济普查领导小组负责经济普查的组织和实施。领导小组办公室设在国家统计局，具体负责经济普查的日常组织和协调。</font></font></p><p><font color="#333333"><font size="4">　　国务院各有关部门应当各负其责、密切配合，认真做好相关工作。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十六条　</b></font></font><font color="#333333"><font size="4">地方各级人民政府设立经济普查领导小组及其办公室，按照国务院经济普查领导小组及其办公室的统一规定和要求，具体组织实施当地的经济普查工作。</font></font></p><p><font color="#333333"><font size="4">　　街道办事处和居（村）民委员会应当广泛动员和组织社会力量积极参与并认真做好经济普查工作。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十七条　</b></font></font><font color="#333333"><font size="4">国务院和地方各级人民政府有关部门设立经济普查机构，负责完成国务院和本级地方人民政府经济普查领导小组办公室指定的经济普查任务。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十八条　</b></font></font><font color="#333333"><font size="4">大型企业应当设立经济普查机构，负责本企业经济普查表的填报工作。其他各类法人单位应当指定相关人员负责本单位经济普查表的填报工作。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十九条　</b></font></font><font color="#333333"><font size="4">地方各级经济普查机构应当根据工作需要，聘用或者从有关单位商调普查指导员和普查员。各有关单位应当积极推荐符合条件的人员担任普查指导员和普查员。</font></font></p><p><font color="#333333"><font size="4">　　普查指导员和普查员应当身体健康、责任心强并具有相应的专业知识。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十条　</b></font></font><font color="#333333"><font size="4">聘用人员应当由当地经济普查机构支付劳动报酬。商调人员的工资由原单位支付，其福利待遇保持不变。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十一条　</b></font></font><font color="#333333"><font size="4">地方各级经济普查机构应当统一对普查指导员和普查员进行业务培训，并经考核合格后颁发普查指导员证或者普查员证。普查指导员和普查员在执行经济普查任务时，应当主动出示证件。</font></font></p><p><font color="#333333"><font size="4">　　普查员负责组织指导经济普查对象填报经济普查表，普查指导员负责指导、检查普查员的工作。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十二条　</b></font></font><font color="#333333"><font size="4">普查指导员和普查员有权查阅法人单位、产业活动单位和个体经营户与经济普查有关的财务会计、统计和业务核算等相关原始资料及有关经营证件，有权要求经济普查对象改正其经济普查表中不确实的内容。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十三条　</b></font></font><font color="#333333"><font size="4">各级经济普查机构在经济普查准备阶段应当进行单位清查，准确界定经济普查表的种类。</font></font></p><p><font color="#333333"><font size="4">　　各级编制、民政、税务、工商、质检以及其他具有单位设立审批、登记职能的部门，负责向同级经济普查机构提供其审批或者登记的单位资料，并共同做好单位清查工作。</font></font></p><p><font color="#333333"><font size="4">　　县级经济普查机构以本地区现有基本单位名录库为基础，结合有关部门提供的单位资料，按照经济普查小区逐一核实清查，形成经济普查单位名录。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十四条　</b></font></font><font color="#333333"><font size="4">县级经济普查机构应当按照清查形成的单位名录，做好经济普查表的发放、收集、审核、录入和上报工作。</font></font></p><p><font color="#333333"><font size="4">　　法人单位填报法人单位调查表，并负责组织其下属的产业活动单位填报产业活动单位调查表。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十五条　</b></font></font><font color="#333333"><font size="4">各级经济普查机构和经济普查人员依法独立行使调查、报告、监督的职权，任何单位和个人不得干涉。</font></font></p><p><font color="#333333"><font size="4">　　各地方、各部门、各单位的领导人对经济普查机构和经济普查人员依法提供的经济普查资料不得自行修改，不得强令或者授意经济普查机构、经济普查人员篡改经济普查资料或者编造虚假数据。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第五章　数据处理和质量控制</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十六条　</b></font></font><font color="#333333"><font size="4">经济普查的数据处理工作由县级以上各级经济普查机构组织实施。</font></font></p><p><font color="#333333"><font size="4">　　国务院经济普查领导小组办公室负责提供各地方使用的数据处理标准和程序。</font></font></p><p><font color="#333333"><font size="4">　　地方各级经济普查机构按照国务院经济普查领导小组办公室的统一要求和标准进行数据处理，并逐级上报经济普查数据。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十七条　</b></font></font><font color="#333333"><font size="4">经济普查数据处理结束后，各级经济普查机构应当做好数据备份和数据入库工作，建立健全基本单位名录库及其数据库系统，并强化日常管理和维护更新。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十八条　</b></font></font><font color="#333333"><font size="4">地方各级经济普查机构应当根据国务院经济普查领导小组办公室的统一规定，建立经济普查数据质量控制岗位责任制，并对经济普查实施中的每个环节实行质量控制和检查验收。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十九条　</b></font></font><font color="#333333"><font size="4">国务院经济普查领导小组办公室统一组织经济普查数据的质量抽查工作，抽查结果作为评估全国及各地区经济普查数据质量的主要依据。</font></font></p><p><font color="#333333"><font size="4">　　各级经济普查机构应当对经济普查的汇总数据进行认真分析和综合评估。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第六章　数据公布、资料管理和开发应用</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十条　</b></font></font><font color="#333333"><font size="4">各级经济普查机构应当按照国家规定发布经济普查公报。</font></font></p><p><font color="#333333"><font size="4">　　地方各级经济普查机构发布经济普查公报应当经上一级经济普查机构核准。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十一条　</b></font></font><font color="#333333"><font size="4">各级经济普查机构应当认真做好经济普查资料的保存、管理和对社会公众提供服务等项工作，并对经济普查资料进行开发和应用。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十二条　</b></font></font><font color="#333333"><font size="4">各级经济普查机构及其工作人员应当遵守《中华人民共和国统计法》和《中华人民共和国统计法实施细则》的有关规定，对在经济普查中所知悉的国家秘密和经济普查对象的商业秘密，履行保密义务。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十三条　</b></font></font><font color="#333333"><font size="4">经济普查取得的单位和个人资料，严格限定用于经济普查的目的，不作为任何单位对经济普查对象实施处罚的依据。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第七章　表彰和处罚</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十四条　</b></font></font><font color="#333333"><font size="4">对在经济普查工作中贡献突出的先进集体和先进个人，由各级经济普查机构给予表彰和奖励。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十五条　</b></font></font><font color="#333333"><font size="4">地方、部门、单位的领导人自行修改经济普查资料、编造虚假数据或者强令、授意经济普查机构、经济普查人员篡改经济普查资料或者编造虚假数据的，依法给予行政处分或者纪律处分，并由县级以上人民政府统计机构给予通报批评。</font></font></p><p><font color="#333333"><font size="4">　　经济普查人员参与篡改经济普查资料、编造虚假数据的，由县级以上人民政府统计机构给予通报批评，依法给予行政处分，或者建议有关部门、单位依法给予行政处分或者纪律处分。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十六条　</b></font></font><font color="#333333"><font size="4">经济普查对象有下列违法行为之一的，由县级以上人民政府统计机构责令改正，给予通报批评；情节较重的，可以建议有关部门、单位对负有直接责任的主管人员和其他直接责任人员依法给予行政处分或者纪律处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）拒绝或者妨碍接受经济普查机构、经济普查人员依法进行的调查的；</font></font></p><p><font color="#333333"><font size="4">　　（二）提供虚假或者不完整的经济普查资料的；</font></font></p><p><font color="#333333"><font size="4">　　（三）未按时提供与经济普查有关的资料，经催报后仍未提供的。</font></font></p><p><font color="#333333"><font size="4">　　企业事业组织有前款所列违法行为之一的，由县级以上人民政府统计机构给予警告，并可以处</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">5</font></font></span></font></font><font color="#333333"><font size="4">万元以下罚款；个体经营户有前款所列违法行为之一的，由县级以上人民政府统计机构给予警告，并可以处</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font></font><font color="#333333"><font size="4">万元以下罚款。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十七条　</b></font></font><font color="#333333"><font size="4">各级经济普查机构应当设立举报电话，接受社会各界对经济普查中单位和个人违法行为的检举和监督，并对举报有功人员给予奖励。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第八章　附 则</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十八条　</b></font></font><font color="#333333"><font size="4">本条例自公布之日起施行</font></font></p>', 1488046015, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(121, 45, '全国农业普查条例', '<p align="CENTER"><a name="_GoBack"></a><font size="4">中华人民共和国国务院令</font></p><p align="CENTER"><font size="4">第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">473</font></font></span></font><font size="4">号</font></p><p><font size="4">　　现公布《全国农业普查条例》，自公布之日起施行。</font></p><p><font size="4">总　理　 温家宝    </font></p><p><font size="4">二〇〇六年八月二十三日</font></p><p align="CENTER"><font size="4">全国农业普查条例</font></p><p align="CENTER"><font size="4">第一章　总　　则</font></p><p><font size="4">　　第一条　为了科学、有效地组织实施全国农业普查，保障农业普查数据的准确性和及时性，根据《中华人民共和国统计法》，制定本条例。</font></p><p><font size="4">　　第二条　农业普查的目的，是全面掌握我国农业、农村和农民的基本情况，为研究制定经济社会发展战略、规划、政策和科学决策提供依据，并为农业生产经营者和社会公众提供统计信息服务。</font></p><p><font size="4">　　第三条　农业普查工作按照全国统一领导、部门分工协作、地方分级负责的原则组织实施。</font></p><p><font size="4">　　第四条　国家机关、社会团体以及与农业普查有关的单位和个人，应当依照《中华人民共和国统计法》和本条例的规定，积极参与并密切配合农业普查工作。</font></p><p><font size="4">　　第五条　各级农业普查领导小组办公室（以下简称普查办公室）和普查办公室工作人员、普查指导员、普查员（以下统称普查人员）依法独立行使调查、报告、监督的职权，任何单位和个人不得干涉。</font></p><p><font size="4">　　各地方、各部门、各单位的领导人对普查办公室和普查人员依法提供的农业普查资料不得自行修改，不得强令、授意普查办公室、普查人员和普查对象篡改农业普查资料或者编造虚假数据，不得对拒绝、抵制篡改农业普查资料或者拒绝、抵制编造虚假数据的人员打击报复。</font></p><p><font size="4">　　第六条　各级宣传部门应当充分利用报刊、广播、电视、互联网和户外广告等媒体，采取多种形式，认真做好农业普查的宣传动员工作。</font></p><p><font size="4">　　第七条　农业普查所需经费，由中央和地方各级人民政府共同负担，并列入相应年度的财政预算，按时拨付，确保足额到位。</font></p><p><font size="4">　　农业普查经费应当统一管理、专款专用、从严控制支出。</font></p><p><font size="4">　　第八条　农业普查每</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">10</font></font></span></font><font size="4">年进行一次，尾数逢</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">6</font></font></span></font><font size="4">的年份为普查年度，标准时点为普查年度的</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">12</font></font></span></font><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">31</font></font></span></font><font size="4">日</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">24</font></font></span></font><font size="4">时。特殊地区的普查登记时间经国务院农业普查领导小组办公室批准，可以适当调整。</font></p><p align="CENTER"><font size="4">第二章　农业普查的对象、范围和内容</font></p><p><font size="4">　　第九条　农业普查对象是在中华人民共和国境内的下列个人和单位：</font></p><p><font size="4">　　（一）农村住户，包括农村农业生产经营户和其他住户；</font></p><p><font size="4">　　（二）城镇农业生产经营户；</font></p><p><font size="4">　　（三）农业生产经营单位；</font></p><p><font size="4">　　（四）村民委员会；</font></p><p><font size="4">　　（五）乡镇人民政府。</font></p><p><font size="4">　　第十条　农业普查对象应当如实回答普查人员的询问，按时填报农业普查表，不得虚报、瞒报、拒报和迟报。</font></p><p><font size="4">　　农业普查对象应当配合县级以上人民政府统计机构和国家统计局派出的调查队依法进行的监督检查，如实反映情况，提供有关资料，不得拒绝、推诿和阻挠检查，不得转移、隐匿、篡改、毁弃原始记录、统计台账、普查表、会计资料及其他相关资料。</font></p><p><font size="4">　　第十一条　农业普查行业范围包括：农作物种植业、林业、畜牧业、渔业和农林牧渔服务业。</font></p><p><font size="4">　　第十二条　农业普查内容包括：农业生产条件、农业生产经营活动、农业土地利用、农村劳动力及就业、农村基础设施、农村社会服务、农民生活，以及乡镇、村民委员会和社区环境等情况。</font></p><p><font size="4">　　前款规定的农业普查内容，国务院农业普查领导小组办公室可以根据具体情况进行调整。</font></p><p><font size="4">　　第十三条　农业普查采用全面调查的方法。国务院农业普查领导小组办公室可以决定对特定内容采用抽样调查的方法。</font></p><p><font size="4">　　第十四条　农业普查采用国家统计分类标准。</font></p><p><font size="4">　　第十五条　农业普查方案由国务院农业普查领导小组办公室统一制订。</font></p><p><font size="4">　　省级普查办公室可以根据需要增设农业普查附表，报经国务院农业普查领导小组办公室批准后实施。</font></p><p align="CENTER"><font size="4">第三章　农业普查的组织实施</font></p><p><font size="4">　　第十六条　国务院设立农业普查领导小组及其办公室。国务院农业普查领导小组负责组织和领导全国农业普查工作。国务院农业普查领导小组办公室设在国家统计局，具体负责农业普查日常工作的组织和协调。</font></p><p><font size="4">　　第十七条　地方各级人民政府设立农业普查领导小组及其办公室，按照国务院农业普查领导小组及其办公室的统一规定和要求，负责本行政区域内农业普查的组织实施工作。国家统计局派出的调查队作为农业普查领导小组及其办公室的成员单位，参与农业普查的组织实施工作。</font></p><p><font size="4">　　村民委员会应当在乡镇人民政府的指导下做好本区域内的农业普查工作。</font></p><p><font size="4">　　第十八条　国务院和地方各级人民政府的有关部门应当积极参与并密切配合普查办公室开展农业普查工作。</font></p><p><font size="4">　　军队、武警部队所属农业生产单位的农业普查工作，由军队、武警部队分别负责组织实施。</font></p><p><font size="4">　　新疆生产建设兵团的农业普查工作，由新疆生产建设兵团农业普查领导小组及其办公室负责组织实施。</font></p><p><font size="4">　　第十九条　农村的普查现场登记按普查区进行。普查区以村民委员会管理地域为基础划分，每个普查区可以划分为若干个普查小区。</font></p><p><font size="4">　　城镇的普查现场登记，按照普查方案的规定进行。</font></p><p><font size="4">　　第二十条　每个普查小区配备一名普查员，负责普查的访问登记工作。每个普查区至少配备一名普查指导员，负责安排、指导和督促检查普查员的工作，也可以直接进行访问登记。</font></p><p><font size="4">　　普查指导员和普查员主要由有较高文化水平的乡村干部、村民小组长和其他当地居民担任。</font></p><p><font size="4">　　普查指导员和普查员应当身体健康、责任心强。</font></p><p><font size="4">　　第二十一条　普查办公室根据工作需要，可以聘用或者从其他有关单位借调人员从事农业普查工作。有关单位应当积极推荐符合条件的人员从事农业普查工作。</font></p><p><font size="4">　　聘用人员应当由聘用单位支付劳动报酬。借调人员的工资由原单位支付，其福利待遇保持不变。</font></p><p><font size="4">　　农业普查经费中应当对村普查指导员、普查员安排适当的工作补贴。</font></p><p><font size="4">　　第二十二条　地方普查办公室应当对普查指导员和普查员进行业务培训，并对考核合格的人员颁发全国统一的普查指导员证或者普查员证。</font></p><p><font size="4">　　第二十三条　普查人员有权就与农业普查有关的问题询问有关单位和个人，要求有关单位和个人如实提供有关情况和资料、修改不真实的资料。</font></p><p><font size="4">　　第二十四条　普查人员应当坚持实事求是，恪守职业道德，拒绝、抵制农业普查工作中的违法行为。</font></p><p><font size="4">　　普查人员应当严格执行普查方案，不得伪造、篡改普查资料，不得强令、授意普查对象提供虚假的普查资料。</font></p><p><font size="4">　　普查指导员和普查员执行农业普查任务时，应当出示普查指导员证或者普查员证。</font></p><p><font size="4">　　第二十五条　普查员应当依法直接访问普查对象，当场进行询问、填报。普查表填写完成后，应当由普查对象签字或者盖章确认。普查对象应当对其签字或者盖章的普查资料的真实性负责。</font></p><p><font size="4">　　普查人员应当对其负责登记、审核、录入的普查资料与普查对象签字或者盖章的普查资料的一致性负责。</font></p><p><font size="4">　　普查办公室应当对其加工、整理的普查资料的准确性负责。</font></p><p align="CENTER"><font size="4">第四章　数据处理和质量控制</font></p><p><font size="4">　　第二十六条　农业普查数据处理方案和实施办法，由国务院农业普查领导小组办公室制订。</font></p><p><font size="4">　　地方普查办公室应当按照数据处理方案和实施办法的规定进行数据处理，并按时上报普查数据。</font></p><p><font size="4">　　第二十七条　农业普查的数据处理工作由设区的市级以上普查办公室组织实施。</font></p><p><font size="4">　　第二十八条　普查办公室应当做好数据备份和加载入库工作，建立健全农业普查数据库系统，并加强日常管理和维护更新。</font></p><p><font size="4">　　第二十九条　国家建立农业普查数据质量控制制度。</font></p><p><font size="4">　　普查办公室应当对普查实施中的每个环节实行质量控制和检查验收。</font></p><p><font size="4">　　第三十条　普查人员实行质量控制工作责任制。</font></p><p><font size="4">　　普查人员应当按照普查方案的规定对普查数据进行审核、复查和验收。</font></p><p><font size="4">　　第三十一条　国务院农业普查领导小组办公室统一组织农业普查数据的事后质量抽查工作。抽查结果作为评估全国或者各省、自治区、直辖市农业普查数据质量的重要依据。</font></p><p align="CENTER"><font size="4">第五章　数据公布、资料管理和开发应用</font></p><p><font size="4">　　第三十二条　国家建立农业普查资料公布制度。</font></p><p><font size="4">　　农业普查汇总资料，除依法予以保密的外，应当及时向社会公布。</font></p><p><font size="4">　　全国农业普查数据和各省、自治区、直辖市的主要农业普查数据，由国务院农业普查领导小组办公室审定并会同国务院有关部门公布。</font></p><p><font size="4">　　地方普查办公室发布普查公报，应当报经上一级普查办公室核准。</font></p><p><font size="4">　　第三十三条　普查办公室和普查人员对在农业普查工作中搜集的单个普查对象的资料，应予保密，不得用于普查以外的目的。</font></p><p><font size="4">　　第三十四条　普查办公室应当做好农业普查资料的保存、管理和为社会公众提供服务等工作，并对农业普查资料进行开发和应用。</font></p><p><font size="4">　　第三十五条　县级以上各级人民政府统计机构和有关部门可以根据农业普查结果，对有关常规统计的历史数据进行修正，具体办法由国家统计局规定。</font></p><p align="CENTER"><font size="4">第六章　表彰和处罚</font></p><p><font size="4">　　第三十六条　对认真执行本条例，忠于职守，坚持原则，做出显著成绩的单位和个人，应当给予奖励。</font></p><p><font size="4">　　第三十七条　地方、部门、单位的领导人自行修改农业普查资料，强令、授意普查办公室、普查人员和普查对象篡改农业普查资料或者编造虚假数据，对拒绝、抵制篡改农业普查资料或者拒绝、抵制编造虚假数据的人员打击报复的，依法给予行政处分或者纪律处分，并由县级以上人民政府统计机构或者国家统计局派出的调查队给予通报批评；构成犯罪的，依法追究刑事责任。</font></p><p><font size="4">　　第三十八条　普查人员不执行普查方案，伪造、篡改普查资料，强令、授意普查对象提供虚假普查资料的，由县级以上人民政府统计机构或者国家统计局派出的调查队责令改正，依法给予行政处分或者纪律处分，并可以给予通报批评。</font></p><p><font size="4">　　第三十九条　农业普查对象有下列违法行为之一的，由县级以上人民政府统计机构或者国家统计局派出的调查队责令改正，给予通报批评；情节严重的，对负有直接责任的主管人员和其他直接责任人员依法给予行政处分或者纪律处分：</font></p><p><font size="4">　　（一）拒绝或者妨碍普查办公室、普查人员依法进行调查的；</font></p><p><font size="4">　　（二）提供虚假或者不完整的农业普查资料的；</font></p><p><font size="4">　　（三）未按时提供与农业普查有关的资料，经催报后仍未提供的；</font></p><p><font size="4">　　（四）拒绝、推诿和阻挠依法进行的农业普查执法检查的；</font></p><p><font size="4">　　（五）在接受农业普查执法检查时，转移、隐匿、篡改、毁弃原始记录、统计台账、普查表、会计资料及其他相关资料的。</font></p><p><font size="4">　　农业生产经营单位有前款所列违法行为之一的，由县级以上人民政府统计机构或者国家统计局派出的调查队予以警告，并可以处</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">5</font></font></span></font><font size="4">万元以下罚款；农业生产经营户有前款所列违法行为之一的，由县级以上人民政府统计机构或者国家统计局派出的调查队予以警告，并可以处</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font><font size="4">万元以下罚款。</font></p><p><font size="4">　　农业普查对象有本条第一款第（一）、（四）项所列违法行为之一的，由公安机关依法给予治安管理处罚。</font></p><p><font size="4">　　第四十条　普查人员失职、渎职等造成严重后果的，应当依法给予行政处分或者纪律处分，并可以由县级以上人民政府统计机构或者国家统计局派出的调查队给予通报批评。</font></p><p><font size="4">　　第四十一条　普查办公室应当设立举报电话和信箱，接受社会各界对农业普查违法行为的检举和监督，并对举报有功人员给予奖励。</font></p><p align="CENTER"><font size="4">第七章　附　　则</font></p><p><font size="4">第四十二条　本条例自公布之日起施行</font></p>', 1488046047, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(122, 45, '全国人口普查条例', '<p align="CENTER"><font size="4"><b>中华人民共和国国务院令</b></font></p><p align="CENTER"><font size="4">第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">576</font></font></span></font><font size="4">号</font></p><p><font size="4">　　《全国人口普查条例》已经</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2010</font></font></span></font><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">5</font></font></span></font><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">12</font></font></span></font><font size="4">日国务院第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">111</font></font></span></font><font size="4">次常务会议通过，现予公布，自</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2010</font></font></span></font><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">6</font></font></span></font><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font><font size="4">日起施行。</font></p><p><font size="4">　                                  　 总理</font><font size="4"></font><font size="4">温家宝</font><font size="4"></font></p><p><font size="4">　　                                二○一○年五月二十四日</font></p><p align="CENTER"><font size="4"><b>全国人口普查条例</b></font></p><p align="CENTER"><font size="4">第一章</font><font size="4"></font><font size="4">总则</font></p><p><font size="4">　</font><font size="4"><b>　第一条</b></font><font size="4"><b></b></font><font size="4"></font><font size="4">为了科学、有效地组织实施全国人口普查，保障人口普查数据的真实性、准确性、完整性和及时性，根据《中华人民共和国统计法》，制定本条例。</font></p><p><font size="4">　</font><font size="4"><b>　第二条</b></font><font size="4"><b></b></font><font size="4">人口普查的目的是全面掌握全国人口的基本情况，为研究制定人口政策和经济社会发展规划提供依据，为社会公众提供人口统计信息服务。</font></p><p><font size="4">　　</font><font size="4"><b>第三条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">人口普查工作按照全国统一领导、部门分工协作、地方分级负责、各方共同参与的原则组织实施。</font></p><p><font size="4">　　国务院统一领导全国人口普查工作，研究决定人口普查中的重大问题。地方各级人民政府按照国务院的统一规定和要求，领导本行政区域的人口普查工作。</font></p><p><font size="4">　　在人口普查工作期间，各级人民政府设立由统计机构和有关部门组成的人口普查机构（以下简称普查机构），负责人口普查的组织实施工作。</font></p><p><font size="4">　　村民委员会、居民委员会应当协助所在地人民政府动员和组织社会力量，做好本区域的人口普查工作。</font></p><p><font size="4">　　国家机关、社会团体、企业事业单位应当按照《中华人民共和国统计法》和本条例的规定，参与并配合人口普查工作。</font></p><p><font size="4">　　</font><font size="4"><b>第四条</b></font><font size="4"><b></b></font><font size="4"></font><font size="4">人口普查对象应当按照《中华人民共和国统计法》和本条例的规定，真实、准确、完整、及时地提供人口普查所需的资料。</font></p><p><font size="4">　　人口普查对象提供的资料，应当依法予以保密。</font></p><p><font size="4">　</font><font size="4"><b>　第五条</b></font><font size="4"><b></b></font><font size="4">普查机构和普查机构工作人员、普查指导员、普查员（以下统称普查人员）依法独立行使调查、报告、监督的职权，任何单位和个人不得干涉。</font></p><p><font size="4">　　地方各级人民政府、各部门、各单位及其负责人，不得自行修改普查机构和普查人员依法搜集、整理的人口普查资料，不得以任何方式要求普查机构和普查人员及其他单位和个人伪造、篡改人口普查资料，不得对依法履行职责或者拒绝、抵制人口普查违法行为的普查人员打击报复。</font></p><p><font size="4">　</font><font size="4"><b>　第六条</b></font><font size="4"><b></b></font><font size="4">各级人民政府应当利用报刊、广播、电视、互联网和户外广告等媒介，开展人口普查的宣传动员工作。</font></p><p><font size="4">　</font><font size="4"><b>　第七条</b></font><font size="4"><b></b></font><font size="4">人口普查所需经费，由国务院和地方各级人民政府共同负担，并列入相应年度的财政预算，按时拨付，确保足额到位。</font></p><p><font size="4">　　人口普查经费应当统一管理、专款专用，从严控制支出。</font></p><p><font size="4">　　</font><font size="4"><b>第八条</b></font><font size="4"><b></b></font><font size="4">人口普查每</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">10</font></font></span></font><font size="4">年进行一次，尾数逢</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">0</font></font></span></font><font size="4">的年份为普查年度，标准时点为普查年度的</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">11</font></font></span></font><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font><font size="4">日零时。</font></p><p><font size="4">　</font><font size="4"><b>　第九条</b></font><font size="4"><b></b></font><font size="4">国家统计局会同国务院有关部门制定全国人口普查方案（以下简称普查方案），报国务院批准。</font></p><p><font size="4">　　人口普查应当按照普查方案的规定执行。</font></p><p><font size="4">　　</font><font size="4"><b>第十条</b></font><font size="4"><b></b></font><font size="4">对认真执行本条例，忠于职守、坚持原则，做出显著成绩的单位和个人，按照国家有关规定给予表彰和奖励。</font></p><p align="CENTER"><font size="4">第二章</font><font size="4"></font><font size="4">人口普查的对象、内容和方法</font></p><p><font size="4"><b>　　第十一条</b></font><font size="4"><b></b></font><font size="4"></font><font size="4">人口普查对象是指普查标准时点在中华人民共和国境内的自然人以及在中华人民共和国境外但未定居的中国公民，不包括在中华人民共和国境内短期停留的境外人员。</font></p><p><font size="4"><b>　　第十二条</b></font><font size="4"><b></b></font><font size="4"></font><font size="4">人口普查主要调查人口和住户的基本情况，内容包括姓名、性别、年龄、民族、国籍、受教育程度、行业、职业、迁移流动、社会保障、婚姻、生育、死亡、住房情况等。</font></p><p><font size="4"><b>　　第十三条</b></font><font size="4"><b></b></font><font size="4">人口普查采用全面调查的方法，以户为单位进行登记。</font></p><p><font size="4"><b>　　第十四条</b></font><font size="4"><b></b></font><font size="4">人口普查采用国家统计分类标准。</font></p><p align="CENTER"><font size="4">第三章</font><font size="4"></font><font size="4">人口普查的组织实施</font></p><p><font size="4"><b>　　第十五条</b></font><font size="4"><b></b></font><font size="4">人口普查登记前，公安机关应当按照普查方案的规定完成户口整顿工作，并将有关资料提交本级人口普查机构。</font></p><p><font size="4"><b>　　第十六条</b></font><font size="4"><b></b></font><font size="4">人口普查登记前应当划分普查区，普查区以村民委员会、居民委员会所辖区域为基础划分，每个普查区划分为若干普查小区。</font></p><p><font size="4">　</font><font size="4"><b>　第十七条</b></font><font size="4"><b></b></font><font size="4">每个普查小区应当至少有一名普查员，负责入户登记等普查工作。每个普查区应当至少有一名普查指导员，负责安排、指导、督促和检查普查员的工作，也可以直接进行入户登记。</font></p><p><font size="4">　　</font><font size="4"><b>第十八条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">普查指导员和普查员应当具有初中以上文化水平，身体健康，责任心强。</font></p><p><font size="4">　</font><font size="4"><b>　第十九条</b></font><font size="4"><b></b></font><font size="4">普查指导员和普查员可以从国家机关、社会团体、企业事业单位借调，也可以从村民委员会、居民委员会或者社会招聘。借调和招聘工作由县级人民政府负责。</font></p><p><font size="4">　　国家鼓励符合条件的公民作为志愿者参与人口普查工作</font></p><p><font size="4">　　</font><font size="4"><b>第二十条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">借调的普查指导员和普查员的工资由原单位支付，其福利待遇保持不变，并保留其原有工作岗位。</font></p><p><font size="4">　　招聘的普查指导员和普查员的劳动报酬，在人口普查经费中予以安排，由聘用单位支付。</font></p><p><font size="4">　　</font><font size="4"><b>第二十一条</b></font><font size="4"><b></b></font><font size="4">普查机构应当对普查指导员和普查员进行业务培训，并对考核合格的人员颁发全国统一的普查指导员证或者普查员证。</font></p><p><font size="4">　　普查指导员和普查员执行人口普查任务时，应当出示普查指导员证或者普查员证。</font></p><p><font size="4">　　</font><font size="4"><b>第二十二条</b></font><font size="4"><b></b></font><font size="4">人口普查登记前，普查指导员、普查员应当绘制普查小区图，编制普查小区户主姓名底册。</font></p><p><font size="4">　　</font><font size="4"><b>第二十三条</b></font><font size="4"><b></b></font><font size="4">普查指导员、普查员入户登记时，应当向人口普查对象说明人口普查的目的、法律依据以及人口普查对象的权利和义务。</font></p><p><font size="4">　　</font><font size="4"><b>第二十四条</b></font><font size="4"><b></b></font><font size="4"></font><font size="4">人口普查对象应当按时提供人口普查所需的资料，如实回答相关问题，不得隐瞒有关情况，不得提供虚假信息，不得拒绝或者阻碍人口普查工作。</font></p><p><font size="4">　　</font><font size="4"><b>第二十五条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">人口普查对象应当在普查表上签字或者盖章确认，并对其内容的真实性负责。</font></p><p><font size="4">　　</font><font size="4"><b>第二十六条</b></font><font size="4"><b></b></font><font size="4">普查人员应当坚持实事求是，恪守职业道德，拒绝、抵制人口普查工作中的违法行为。</font></p><p><font size="4">　　普查机构和普查人员不得伪造、篡改普查资料，不得以任何方式要求任何单位和个人提供虚假的普查资料。</font></p><p><font size="4">　　</font><font size="4"><b>第二十七条</b></font><font size="4"><b></b></font><font size="4">人口普查实行质量控制岗位责任制，普查机构应当对人口普查实施中的每个环节实行质量控制和检查，对人口普查数据进行审核、复查和验收。</font></p><p><font size="4">　</font><font size="4"><b>　第二十八条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">国家统计局统一组织人口普查数据的事后质量抽查工作。</font></p><p align="CENTER"><font size="4">第四章</font><font size="4"></font><font size="4">人口普查资料的管理和公布</font></p><p><font size="4">　　</font><font size="4"><b>第二十九条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">地方各级普查机构应当按照普查方案的规定进行数据处理，并按时上报人口普查资料。</font></p><p><font size="4">　</font><font size="4"><b>　第三十条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">人口普查汇总资料，除依法应当保密的外，应当予以公布。</font></p><p><font size="4">　　全国和各省、自治区、直辖市主要人口普查数据，由国家统计局以公报形式公布。</font></p><p><font size="4">　　地方人民政府统计机构公布本行政区域主要人口普查数据，应当报经上一级人民政府统计机构核准。</font></p><p><font size="4">　　</font><font size="4"><b>第三十一条</b></font><font size="4"><b></b></font><font size="4">各级人民政府统计机构应当做好人口普查资料的管理、开发和应用，为社会公众提供查询、咨询等服务。</font></p><p><font size="4">　　</font><font size="4"><b>第三十二条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">人口普查中获得的原始普查资料，按照国家有关规定保存、销毁。</font></p><p><font size="4">　　</font><font size="4"><b>第三十三条</b></font><font size="4"><b></b></font><font size="4"></font><font size="4">人口普查中获得的能够识别或者推断单个普查对象身份的资料，任何单位和个人不得对外提供、泄露，不得作为对人口普查对象作出具体行政行为的依据，不得用于人口普查以外的目的。</font></p><p><font size="4">　　人口普查数据不得作为对地方人民政府进行政绩考核和责任追究的依据。</font></p><p align="CENTER"><font size="4">第五章</font><font size="4"></font><font size="4">法律责任</font></p><p><font size="4">　</font><font size="4"><b>　第三十四条</b></font><font size="4"><b></b></font><font size="4">地方人民政府、政府统计机构或者有关部门、单位的负责人有下列行为之一的，由任免机关或者监察机关依法给予处分，并由县级以上人民政府统计机构予以通报；构成犯罪的，依法追究刑事责任：</font></p><p><font size="4">　　（一）自行修改人口普查资料、编造虚假人口普查数据的；</font></p><p><font size="4">　　（二）要求有关单位和个人伪造、篡改人口普查资料的；</font></p><p><font size="4">　　（三）不按照国家有关规定保存、销毁人口普查资料的；</font></p><p><font size="4">　　（四）违法公布人口普查资料的；</font></p><p><font size="4">　　（五）对依法履行职责或者拒绝、抵制人口普查违法行为的普查人员打击报复的；</font></p><p><font size="4">　　（六）对本地方、本部门、本单位发生的严重人口普查违法行为失察的。</font></p><p><font size="4">　</font><font size="4"><b>　第三十五条</b></font><font size="4"><b></b></font><font size="4">普查机构在组织实施人口普查活动中有下列违法行为之一的，由本级人民政府或者上级人民政府统计机构责令改正，予以通报；对直接负责的主管人员和其他直接责任人员，由任免机关或者监察机关依法给予处分：</font></p><p><font size="4">　　（一）不执行普查方案的；</font></p><p><font size="4">　　（二）伪造、篡改人口普查资料的；</font></p><p><font size="4">　　（三）要求人口普查对象提供不真实的人口普查资料的；</font></p><p><font size="4">　　（四）未按照普查方案的规定报送人口普查资料的；</font></p><p><font size="4">　　（五）违反国家有关规定，造成人口普查资料毁损、灭失的；</font></p><p><font size="4">　　（六）泄露或者向他人提供能够识别或者推断单个普查对象身份的资料的。</font></p><p><font size="4">　　普查人员有前款所列行为之一的，责令其停止执行人口普查任务，予以通报，依法给予处分。</font></p><p><font size="4">　</font><font size="4"><b>　第三十六条</b></font><font size="4"><b></b></font><font size="4"></font><font size="4">人口普查对象拒绝提供人口普查所需的资料，或者提供不真实、不完整的人口普查资料的，由县级以上人民政府统计机构责令改正，予以批评教育。</font></p><p><font size="4">　　人口普查对象阻碍普查机构和普查人员依法开展人口普查工作，构成违反治安管理行为的，由公安机关依法给予处罚。</font></p><p><font size="4">　</font><font size="4"><b>　第三十七条</b></font><font size="4"><b></b></font><font size="4">县级以上人民政府统计机构应当设立举报电话和信箱，接受社会各界对人口普查违法行为的检举和监督。</font></p><p align="CENTER"><font size="4">第六章</font><font size="4"></font><font size="4">附则</font></p><p><font size="4">　　</font><font size="4"><b>第三十八条</b></font><font size="4"><b></b></font><font size="4">中国人民解放军现役军人、人民武装警察等人员的普查内容和方法，由国家统计局会同国务院有关部门、军队有关部门规定。</font></p><p><font size="4">　  交通极为不便地区的人口普查登记的时间和方法，由国家统计局会同国务院有关部门规定。</font></p><p><font size="4">　</font><font size="4"><b>　第三十九条</b></font><font size="4"><b></b></font><font size="4">香港特别行政区、澳门特别行政区的人口数，按照香港特别行政区政府、澳门特别行政区政府公布的资料计算。</font></p><p><font size="4">　　台湾地区的人口数，按照台湾地区有关主管部门公布的资料计算。</font></p><p><font size="4"><b>　　第四十条</b></font><font size="4"><b></b></font><font size="4"></font><font size="4">为及时掌握人口发展变化情况，在两次人口普查之间进行全国</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1%</font></font></span></font><font size="4">人口抽样调查。全国</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1%</font></font></span></font><font size="4">人口抽样调查参照本条例执行。</font></p><p><font size="4">　</font><font size="4"><b>　第四十一条</b></font><font size="4"><b></b></font><font size="4"> </font><font size="4">本条例自</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2010</font></font></span></font><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">6</font></font></span></font><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font><font size="4">日起施行</font></p>', 1488046095, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(123, 45, '全国污染源普查条例', '<p align="CENTER"><a name="_GoBack"></a><font color="#333333"><font size="4"><b>中华人民共和国国务院令</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">第</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">508</font></font></font></span></font><font color="#333333"><font size="4">号</font></font></p><p><font color="#333333"><font size="4">　　现公布《全国污染源普查条例》，自公布之日起施行。</font></font></p><p><font color="#333333"><font size="4">总理</font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">温家宝</font></font><font color="#333333"><font size="4"></font></font></p><p><font color="#333333"><font size="4">二○○七年十月九日</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>全国污染源普查条例</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">第一章</font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">总则</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第一条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">为了科学、有效地组织实施全国污染源普查，保障污染源普查数据的准确性和及时性，根据《中华人民共和国统计法》和《中华人民共和国环境保护法》，制定本条例。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4">污染源普查的任务是，掌握各类污染源的数量、行业和地区分布情况，了解主要污染物的产生、排放和处理情况，建立健全重点污染源档案、污染源信息数据库和环境统计平台，为制定经济社会发展和环境保护政策、规划提供依据。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">本条例所称污染源，是指因生产、生活和其他活动向环境排放污染物或者对环境产生不良影响的场所、设施、装置以及其他污染发生源。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第四条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查按照全国统一领导、部门分工协作、地方分级负责、各方共同参与的原则组织实施。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查所需经费，由中央和地方各级人民政府共同负担，并列入相应年度的财政预算，按时拨付，确保足额到位。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查经费应当统一管理，专款专用，严格控制支出。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第六条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">全国污染源普查每</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">10</font></font></font></span></font><font color="#333333"><font size="4">年进行</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1</font></font></font></span></font><font color="#333333"><font size="4">次，标准时点为普查年份的</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">12</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">31</font></font></font></span></font><font color="#333333"><font size="4">日。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">报刊、广播、电视和互联网等新闻媒体，应当及时开展污染源普查工作的宣传报道。</font></font></p><p><font color="#333333"><font size="4"><b>第二章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>污染源普查的对象、范围、内容和方法</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第八条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查的对象是中华人民共和国境内有污染源的单位和个体经营户。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查对象有义务接受污染源普查领导小组办公室、普查人员依法进行的调查，并如实反映情况，提供有关资料，按照要求填报污染源普查表。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查对象不得迟报、虚报、瞒报和拒报普查数据；不得推诿、拒绝和阻挠调查；不得转移、隐匿、篡改、毁弃原材料消耗记录、生产记录、污染物治理设施运行记录、污染物排放监测记录以及其他与污染物产生和排放有关的原始资料。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查范围包括：工业污染源，农业污染源，生活污染源，集中式污染治理设施和其他产生、排放污染物的设施。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">工业污染源普查的主要内容包括：企业基本登记信息，原材料消耗情况，产品生产情况，产生污染的设施情况，各类污染物产生、治理、排放和综合利用情况，各类污染防治设施建设、运行情况等。</font></font></p><p><font color="#333333"><font size="4">　　农业污染源普查的主要内容包括：农业生产规模，用水、排水情况，化肥、农药、饲料和饲料添加剂以及农用薄膜等农业投入品使用情况，秸秆等种植业剩余物处理情况以及养殖业污染物产生、治理情况等。</font></font></p><p><font color="#333333"><font size="4">　　生活污染源普查的主要内容包括：从事第三产业的单位的基本情况和污染物的产生、排放、治理情况，机动车污染物排放情况，城镇生活能源结构和能源消费量，生活用水量、排水量以及污染物排放情况等。</font></font></p><p><font color="#333333"><font size="4">　　集中式污染治理设施普查的主要内容包括：设施基本情况和运行状况，污染物的处理处置情况，渗滤液、污泥、焚烧残渣和废气的产生、处置以及利用情况等。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">每次污染源普查的具体范围和内容，由国务院批准的普查方案确定。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查采用全面调查的方法，必要时可以采用抽样调查的方法。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查采用全国统一的标准和技术要求。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第三章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>污染源普查的组织实施</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">全国污染源普查领导小组负责领导和协调全国污染源普查工作。</font></font></p><p><font color="#333333"><font size="4">　　全国污染源普查领导小组办公室设在国务院环境保护主管部门，负责全国污染源普查日常工作。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">县级以上地方人民政府污染源普查领导小组，按照全国污染源普查领导小组的统一规定和要求，领导和协调本行政区域的污染源普查工作。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方人民政府污染源普查领导小组办公室设在同级环境保护主管部门，负责本行政区域的污染源普查日常工作。</font></font></p><p><font color="#333333"><font size="4">　　乡（镇）人民政府、街道办事处和村（居）民委员会应当广泛动员和组织社会力量积极参与并认真做好污染源普查工作。</font></font></p><p><font color="#333333"><font size="4">　　第十六条</font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">县级以上人民政府环境保护主管部门和其他有关部门，按照职责分工和污染源普查领导小组的统一要求，做好污染源普查相关工作。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">全国污染源普查方案由全国污染源普查领导小组办公室拟订，经全国污染源普查领导小组审核同意，报国务院批准。</font></font></p><p><font color="#333333"><font size="4">　　全国污染源普查方案应当包括：普查的具体范围和内容、普查的主要污染物、普查方法、普查的组织实施以及经费预算等。</font></font></p><p><font color="#333333"><font size="4">　　拟订全国污染源普查方案，应当充分听取有关部门和专家的意见。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十八条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">全国污染源普查领导小组办公室根据全国污染源普查方案拟订污染源普查表，报国家统计局审定。</font></font></p><p><font color="#333333"><font size="4">　　省、自治区、直辖市人民政府污染源普查领导小组办公室，可以根据需要增设本行政区域污染源普查附表，报全国污染源普查领导小组办公室批准后使用。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">在普查启动阶段，污染源普查领导小组办公室应当进行单位清查。</font></font></p><p><font color="#333333"><font size="4">　　县级以上人民政府机构编制、民政、工商、质检以及其他具有设立审批、登记职能的部门，应当向同级污染源普查领导小组办公室提供其审批或者登记的单位资料，并协助做好单位清查工作。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查领导小组办公室应当以本行政区域现有的基本单位名录库为基础，按照全国污染源普查方案确定的污染源普查的具体范围，结合有关部门提供的单位资料，对污染源逐一核实清查，形成污染源普查单位名录。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">列入污染源普查范围的大、中型工业企业，应当明确相关机构负责本企业污染源普查表的填报工作，其他单位应当指定人员负责本单位污染源普查表的填报工作。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十一条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查领导小组办公室可以根据工作需要，聘用或者从有关单位借调人员从事污染源普查工作。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查领导小组办公室应当与聘用人员依法签订劳动合同，支付劳动报酬，并为其办理社会保险。借调人员的工资由原单位支付，其福利待遇保持不变。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">普查人员应当坚持实事求是，恪守职业道德，具有执行普查任务所需要的专业知识。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查领导小组办公室应当对普查人员进行业务培训，对考核合格的颁发全国统一的普查员工作证。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">普查人员依法独立行使调查、报告、监督和检查的职权，有权查阅普查对象的原材料消耗记录、生产记录、污染物治理设施运行记录、污染物排放监测记录以及其他与污染物产生和排放有关的原始资料，并有权要求普查对象改正其填报的污染源普查表中不真实、不完整的内容。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">普查人员应当严格执行全国污染源普查方案，不得伪造、篡改普查资料，不得强令、授意普查对象提供虚假普查资料。</font></font></p><p><font color="#333333"><font size="4">　　普查人员执行污染源调查任务，不得少于</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2</font></font></font></span></font><font color="#333333"><font size="4">人，并应当出示普查员工作证；未出示普查员工作证的，普查对象可以拒绝接受调查。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">普查人员应当依法直接访问普查对象，指导普查对象填报污染源普查表。污染源普查表填写完成后，应当由普查对象签字或者盖章确认。普查对象应当对其签字或者盖章的普查资料的真实性负责。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查领导小组办公室对其登记、录入的普查资料与普查对象填报的普查资料的一致性负责，并对其加工、整理的普查资料的准确性负责。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查领导小组办公室在登记、录入、加工和整理普查资料过程中，对普查资料有疑义的，应当向普查对象核实，普查对象应当如实说明或者改正。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十六条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">各地方、各部门、各单位的负责人不得擅自修改污染源普查领导小组办公室、普查人员依法取得的污染源普查资料；不得强令或者授意污染源普查领导小组办公室、普查人员伪造或者篡改普查资料；不得对拒绝、抵制伪造或者篡改普查资料的普查人员打击报复。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第四章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>数据处理和质量控制</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查领导小组办公室应当按照全国污染源普查方案和有关标准、技术要求进行数据处理，并按时上报普查数据。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十八条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查领导小组办公室应当做好污染源普查数据备份和数据入库工作，建立健全污染源信息数据库，并加强日常管理和维护更新。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查领导小组办公室应当按照全国污染源普查方案，建立污染源普查数据质量控制岗位责任制，并对普查中的每个环节进行质量控制和检查验收。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查数据不符合全国污染源普查方案或者有关标准、技术要求的，上一级污染源普查领导小组办公室可以要求下一级污染源普查领导小组办公室重新调查，确保普查数据的一致性、真实性和有效性。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">全国污染源普查领导小组办公室统一组织对污染源普查数据的质量核查。核查结果作为评估全国或者各省、自治区、直辖市污染源普查数据质量的重要依据。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查数据的质量达不到规定要求的，有关污染源普查领导小组办公室应当在全国污染源普查领导小组办公室规定的时间内重新进行污染源普查。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第五章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>数据发布、资料管理和开发应用</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十一条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">全国污染源普查公报，根据全国污染源普查领导小组的决定发布。</font></font></p><p><font color="#333333"><font size="4">　　地方污染源普查公报，经上一级污染源普查领导小组办公室核准发布。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">普查对象提供的资料和污染源普查领导小组办公室加工、整理的资料属于国家秘密的，应当注明秘密的等级，并按照国家有关保密规定处理。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查领导小组办公室、普查人员对在污染源普查中知悉的普查对象的商业秘密，负有保密义务。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十三条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查领导小组办公室应当建立污染源普查资料档案管理制度。污染源普查资料档案的保管、调用和移交应当遵守国家有关档案管理规定。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">国家建立污染源普查资料信息共享制度。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查领导小组办公室应当在污染源信息数据库的基础上，建立污染源普查资料信息共享平台，促进普查成果的开发和应用。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查取得的单个普查对象的资料严格限定用于污染源普查目的，不得作为考核普查对象是否完成污染物总量削减计划的依据，不得作为依照其他法律、行政法规对普查对象实施行政处罚和征收排污费的依据。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第六章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>表彰和处罚</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十六条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">对在污染源普查工作中做出突出贡献的集体和个人，应当给予表彰和奖励。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">地方、部门、单位的负责人有下列行为之一的，依法给予处分，并由县级以上人民政府统计机构予以通报批评；构成犯罪的，依法追究刑事责任：</font></font></p><p><font color="#333333"><font size="4">　　（一）擅自修改污染源普查资料的；</font></font></p><p><font color="#333333"><font size="4">　　（二）强令、授意污染源普查领导小组办公室、普查人员伪造或者篡改普查资料的；</font></font></p><p><font color="#333333"><font size="4">　　（三）对拒绝、抵制伪造或者篡改普查资料的普查人员打击报复的。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十八条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">普查人员不执行普查方案，或者伪造、篡改普查资料，或者强令、授意普查对象提供虚假普查资料的，依法给予处分。</font></font></p><p><font color="#333333"><font size="4">　　污染源普查领导小组办公室、普查人员泄露在普查中知悉的普查对象商业秘密的，对直接负责的主管人员和其他直接责任人员依法给予处分；对普查对象造成损害的，应当依法承担民事责任。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查对象有下列行为之一的，污染源普查领导小组办公室应当及时向同级人民政府统计机构通报有关情况，提出处理意见，由县级以上人民政府统计机构责令改正，予以通报批评；情节严重的，可以建议对直接负责的主管人员和其他直接责任人员依法给予处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）迟报、虚报、瞒报或者拒报污染源普查数据的；</font></font></p><p><font color="#333333"><font size="4">　　（二）推诿、拒绝或者阻挠普查人员依法进行调查的；</font></font></p><p><font color="#333333"><font size="4">　　（三）转移、隐匿、篡改、毁弃原材料消耗记录、生产记录、污染物治理设施运行记录、污染物排放监测记录以及其他与污染物产生和排放有关的原始资料的。</font></font></p><p><font color="#333333"><font size="4">　　单位有本条第一款所列行为之一的，由县级以上人民政府统计机构予以警告，可以处</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">5</font></font></font></span></font><font color="#333333"><font size="4">万元以下的罚款。</font></font></p><p><font color="#333333"><font size="4">　　个体经营户有本条第一款所列行为之一的，由县级以上人民政府统计机构予以警告，可以处</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1</font></font></font></span></font><font color="#333333"><font size="4">万元以下的罚款。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第四十条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">污染源普查领导小组办公室应当设立举报电话和信箱，接受社会各界对污染源普查工作的监督和对违法行为的检举，并对检举有功的人员依法给予奖励，对检举的违法行为，依法予以查处。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第七章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>附则</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第四十一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">军队、武装警察部队的污染源普查工作，由中国人民解放军总后勤部按照国家统一规定和要求组织实施。</font></font></p><p><font color="#333333"><font size="4">　　新疆生产建设兵团的污染源普查工作，由新疆生产建设兵团按照国家统一规定和要求组织实施。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第四十二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">本条例自公布之日起施行。</font></font></p>', 1488046125, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(124, 45, '部门统计调查项目管理暂行办法', '<p align="CENTER"><a name="_GoBack"></a><font color="#333333"><font size="4">（</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1999</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">10</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">27</font></font></span></font></font><font color="#333333"><font size="4">日国家统计局令第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">4</font></font></span></font></font><font color="#333333"><font size="4">号公布）</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第一章　总则</font></font></p><p><font color="#333333"><font size="4"><b>　　第一条　</b></font></font><font color="#333333"><font size="4">为加强对部门统计调查的管理和监督，规范部门统计调查行为，提高统计调查的整体效率，减轻被调查者负担，保障统计资料的准确性，提高共享性，根据《中华人民共和国统计法》及其实施细则，制定本办法。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二条　</b></font></font><font color="#333333"><font size="4">本办法适用于国家机关、具有行政管理职能的事业单位、经授权代主管部门行使统计职能的国家级集团公司和工商领域联合会或协会、经国务院授权具有一定行政职能的人民团体开展的统计调查，以及上述部门和单位与其他部门联合组织实施的统计调查。法院、检察院组织实施的统计调查，参照本办法执行。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三条　</b></font></font><font color="#333333"><font size="4">本办法所称的统计调查，是指部门搜集国民经济、社会和科技发展情况，用于政府管理目的的各类统计调查。包括以数字形式、文字形式或混合形式；以表格、问卷、电讯（电报、电话、传真等）、磁盘磁带、网络通讯（网络表格、电子邮件等）等为介质的普查、经常性调查、一次性调查、试点调查等。</font></font></p><p><font color="#333333"><font size="4">　　第二章　部门统计调查项目的管理</font></font></p><p><font color="#333333"><font size="4"><b>　　第四条　</b></font></font><font color="#333333"><font size="4">政府综合统计机构统一管理和协调部门统计调查。国家统计局管理和协调国家一级部门制定的统计调查；县及县以上地方各级政府统计局管理和协调同级部门的统计调查。</font></font></p><p><font color="#333333"><font size="4"><b>　　第五条　</b></font></font><font color="#333333"><font size="4">部门的综合统计机构统一组织、管理和协调本部门各职能机构的统计调查活动，制定本部门的统计调查总体方案。部门内其它职能机构无权单独制定统计调查项目。</font></font></p><p><font color="#333333"><font size="4"><b>　　第六条　</b></font></font><font color="#333333"><font size="4">政府综合统计机构通过建立审批备案制度、有效期制度、调查项目公布制度、跟踪检查制度、举报制度，对部门统计调查进行管理。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第三章　制定统计调查项目的基本原则与要求</font></font></p><p><font color="#333333"><font size="4"><b>　　第七条　</b></font></font><font color="#333333"><font size="4">国家机关、具有行政管理职能的事业单位、经授权代主管部门行使统计职能的国家级集团公司和工商领域联合会或协会、经国务院授权具有一定行政职能的人民团体，可以制定与职能范围相对应的统计调查项目。</font></font></p><p><font color="#333333"><font size="4">　　国务院临时机构，一般不得直接制定统计调查项目。工作需要的统计资料，应当向有关部门搜集、加工。确有需要调查的，须事先取得国家统计局的同意，方可制定统计调查。</font></font></p><p><font color="#333333"><font size="4">　　法院、检察院可制定业务情况统计调查项目。</font></font></p><p><font color="#333333"><font size="4"><b>　　第八条　</b></font></font><font color="#333333"><font size="4">统计调查项目的立项必须有充分的理由。调查要有明确的目的和资料使用范围。</font></font></p><p><font color="#333333"><font size="4"><b>　　第九条　</b></font></font><font color="#333333"><font size="4">统计调查的内容和调查范围必须与部门的职能相一致，必须符合既定的政府综合统计与部门统计的分工原则。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十条　</b></font></font><font color="#333333"><font size="4">统计调查项目必须兼顾需要与可能，充分考虑基层调查人员和被调查对象的承受能力。必须符合精简、效能的原则。凡一次性调查能满足需要的，不搞定期调查；凡非全面调查能满足需要的，不搞全面调查。最大限度地减少调查频率，缩小调查规模，降低调查成本。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十一条　</b></font></font><font color="#333333"><font size="4">调查项目中的报表表式和文字说明必须规范；指标解释和计算方法必须科学；调查内容要简明扼要，不能与其他调查重复、交叉、矛盾。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十二条　</b></font></font><font color="#333333"><font size="4">调查项目中的统计标准和分类必须与政府综合统计部门规定使用的标准和分类相一致。涉及政府综合统计部门规定以外的专业标准和分类，要与有关国家标准或行业标准相一致。尚无国家标准和行业标准的，必须严格按照标准化科学及分类科学的原理进行归纳和设计，并在使用前征求政府综合统计部门的意见。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十三条　</b></font></font><font color="#333333"><font size="4">所使用的调查方法要科学合理。要结合调查目的和要求选择最适当的调查方法，以获得最大的调查效益。避免由于调查方法使用不当给基层造成过重负担和产生数据质量问题。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十四条　</b></font></font><font color="#333333"><font size="4">重大调查项目必须经过研究论证和试点，必须有完备的论证材料和试点材料。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十五条　</b></font></font><font color="#333333"><font size="4">调查者必须依法使用调查资料，对属于国家秘密的统计资料，必须保密；对属于私人、家庭的单项调查资料和在统计调查中知悉的调查对象的商业秘密，负有保密义务。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第四章　审批及备案程序</font></font></p><p><font color="#333333"><font size="4"><b>　　第十六条　</b></font></font><font color="#333333"><font size="4">部门建立以系统内单位为对象的调查项目，须报同级政府综合统计机构备案；部门建立调查范围涉及到系统外单位的统计调查项目，必须报同级政府综合统计机构审批，在取得政府综合统计机构的同意或批准后方可组织实施。</font></font></p><p><font color="#333333"><font size="4">　　系统内是指：与部门有直接隶属关系的单位及部门的派出机构；省及省以下与部门对口设立的管理机构；国家级集团公司所属企业。除此之外均属系统外。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十七条　</b></font></font><font color="#333333"><font size="4">审批及备案程序的有关时间规定：</font></font></p><p><font color="#333333"><font size="4">　　（一）政府综合统计机构在收到部门正式申请函及完整的相关资料后，在</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">20</font></font></span></font></font><font color="#333333"><font size="4">个工作日内完成审批；在</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">5</font></font></span></font></font><font color="#333333"><font size="4">个工作日内完成备案。完成时间以复函日期为准。</font></font></p><p><font color="#333333"><font size="4">　　（二）部门收到复函后，在</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">20</font></font></span></font></font><font color="#333333"><font size="4">个工作日内将布置调查的正式文件、调查方案和调查表式送达政府综合统计机构，以便及时在“部门统计调查项目库”中建立或更新记录，以及履行公文存档手续。</font></font></p><p><font color="#333333"><font size="4">　　（三）对有关自然灾害、突发事件等不可预知、有特殊时效性要求的调查，政府综合统计机构将根据特事特办的原则，在最短的时间内完成审批工作，备案项目可事后补办。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十八条　</b></font></font><font color="#333333"><font size="4">部门统计调查项目送审及备案时，须备齐以下文件：</font></font></p><p><font color="#333333"><font size="4">　　（一）以部门名义发出的申请审批或备案的函。</font></font></p><p><font color="#333333"><font size="4">　　（二）调查方案和表式。包括：总说明、报表目录、基层表式、综合表式、统计标准和分类目录、指标解释、逻辑关系及抽样方案（针对抽样调查）等。应明确表述调查目的、调查对象、统计范围、调查方法、调查频率、填报要求、报送渠道、时间要求等。</font></font></p><p><font color="#333333"><font size="4">　　（三）相关文件。包括新建立该调查项目的背景材料、重大调查项目的研究论证材料及试点报告等。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十九条　</b></font></font><font color="#333333"><font size="4">制定统计调查的部门，在将调查方案送审的同时，要认真填写《部门统计调查项目审批</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">/</font></font></span></font></font><font color="#333333"><font size="4">备案登记表》。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十条　</b></font></font><font color="#333333"><font size="4">政府综合统计机构对送审的部门统计调查进行初审，提出修改意见和建议，部门应积极配合，及时作出说明和解释，并按照修改意见认真进行修改；如有不同意见，双方应进一步研究磋商达成一致。否则由国家统计局进行最终裁决，部门应按最终裁决意见进行修改。</font></font></p><p><font color="#333333"><font size="4">　　政府综合统计机构对送备案的部门统计调查提出修改意见和完善建议。</font></font></p><p><font color="#333333"><font size="4">　　第二十一 条 政府综合统计机构对部门统计调查的具体审核工作完成之后，以统计局名义发函批复。批复分为：同意实施；不同意实施；建议暂缓实施三种。部门收到批复后，应严格按照批复执行。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十二条　</b></font></font><font color="#333333"><font size="4">部门收到同意实施的复文后，要及时印制调查方案和调查表式，起草布置实施的部门文件，部署调查工作。如遇特殊情况不能实施调查、延期实施调查或需调整变更调查方案的，须及时向政府综合统计机构报告说明。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十三条　</b></font></font><font color="#333333"><font size="4">调查范围涉及到省及省以下单位的部门调查，在将调查任务逐级布置时，应及时通知同级政府综合统计机构。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十四条　</b></font></font><font color="#333333"><font size="4">对部门内职能机构为监控生产经营活动的具体环节而建立的内容专一、分类至细、频率固定的业务统计项目，在其内容不与其它统计调查项目重复的前提下，可向政府综合统计机构提出申请，由政府综合统计机构研究同意后，授权部门的综合统计机构进行定期审批管理。部门的综合统计机构审批后，须将调查方案送政府综合统计机构，以便纳入“部门统计调查项目库”。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第五章　调查的法定标识和有效期</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十五条　</b></font></font><font color="#333333"><font size="4">部门统计调查经政府综合统计机构批准或备案后，必须在报表的右上角标明法定标识。法定标识包括：</font></font></p><p><font color="#333333"><font size="4">　　（一）表号；</font></font></p><p><font color="#333333"><font size="4">　　（二）制表机关；</font></font></p><p><font color="#333333"><font size="4">　　（三）批准机关</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">/</font></font></span></font></font><font color="#333333"><font size="4">备案机关；</font></font></p><p><font color="#333333"><font size="4">　　（四）批准文号</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">/</font></font></span></font></font><font color="#333333"><font size="4">备案文号；</font></font></p><p><font color="#333333"><font size="4">　　（五）有效期截止时间。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十六条　</b></font></font><font color="#333333"><font size="4">政府综合统计机构对部门统计调查实行有效期管理制度。批准的年度调查及其调查周期小于一年的定期调查的有效期为两年；普查、一次性调查、调查周期大于一年的定期调查，其有效期到该次调查的资料上报结束时止。备案的定期调查的有效期为三年；一次性调查的有效期到该次调查的资料上报结束时止。有效期皆以复函的日期为起点计算。</font></font></p><p><font color="#333333"><font size="4">　　超过有效期的调查项目，一律自动废止。如需要继续执行，应当重新办理审批或备案手续。在有效期内发生变化的调查项目，应随时办理重新审批或备案手续。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第六章　监督与处罚</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十七条　</b></font></font><font color="#333333"><font size="4">政府综合统计机构为了保护合法的部门统计调查项目顺利实施，采取以下监督措施：</font></font></p><p><font color="#333333"><font size="4">　　（一）定期通过公共媒体向社会公布“部门统计调查项目目录”，以便建立全社会监督机制。“部门统计调查项目目录”的内容包括：</font></font></p><p><font color="#333333"><font size="4">　　</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">1</font></font></span></font></font><font color="#333333"><font size="4">、经过批准或备案的部门统计调查项目名称；</font></font></p><p><font color="#333333"><font size="4">　　</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2</font></font></span></font></font><font color="#333333"><font size="4">、制定及组织实施该项调查的单位名称；</font></font></p><p><font color="#333333"><font size="4">　　</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">3</font></font></span></font></font><font color="#333333"><font size="4">、批准文号或备案文号及其日期；</font></font></p><p><font color="#333333"><font size="4">　　</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">4</font></font></span></font></font><font color="#333333"><font size="4">、调查项目的有效期。</font></font></p><p><font color="#333333"><font size="4">　　同时公布经查实的违规调查项目和因超过有效期而被废止的调查项目。</font></font></p><p><font color="#333333"><font size="4">　　（二）建立对违规调查的举报核实制度。在政府综合统计机构设立举报接待部门，根据举报线索，对违规调查的情况进行调查核实后予以公布。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十八条　</b></font></font><font color="#333333"><font size="4">为保护国家利益和被调查者的权益，减轻被调查者和各级数据加工部门的负担，政府综合统计机构对部门调查的资料使用情况、实施方案与批准方案的一致性进行监督，监督内容包括：</font></font></p><p><font color="#333333"><font size="4">　　（一）检查调查取得的资料是否被正当使用。资料使用与调查目的是否一致；资料使用是否超出原定的范围；资料是否被私自用于营利目的；是否违反有关保密的规定；是否有其他损害国家利益和被调查者权益的行为。</font></font></p><p><font color="#333333"><font size="4">　　（二）检查调查资料的有用性。对大部分内容使用频率不高、针对性不强的调查，建议修改、合并或停止实施。</font></font></p><p><font color="#333333"><font size="4">　　（三）检查调查实施过程中是否严格按政府统计机构批准的方案执行，是否有擅自变更调查内容、调查范围、计算方法和报送频率等行为。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十九条　</b></font></font><font color="#333333"><font size="4">对违反本办法第十六条、第二十二条、第二十六条的规定，未履行法定的审批或者备案程序和擅自变更调查方案的部门统计调查，县级以上政府综合统计机构可以依法予以废止，并按照《统计法》及其实施细则的有关规定处理。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第七章　管理部门提供的服务</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十条　</b></font></font><font color="#333333"><font size="4">为主动作好部门统计调查的管理工作，避免可能出现的问题，政府综合统计机构为制定统计调查的部门提供有关业务性咨询、调查方案设计指导。帮助部门掌握设计统计调查项目的基本方法，提高设计工作的效率和质量。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十一条　</b></font></font><font color="#333333"><font size="4">为了避免调查项目之间的重复、矛盾，政府综合统计机构建立并向部门开放“部门统计调查项目库”。部门在制定调查项目前可先进行查询，以达到避免重复调查，充分利用已有信息的目的。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十二条　</b></font></font><font color="#333333"><font size="4">对于部门利用已有资料，精简调查项目和调查内容的，政府综合统计机构给予充分支持和协助。对部门之间在利用对方资料中遇到障碍的，政府综合统计机构可出面协调。</font></font></p><p><font color="#333333"><font size="4">　　第八章　附则</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十三条　</b></font></font><font color="#333333"><font size="4">对于政府综合统计机构与部门联合制定的统计调查项目，以政府综合统计机构为主制定的（使用政府统计机构的文号），参照政府综合统计机构调查审批程序办理；以部门为主制定的（使用部门的文号），参照部门调查审批程序办理，最后以会签文件作为实施依据。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十四条　</b></font></font><font color="#333333"><font size="4">本办法由国家统计局负责解释。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十五条　</b></font></font><font color="#333333"><font size="4">本办法自公布之日起施行。</font></font></p>', 1488046203, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(125, 45, '涉外调查管理办法', '<p align="CENTER"><font color="#333333"><font size="4">（</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2004</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">10</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">13</font></font></span></font></font><font color="#333333"><font size="4">日国家统计局令第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">7</font></font></span></font></font><font color="#333333"><font size="4">号公布）</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第一章 总则</b></font></font></p><p><font color="#333333"><font size="4">　　第一条 为了加强对涉外调查的规范和管理，维护国家安全和社会公共利益，保障调查机构和调查对象的合法权益，根据《中华人民共和国统计法》及其实施细则，制定本办法。</font></font></p><p><font color="#333333"><font size="4">　　第二条 本办法所称涉外调查，包括：</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4">（</font></font><font color="#333333"><font size="4">一</font></font><font color="#333333"><font size="4">）</font></font><font color="#333333"><font size="4">受境外组织、个人或者境外组织在华机构委托、资助进行的市场调查和社会调查；</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4">（</font></font><font color="#333333"><font size="4">二</font></font><font color="#333333"><font size="4">）</font></font><font color="#333333"><font size="4">与境外组织、个人或者境外组织在华机构合作进行的市场调查和社会调查；</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4">（</font></font><font color="#333333"><font size="4">三</font></font><font color="#333333"><font size="4">）</font></font><font color="#333333"><font size="4">境外组织在华机构依法进行的市场调查；</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4">（</font></font><font color="#333333"><font size="4">四</font></font><font color="#333333"><font size="4">）</font></font><font color="#333333"><font size="4">将调查资料、调查结果提供给境外组织、个人或者境外组织在华机构的市场调查和社会调查。</font></font></p><p><font color="#333333"><font size="4">　　第三条 本办法所称市场调查，是指收集整理有关商品和商业服务在市场中的表现和前景信息的活动。</font></font></p><p><font color="#333333"><font size="4">　　本办法所称社会调查，是指市场调查之外，以问卷、访谈、观察或者其他方式，收集、整理和分析有关社会信息的活动。</font></font></p><p><font color="#333333"><font size="4">　　本办法所称境外，是指中华人民共和国关境外；境内，是指中华人民共和国关境内。</font></font></p><p><font color="#333333"><font size="4">　　本办法所称境外组织在华机构，是指经我国政府批准，境外组织在境内设立的分支机构和常驻代表机构。</font></font></p><p><font color="#333333"><font size="4">　　本办法所称涉外调查机构，是指依法取得涉外调查许可证的机构。</font></font></p><p><font color="#333333"><font size="4">　　第四条 国家统计局会同国务院有关部门负责对全国的涉外调查实施监督管理。县级以上地方各级人民政府统计机构会同同级人民政府有关部门负责对本行政区域内的涉外调查实施监督管理。</font></font></p><p><font color="#333333"><font size="4">　　第五条 国家统计局和省级人民政府统计机构及其工作人员对在涉外调查管理中知悉的商业秘密，负有保密义务。</font></font></p><p><font color="#333333"><font size="4">　　第六条 从事涉外调查，必须遵守我国法律、法规、规章和国家有关规定。</font></font></p><p><font color="#333333"><font size="4">　　第七条 任何组织、个人不得进行可能导致下列后果的涉外调查：</font></font></p><p><font color="#333333"><font size="4">　　（一）违背宪法确定的基本原则的；</font></font></p><p><font color="#333333"><font size="4">　　（二）危害国家统一、主权和领土完整的；</font></font></p><p><font color="#333333"><font size="4">　　（三）窃取、刺探、收买、泄露国家秘密或者情报，危害国家安全、损害国家利益的；</font></font></p><p><font color="#333333"><font size="4">　　（四）违反国家宗教政策，破坏民族团结的；</font></font></p><p><font color="#333333"><font size="4">　　（五）扰乱社会经济秩序，破坏社会稳定，损害社会公共利益的；</font></font></p><p><font color="#333333"><font size="4">　　（六）宣传邪教、迷信的；</font></font></p><p><font color="#333333"><font size="4">　　（七）进行欺诈活动，侵害他人合法权益的；</font></font></p><p><font color="#333333"><font size="4">　　（八）法律、法规、规章和国家有关规定认定的其他情形。</font></font></p><p><font color="#333333"><font size="4">　　第八条 国家实行涉外调查机构资格认定制度和涉外社会调查项目审批制度。</font></font></p><p><font color="#333333"><font size="4">　　第九条 涉外市场调查必须通过涉外调查机构进行，涉外社会调查必须通过涉外调查机构报经批准后进行。</font></font></p><p><font color="#333333"><font size="4">　　境外组织和个人不得在境内直接进行市场调查和社会调查，不得通过未取得涉外调查许可证的机构进行市场调查和社会调查。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第二章 涉外调查机构资格认定和管理</b></font></font></p><p><font color="#333333"><font size="4">　　第十条 国家统计局和省、自治区、直辖市人民政府统计机构负责对申请涉外调查许可证的机构进行资格认定。</font></font></p><p><font color="#333333"><font size="4">　　任何个人和未取得涉外调查许可证的组织，不得以任何形式进行涉外调查。</font></font></p><p><font color="#333333"><font size="4">　　第十一条 申请涉外调查许可证的机构，应当具备下列条件：</font></font></p><p><font color="#333333"><font size="4">　　（一）依法成立，具有法人资格；</font></font></p><p><font color="#333333"><font size="4">　　（二）经营范围或业务范围包含市场调查或者社会调查内容；</font></font></p><p><font color="#333333"><font size="4">　　（三）具有熟悉国家有关涉外调查管理规定的人员；</font></font></p><p><font color="#333333"><font size="4">　　（四）具备与所从事涉外调查相适应的调查能力；</font></font></p><p><font color="#333333"><font size="4">　　（五）在申请之日前一年内开展三项以上调查项目，或者调查营业额达到三十万元；</font></font></p><p><font color="#333333"><font size="4">　　（六）有严格、健全的资料保密制度；</font></font></p><p><font color="#333333"><font size="4">　　（七）在最近两年内无重大违法记录。</font></font></p><p><font color="#333333"><font size="4">　　第十二条 业务范围中含有市场调查内容的境外组织在华机构，具备第十一条第（三）、（六）、（七）项条件的，可以申请涉外调查许可证，在境内直接进行与本机构有关的商品或者商业服务的市场调查；但是，不得从事社会调查。</font></font></p><p><font color="#333333"><font size="4">　　第十三条 申请涉外调查许可证，应当提交下列文件：</font></font></p><p><font color="#333333"><font size="4">　　（一）涉外调查许可证申请表；</font></font></p><p><font color="#333333"><font size="4">　　（二）用以证明第十一条或者第十二条所列内容的其他材料。</font></font></p><p><font color="#333333"><font size="4">　　第十四条 申请涉外调查许可证的机构，调查范围跨省、自治区、直辖市行政区域的，向国家统计局提出；调查范围限于省、自治区、直辖市行政区域内的，向所在省、自治区、直辖市人民政府统计机构提出。</font></font></p><p><font color="#333333"><font size="4">　　国家统计局或者省、自治区、直辖市人民政府统计机构应当自受理之日起二十日内，作出批准或者不批准的决定。逾期不能作出决定的，经本行政机关负责人批准，可以延长十日，并将延长期限的理由告知申请人。决定批准的，颁发涉外调查许可证；决定不批准的，应当书面通知申请人，并说明理由。</font></font><font color="#333333"><font size="4"> </font></font></p><p><font color="#333333"><font size="4">　　第十五条 国家统计局颁发的涉外调查许可证，在全国范围内有效。省、自治区、直辖市人民政府统计机构颁发的涉外调查许可证，在本行政区域内有效。</font></font></p><p><font color="#333333"><font size="4">　　第十六条 涉外调查许可证应当注明调查机构的名称、登记类型、法定代表人或者主要负责人、住所和颁发机关、颁发日期、编号、许可范围、有效期等项内容。</font></font></p><p><font color="#333333"><font size="4">　　第十七条 涉外调查机构的名称、登记类型、法定代表人或者主要负责人、住所等发生变更的，应当向原颁发机关申请变更涉外调查许可证。</font></font></p><p><font color="#333333"><font size="4">　　第十八条 涉外调查许可证的有效期为三年。</font></font></p><p><font color="#333333"><font size="4">　　涉外调查机构需要延续涉外调查许可证有效期的，应当在有效期届满三十日前向原颁发机关提出申请。逾期未提出的，将不再延续涉外调查许可证的有效期。</font></font></p><p><font color="#333333"><font size="4">　　第十九条 终止涉外调查业务的，应当在终止业务后三十日内，向原颁发机关缴回涉外调查许可证。</font></font></p><p><font color="#333333"><font size="4">　　涉外调查许可证有效期届满的，应当在届满后三十日内，向原颁发机关缴回已过期的涉外调查许可证。</font></font></p><p><font color="#333333"><font size="4">　　第二十条 任何组织、个人不得伪造、冒用或者转让涉外调查许可证。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第三章 涉外调查项目管理</b></font></font></p><p><font color="#333333"><font size="4">　　第二十一条 国家统计局和省、自治区、直辖市人民政府统计机构负责对涉外社会调查项目的审批。</font></font></p><p><font color="#333333"><font size="4">　　第二十二条 涉外调查机构申请批准涉外社会调查项目时，应提交下列文件：</font></font></p><p><font color="#333333"><font size="4">　　（一）涉外社会调查项目申请表；</font></font></p><p><font color="#333333"><font size="4">　　（二）涉外调查许可证复印件；</font></font></p><p><font color="#333333"><font size="4">　　（三）委托、资助、合作的合同复印件；</font></font></p><p><font color="#333333"><font size="4">　　（四）调查方案，包括调查的目的、内容、范围、时间、对象、方式等；</font></font></p><p><font color="#333333"><font size="4">　　（五）调查问卷、表格或者访谈、观察提纲；</font></font></p><p><font color="#333333"><font size="4">　　（六）与调查项目有关的其他背景材料。</font></font></p><p><font color="#333333"><font size="4">　　第二十三条 涉外调查机构申请批准涉外社会调查项目，调查范围跨省、自治区、直辖市行政区域的，向国家统计局提出；调查范围限于省、自治区、直辖市行政区域内的，向所在省、自治区、直辖市人民政府统计机构提出。</font></font></p><p><font color="#333333"><font size="4">　　国家统计局或者省、自治区、直辖市人民政府统计机构应当自受理之日起二十日内，作出批准或者不批准的决定。逾期不能作出决定的，经本行政机关负责人批准，可以延长十日，并将延长期限的理由告知申请人。决定批准的，发给涉外社会调查项目批准文件；决定不批准的，应当书面通知申请人，并说明理由。</font></font></p><p><font color="#333333"><font size="4">　　第二十四条 经批准的涉外社会调查项目，不得擅自变更；需要变更的，涉外调查机构应当就变更部分向原批准机关提出申请。</font></font></p><p><font color="#333333"><font size="4">　　审批机关应当依据第二十三条第二款的规定作出批准或者不批准变更的决定。</font></font></p><p><font color="#333333"><font size="4">　　第二十五条 涉外调查应当遵循自愿的原则，调查对象有权自主决定是否接受调查，任何组织和个人不得强迫调查对象接受调查。</font></font></p><p><font color="#333333"><font size="4">　　涉外调查机构进行涉外调查时，应当向调查对象说明调查目的，不得冒用其他机构的名义，不得进行误导。</font></font></p><p><font color="#333333"><font size="4">　　第二十六条 经批准进行的涉外社会调查，应当在调查问卷、表格或者访谈、观察提纲首页显著位置标明并向调查对象说明下列事项：</font></font></p><p><font color="#333333"><font size="4">　　涉外调查许可证编号；</font></font></p><p><font color="#333333"><font size="4">　　调查项目的批准机关、批准文号；</font></font></p><p><font color="#333333"><font size="4">　　本调查为调查对象自愿接受的调查。</font></font></p><p><font color="#333333"><font size="4">　　第二十七条 涉外调查机构应当建立涉外调查业务档案。</font></font></p><p><font color="#333333"><font size="4">　　第二十八条 任何组织、个人不得伪造、冒用或者转让涉外社会调查项目批准文件。</font></font></p><p><font color="#333333"><font size="4">　　第二十九条 涉外调查机构和有关人员对在涉外调查中知悉的商业秘密和个人隐私，负有保密义务。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第四章 法律责任</b></font></font></p><p><font color="#333333"><font size="4">　　第三十条 违反本办法第七条规定的，依照《中华人民共和国统计法实施细则》第三十四条的规定予以处罚。</font></font></p><p><font color="#333333"><font size="4">　　第三十一条 违反本办法规定，有下列情形之一的，由国家统计局或者省级人民政府统计机构责令改正。其调查活动属于非经营性的，可处以五百元至一千元的罚款；其调查活动属于经营性，有违法所得的，可处以相当于违法所得一至三倍但是不超过三万元的罚款；没有违法所得的，可处以三千元至一万元的罚款。构成犯罪的，依法追究刑事责任：</font></font></p><p><font color="#333333"><font size="4">　　（一）未通过取得涉外调查许可证的机构进行涉外调查的；</font></font></p><p><font color="#333333"><font size="4">　　（二）未取得涉外调查许可证进行涉外调查的；</font></font></p><p><font color="#333333"><font size="4">　　（三）伪造、冒用、转让涉外调查许可证、涉外社会调查项目批准文件的；</font></font></p><p><font color="#333333"><font size="4">　　（四）使用已超过有效期的涉外调查许可证从事涉外调查的；</font></font></p><p><font color="#333333"><font size="4">　　（五）超出许可范围从事涉外调查的。</font></font></p><p><font color="#333333"><font size="4">　　第三十二条 涉外调查机构和有关人员违反本办法规定，有下列情形之一的，由国家统计局或者省级人民政府统计机构责令改正。其调查活动属于非经营性的，可处以五百元至一千元的罚款。其调查活动属于经营性，有违法所得的，可处以相当于违法所得一至三倍但是不超过三万元的罚款；没有违法所得的，可处以三千元至一万元的罚款。构成犯罪的，依法追究刑事责任：</font></font></p><p><font color="#333333"><font size="4">　　（一）未经批准，擅自进行涉外社会调查的；</font></font></p><p><font color="#333333"><font size="4">　　（二）未经批准，擅自变更已批准的涉外社会调查项目的；</font></font></p><p><font color="#333333"><font size="4">　　（三）泄露调查对象商业秘密和个人隐私的；</font></font></p><p><font color="#333333"><font size="4">　　（四）强迫调查对象接受调查的；</font></font></p><p><font color="#333333"><font size="4">　　（五）冒用其他机构名义进行涉外调查的；</font></font></p><p><font color="#333333"><font size="4">　　（六）未建立涉外调查业务档案的；</font></font></p><p><font color="#333333"><font size="4">　　（七）拒绝接受管理机关检查的；</font></font></p><p><font color="#333333"><font size="4">　　（八）在接受管理机关检查时，拒绝提供情况和有关材料、提供虚假情况和材料的；</font></font></p><p><font color="#333333"><font size="4">　　（九）未标明、未向调查对象说明第二十六条规定事项的。</font></font></p><p><font color="#333333"><font size="4">　　第三十三条 涉外调查机构违反本办法规定，有下列情形之一的，由国家统计局或者省级人民政府统计机构责令改正，给予警告，可处以五百元至一千元的罚款：</font></font></p><p><font color="#333333"><font size="4">　　（一）涉外调查机构的名称、登记类型、法定代表人或者主要负责人、住所等发生变更，未依法申请变更涉外调查许可证的；</font></font></p><p><font color="#333333"><font size="4">　　（二）终止涉外调查业务，或者涉外调查许可证有效期届满后，未向原颁发机关缴回涉外调查许可证的。</font></font></p><p><font color="#333333"><font size="4">　　第三十四条 统计机构工作人员在涉外调查管理中玩忽职守、滥用职权的，依法给予行政处分；构成犯罪的，依法追究刑事责任。</font></font></p><p><font color="#333333"><font size="4">　　第三十五条 国家统计局和省级人民政府统计机构工作人员泄露在涉外调查管理中知悉的商业秘密，依法承担民事责任，并对负有直接责任的主管人员和其他直接责任人员依法给予行政处分。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第五章 附则</b></font></font></p><p><font color="#333333"><font size="4">　　第三十六条 我国政府与外国政府及国际组织之间的合作项目中涉及的调查，依据国家有关规定执行。</font></font></p><p><font color="#333333"><font size="4">　　第三十七条 本办法规定的实施行政许可的期限以工作日计算，不含法定节假日。</font></font></p><p><font color="#333333"><font size="4">　　第三十八条 本办法自公布之日起施行。</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1999</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">7</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">16</font></font></font></span></font><font color="#333333"><font size="4">日国家统计局公布的《涉外社会调查活动管理暂行办法》同时废止</font></font></p>', 1488046248, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(126, 45, '统计调查证管理办法', '<p align="CENTER"><font color="#333333"><font size="4"><b>中华人民共和国国家统计局令</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">第</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">11</font></font></font></span></font><font color="#333333"><font size="4">号</font></font></p><p><font color="#333333"><font size="4">　　《统计调查证管理办法》已经</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2007</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">7</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">23</font></font></font></span></font><font color="#333333"><font size="4">日国家统计局第</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">7</font></font></font></span></font><font color="#333333"><font size="4">次局务会议通过，现予公布，自</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2007</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">12</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1</font></font></font></span></font><font color="#333333"><font size="4">日起实施。</font></font></p><p><font color="#333333"><font size="4">局长 谢伏瞻    </font></font></p><p><font color="#333333"><font size="4">二〇〇七年八月二十七日</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>统计调查证管理办法</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">为保障政府统计调查工作顺利进行，规范统计调查证的颁发和管理工作，根据《中华人民共和国统计法》，制定本办法。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计调查证是统计调查人员依法执行政府统计调查任务时证明其身份的有效证件。</font></font></p><p><font color="#333333"><font size="4">　　持证人员依法进行政府统计调查活动时，应当主动向统计调查对象出示证件。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计调查证由国家统计局统一格式，省级人民政府统计机构、国家统计局派出的调查总队印制，县级以上地方各级人民政府统计机构、国家统计局派出的调查队颁发。</font></font></p><p><font color="#333333"><font size="4">　　省级人民政府统计机构、国家统计局派出调查总队依照本办法建立统计调查证核发和管理制度。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计调查证可以颁发给下列人员：</font></font></p><p><font color="#333333"><font size="4">　　（一）县级以上地方各级人民政府统计机构聘用的调查人员；</font></font></p><p><font color="#333333"><font size="4">　　（二）国家统计局派出的调查队聘用的调查人员；</font></font></p><p><font color="#333333"><font size="4">　　（三）县级以上地方各级人民政府统计机构、国家统计局派出的调查队的工作人员中，直接执行政府统计调查任务的调查人员。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方各级人民政府统计机构、国家统计局及其派出的调查队的工作人员也可以持工作证执行政府统计调查任务。</font></font></p><p><font color="#333333"><font size="4">　　各项全国性普查的普查员和普查指导员，持普查员证或者普查指导员证依法执行普查任务。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">取得统计调查证的人员应当具备相关的统计知识和调查技能。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第六条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">取得统计调查证，应当由本人填写登记表，经聘用单位或者本人所在单位审查，报省级人民政府统计机构或者国家统计局派出的调查总队核准，由聘用单位或者本人所在单位颁发。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计调查证应当标明下列内容：</font></font></p><p><font color="#333333"><font size="4">　　（一）持证人姓名、性别、年龄；</font></font></p><p><font color="#333333"><font size="4">　　（二）持证人照片；</font></font></p><p><font color="#333333"><font size="4">　　（三）持证人所在单位或者聘用单位名称；</font></font></p><p><font color="#333333"><font size="4">　　（四）发证机关、证件编号；</font></font></p><p><font color="#333333"><font size="4">　　（五）发证日期、有效期限。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第八条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">持证人员的职责是：</font></font></p><p><font color="#333333"><font size="4">　　（一）宣传、执行统计法律、法规、规章和统计调查制度；</font></font></p><p><font color="#333333"><font size="4">　　（二）依法开展统计调查，准确、及时完成统计调查任务；</font></font></p><p><font color="#333333"><font size="4">　　（三）要求有关统计调查对象依法准确、及时提供统计资料；</font></font></p><p><font color="#333333"><font size="4">　　（四）审核统计资料的准确性，依法要求改正不真实的统计资料。</font></font></p><p><font color="#333333"><font size="4">　　持证人员对在政府统计调查中知悉的统计调查资料负有保密义务。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">发证机关应当加强对持证人员的管理。</font></font></p><p><font color="#333333"><font size="4">　　持证人员不再从事政府统计调查活动或者统计调查证有效期届满的，由发证机关收回统计调查证。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">持证人员应当妥善保管统计调查证，不得涂改、转借、故意毁损或者用作与政府统计调查无关的活动</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">持证人员有下列情况之一的，由县级以上地方各级人民政府统计机构或者国家统计局派出的调查队予以批评教育，并可以收缴统计调查证。情节较重，属于国家工作人员的，依法给予处分；不属于国家工作人员的，解除聘用合同，并依照有关法律规定处理：</font></font></p><p><font color="#333333"><font size="4">　　（一）将统计调查证转借他人使用的；</font></font></p><p><font color="#333333"><font size="4">　　（二）利用统计调查证从事与政府统计调查无关的活动的；</font></font></p><p><font color="#333333"><font size="4">　　（三）泄露调查对象统计调查资料的。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">任何单位违反本办法规定，伪造、变造或者冒用统计调查证的，由县级以上地方各级人民政府统计机构或者国家统计局派出的调查队责令改正，予以警告。对非经营活动中发生前款违法行为的，还可处以</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1000</font></font></font></span></font><font color="#333333"><font size="4">元以下的罚款。对经营活动中发生前款违法行为，有违法所得的，还可处以违法所得一至三倍但不超过</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">30000</font></font></font></span></font><font color="#333333"><font size="4">元的罚款；没有违法所得的，还可处以</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">10000</font></font></font></span></font><font color="#333333"><font size="4">元以下的罚款。</font></font></p><p><font color="#333333"><font size="4">　　对有前款违法行为的有关责任人员，由县级以上地方各级人民政府统计机构或者国家统计局派出的调查队予以警告，还可处以</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1000</font></font></font></span></font><font color="#333333"><font size="4">元以下的罚款，或者提请公安机关依照《中华人民共和国治安管理处罚法》处理。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">对县级以上地方各级人民政府统计机构、国家统计局派出的调查队聘用的执行一次性统计调查任务的调查人员，可以颁发临时统计调查证。</font></font></p><p><font color="#333333"><font size="4">　　临时统计调查证的颁发、管理由省级人民政府统计机构、国家统计局派出的调查总队规定。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">本办法自</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2007</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">12</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1</font></font></font></span></font><font color="#333333"><font size="4">日起实施。国家统计局</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1999</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">4</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">14</font></font></font></span></font><font color="#333333"><font size="4">日公布的《国家统计调查证管理办法》同时废止。</font></font></p>', 1488046286, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(127, 45, '统计从业资格认定办法', '<p align="CENTER"><font color="#333333"><font size="4"><b>中华人民共和国国家统计局令</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">第</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">10</font></font></font></span></font><font color="#333333"><font size="4">号</font></font></p><p><font color="#333333"><font size="4">　　《统计从业资格认定办法》已经</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2007</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">4</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">28</font></font></font></span></font><font color="#333333"><font size="4">日国家统计局第</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">6</font></font></font></span></font><font color="#333333"><font size="4">次局务会议修改通过，现予公布，自</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2007</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">6</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1</font></font></font></span></font><font color="#333333"><font size="4">日起实施。</font></font></p><p><br></p><p><font color="#333333"><font size="4">　　                                       局长</font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">谢伏瞻</font></font><font color="#333333"><font size="4"></font></font></p><p><font color="#333333"><font size="4">　　                                   二</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">OO</font></font></font></span></font><font color="#333333"><font size="4">七年四月三十日</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>统计从业资格认定办法</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">（</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2005</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">5</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">16</font></font></font></span></font><font color="#333333"><font size="4">日国家统计局发布 </font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2007</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">4</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">28</font></font></font></span></font><font color="#333333"><font size="4">日国家统计局修订）</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第一章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>总则</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第一条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4">为规范统计从业资格认定工作，提高统计人员的素质，保障统计资料的准确性和及时性，根据《中华人民共和国统计法》、《中华人民共和国行政许可法》和《国务院对确需保留的行政审批项目设定行政许可的决定》，制定本办法。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4">在国家机关、社会团体、企业事业单位和其他组织等统计调查对象中承担经常性政府统计调查任务的人员，必须取得统计从业资格，持有统计从业资格证书。</font></font></p><p><font color="#333333"><font size="4">　　已取得统计员以上统计专业技术职务资格的人员，可免于统计从业资格考试和申请，凭统计专业技术职务资格证书直接从事统计工作。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4">国家统计局领导和管理全国的统计从业资格认定工作。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第四条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4">省级人民政府统计机构是本行政区域内统计从业资格认定工作的实施机关。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第五条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4">县级人民政府统计机构是本行政区域内统计从业资格认定工作的承办机关。</font></font></p><p><font color="#333333"><font size="4">　　必要时，省级人民政府统计机构可以决定由设区的市人民政府统计机构承办统计从业资格认定的有关工作。</font></font></p><p><font color="#333333"><font size="4">　　新疆生产建设兵团统计局负责所属单位的统计从业资格认定工作。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第二章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>申请与受理</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第六条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">具备下列条件的人员，可申请取得统计从业资格：</font></font></p><p><font color="#333333"><font size="4">　　（一）熟悉统计法律、法规和规章；</font></font></p><p><font color="#333333"><font size="4">　　（二）坚持原则，具备良好的道德品质；</font></font></p><p><font color="#333333"><font size="4">　　（三）具备从事统计工作所需的专业知识和技能。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">国家实行统计从业资格考试制度。</font></font></p><p><font color="#333333"><font size="4">　　统计从业资格考试的时间为每年九月份的第三个星期日。</font></font></p><p><font color="#333333"><font size="4">　　统计从业资格考试的科目为：统计基础知识与统计实务；统计法基础知识。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第八条</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4">已具备教育行政部门认可的会计与统计核算、统计实务专业大专，统计学类、经济学类、工商管理类专业本科以上学历（或学位）的人员，可免于参加统计基础知识与统计实务科目的考试。</font></font></p><p><font color="#333333"><font size="4">　　统计学类、经济学类、工商管理类专业以国务院教育主管部门公布的《高等学校本科专业目录（统计用）》为准。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">国家统计局负责编制统计从业资格考试大纲、考试命题、制定考试管理办法和考务规则等工作。</font></font></p><p><font color="#333333"><font size="4">　　省级人民政府统计机构负责统计从业资格考试考点的设定、试卷的印制、组织阅卷和成绩登记造册等工作。</font></font></p><p><font color="#333333"><font size="4">　　统计从业资格认定工作承办机关负责统计从业资格考试的报名、考务组织和成绩通知等工作。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计从业资格考试应当公开举行。县级以上人民政府统计机构应当事先公布考试的报名条件、报考办法、考试科目以及考试大纲。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">申请取得统计从业资格的人员，在向统计从业资格认定工作承办机关提出申请时，应当提交下列材料：</font></font></p><p><font color="#333333"><font size="4">　　（一）《统计从业资格认定申请表》一式两份；</font></font></p><p><font color="#333333"><font size="4">　　（二）本人有效身份证件及其两份复印件；</font></font></p><p><font color="#333333"><font size="4">　　（三）统计从业资格考试合格成绩单原件及其两份复印件；</font></font></p><p><font color="#333333"><font size="4">　　（四）本人近期正面免冠彩色照片一张。</font></font></p><p><font color="#333333"><font size="4">　　符合本办法第八条规定的人员，在提出统计从业资格认定申请时，除提交前款所规定的材料外，还需同时提交本人学历证书原件及其两份复印件。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">具备条件的地方，可通过网络受理统计从业资格认定申请，所需材料由省级人民政府统计机构规定。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计从业资格认定工作的承办机关应当将有关统计从业资格认定的依据、条件、程序、期限以及需要提交的全部材料的目录和申请书示范文本等在办公场所公示。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">申请人申请统计从业资格，应当如实向受理申请的统计从业资格认定工作承办机关提交有关材料。受理机关不得要求申请人提交与其申请的统计从业资格认定事项无关的材料。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计从业资格认定工作的承办机关对申请人提出的申请，应当根据下列情况分别作出处理：</font></font></p><p><font color="#333333"><font size="4">　　（一）申请人依法不需要取得统计从业资格的，应当即时告知申请人不受理；</font></font></p><p><font color="#333333"><font size="4">　　（二）申请材料存在可以当场更正的错误的，应当允许申请人当场更正；</font></font></p><p><font color="#333333"><font size="4">　　（三）申请材料不齐全或者不符合法定形式的，应当当场或者在五日内一次告知申请人需要补正的全部内容，逾期不告知的，自收到申请材料之日起即为受理；</font></font></p><p><font color="#333333"><font size="4">　　（四）申请材料齐全、符合法定形式，或者申请人按照承办机关的要求提交全部补正申请材料的，应当受理统计从业资格认定申请。</font></font></p><p><font color="#333333"><font size="4">　　统计从业资格认定工作的承办机关受理或者不予受理统计从业资格认定申请，应当向申请人出具加盖本行政机关专用印章并注明日期的书面凭证。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第三章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>审查与决定</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十六条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计从业资格认定工作的承办机关应当对已受理的申请材料进行审查，并将初步审查意见和全部申请材料自受理之日起二十日内报送省级人民政府统计机构。</font></font></p><p><font color="#333333"><font size="4">　　省级人民政府统计机构应当自收到初步审查意见和全部申请材料之日起二十日内作出是否授予统计从业资格的决定。二十日内不能作出决定的，经本行政机关负责人批准，可以延长十日，并将延长期限的理由告知申请人。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">申请人的申请符合法定条件的，省级人民政府统计机构应当依法做出授予统计从业资格的书面决定，并颁发统计从业资格证书。统计从业资格证书应当加盖省级人民政府统计机构印章。</font></font></p><p><font color="#333333"><font size="4">　　申请人的申请不符合法定条件，省级人民政府统计机构依法作出不授予统计从业资格的书面决定的，应当说明理由，并告知申请人享有依法申请行政复议或者提起行政诉讼的权利。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第四章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>证书的使用与管理</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十八条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计从业资格证书在全国范围内有效。</font></font></p><p><font color="#333333"><font size="4">　　统计从业资格证书应当依法使用，不得涂改、转让、出租和出借。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计从业资格证书由国家统计局统一设计样式，统一制定编号规则。</font></font></p><p><font color="#333333"><font size="4">　　省级人民政府统计机构负责统计从业资格证书的印制、编号、颁发和管理工作。</font></font></p><p><font color="#333333"><font size="4">　　统计从业资格认定工作的承办机关负责本行政区域内统计从业资格证书的送达工作。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计从业资格证书遗失或损坏的，取得统计从业资格的人员可持有效证明，向原承办机关提出补发统计从业资格证书的申请。原承办机关进行审查后，报原发证机关依法予以补发。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">对取得统计从业资格的人员，实行统计继续教育。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">有下列情形之一的，国家统计局和省级人民政府统计机构可以依法撤销已经授予的统计从业资格：</font></font></p><p><font color="#333333"><font size="4">　　（一）滥用职权、玩忽职守作出授予统计从业资格决定的；</font></font></p><p><font color="#333333"><font size="4">　　（二）超越法定职权作出授予统计从业资格决定的；</font></font></p><p><font color="#333333"><font size="4">　　（三）违反法定程序作出授予统计从业资格决定的；</font></font></p><p><font color="#333333"><font size="4">　　（四）对不具备申请资格或者不符合法定条件的申请人授予统计从业资格的；</font></font></p><p><font color="#333333"><font size="4">　　（五）以欺骗、贿赂等不正当手段取得统计从业资格的；</font></font></p><p><font color="#333333"><font size="4">　　（六）依法可以撤销统计从业资格的其他情形。</font></font></p><p><font color="#333333"><font size="4">　　因前款所列情形被依法撤销统计从业资格的人员，其已取得的统计从业资格证书应当依法予以收回。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">申请人因第二十二条第一款第（五）项原因被撤销统计从业资格的，自撤销之日起两年内，省级人民政府统计机构不得授予统计从业资格。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">上级人民政府统计机构应当加强对下级人民政府统计机构实施统计从业资格认定工作的监督检查，及时纠正和处理统计从业资格认定工作中的各种违法行为。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第五章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>法律责任</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">任何单位违反本办法第二条的规定，聘请、任用未取得统计从业资格证书的人员从事统计工作的，由县级以上人民政府统计机构责令限期改正，予以警告或者通报批评。拒不改正的，处一千元以下的罚款。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十六条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">县级以上地方各级人民政府统计机构违反本办法的规定，有下列情形之一的，由其上级人民政府统计机构责令改正；情节较重的，对直接负责的主管人员和其他直接责任人员依法给予行政处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）对符合法定条件的统计从业资格申请不予受理的；</font></font></p><p><font color="#333333"><font size="4">　　（二）对不符合法定条件的申请人授予统计从业资格或者超越法定职权作出授予统计从业资格决定的；</font></font></p><p><font color="#333333"><font size="4">　　（三）对符合法定条件的申请人不授予统计从业资格或者不在法定期限内作出授予统计从业资格决定的；</font></font></p><p><font color="#333333"><font size="4">　　（四）法律、法规、规章规定的其他违法行为。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">申请人隐瞒有关情况或者提供虚假材料申请统计从业资格的，县级以上地方各级人民政府统计机构不予受理或者不授予统计从业资格，并给予批评教育。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十八条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">已取得统计从业资格的人员有下列行为之一的，由县级以上人民政府统计机构责令改正，予以警告或者通报批评：</font></font></p><p><font color="#333333"><font size="4">　　（一）涂改、转让、出租、出借统计从业资格证书的；</font></font></p><p><font color="#333333"><font size="4">　　（二）向负责监督检查的县级以上人民政府统计机构隐瞒有关情况、提供虚假材料或者拒绝提供情况的；</font></font></p><p><font color="#333333"><font size="4">　　（三）以欺骗、贿赂等不正当手段取得统计从业资格证书的；</font></font></p><p><font color="#333333"><font size="4">　　（四）法律、法规、规章规定的其他违法行为。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第六章</b></font></font><font color="#333333"><font size="4"><b></b></font></font><font color="#333333"><font size="4"><b>附则</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二十九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">在本办法实施前已依法取得《统计证》、《统计上岗证》或《统计上岗资格证书》的人员，应当自本办法实施之日起一年内，到所在地统计从业资格认定工作承办机关换领统计从业资格证书。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">本办法规定的实施行政许可的期限以工作日计算，不含法定节假日。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三十一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">本办法自</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2005</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">7</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1</font></font></font></span></font><font color="#333333"><font size="4">日起施行。国家统计局</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1998</font></font></font></span></font><font color="#333333"><font size="4">年发布的《统计人员持证上岗暂行规定》同时废止。</font></font></p>', 1488046320, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(128, 45, '统计违法违纪行为处罚规定', '<p align="CENTER"><a name="_GoBack"></a><font color="#333333"><font size="4"><b>中 华 人  民 共 和  国  监 察 部</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>中华人民共和国人力资源和社会保障部 令</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>国　　　家　　　统　　　计　　　局</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">第</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">18</font></font></font></span></font><font color="#333333"><font size="4">号</font></font></p><p><font color="#333333"><font size="4">　　《统计违法违纪行为处分规定》已经监察部</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2009</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">9</font></font></font></span></font><font color="#333333"><font size="4">日第一次部长办公会议、人力资源社会保障部</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2008</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">12</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">30</font></font></font></span></font><font color="#333333"><font size="4">日第十六次部务会议、国家统计局</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2008</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">11</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">6</font></font></font></span></font><font color="#333333"><font size="4">日第十八次局务会议审议通过。现予公布，自</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2009</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">5</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1</font></font></font></span></font><font color="#333333"><font size="4">日起施行。</font></font></p><p><font color="#333333"><font size="4">监  察  部 部 长  </font></font><font color="#333333"><font size="4">马 馼 </font></font></p><p><font color="#333333"><font size="4">　　                      人力资源社会保障部部长  </font></font><font color="#333333"><font size="4">尹蔚民</font></font></p><p><font color="#333333"><font size="4">　　                       国 家 统 计 局 局 长  马建堂</font></font></p><p><font color="#333333"><font size="4">　                             二○○九年三月二十五日    </font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>统计违法违纪行为处分规定</b></font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">为了加强统计工作，提高统计数据的准确性和及时性，惩处和预防统计违法违纪行为，促进统计法律法规的贯彻实施，根据《中华人民共和国统计法》、《中华人民共和国行政监察法》、《中华人民共和国公务员法》、《行政机关公务员处分条例》及其他有关法律、行政法规，制定本规定。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">有统计违法违纪行为的单位中负有责任的领导人员和直接责任人员，以及有统计违法违纪行为的个人，应当承担纪律责任。属于下列人员的（以下统称有关责任人员），由任免机关或者监察机关按照管理权限依法给予处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）行政机关公务员；</font></font></p><p><font color="#333333"><font size="4">　　（二）法律、法规授权的具有公共事务管理职能的事业单位中经批准参照《中华人民共和国公务员法》管理的工作人员；</font></font></p><p><font color="#333333"><font size="4">　　（三）行政机关依法委托的组织中除工勤人员以外的工作人员；</font></font></p><p><font color="#333333"><font size="4">　　（四）企业、事业单位、社会团体中由行政机关任命的人员。</font></font></p><p><font color="#333333"><font size="4">　　法律、行政法规、国务院决定和国务院监察机关、国务院人力资源社会保障部门制定的处分规章对统计违法违纪行为的处分另有规定的，从其规定。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">地方、部门以及企业、事业单位、社会团体的领导人员有下列行为之一的，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）自行修改统计资料、编造虚假数据的；</font></font></p><p><font color="#333333"><font size="4">　　（二）强令、授意本地区、本部门、本单位统计机构、统计人员或者其他有关机构、人员拒报、虚报、瞒报或者篡改统计资料、编造虚假数据的；</font></font></p><p><font color="#333333"><font size="4">　　（三）对拒绝、抵制篡改统计资料或者对拒绝、抵制编造虚假数据的人员进行打击报复的；</font></font></p><p><font color="#333333"><font size="4">　　（四）对揭发、检举统计违法违纪行为的人员进行打击报复的。</font></font></p><p><font color="#333333"><font size="4">　　有前款第（三）项、第（四）项规定行为的，应当从重处分。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">地方、部门以及企业、事业单位、社会团体的领导人员，对本地区、本部门、本单位严重失实的统计数据，应当发现而未发现或者发现后不予纠正，造成不良后果的，给予警告或者记过处分；造成严重后果的，给予记大过或者降级处分；造成特别严重后果的，给予撤职或者开除处分。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">各级人民政府统计机构、有关部门及其工作人员在实施统计调查活动中，有下列行为之一的，对有关责任人员，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）强令、授意统计调查对象虚报、瞒报或者伪造、篡改统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（二）参与篡改统计资料、编造虚假数据的。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第六条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">各级人民政府统计机构、有关部门及其工作人员在实施统计调查活动中，有下列行为之一的，对有关责任人员，给予警告、记过或者记大过处分；情节较重的，给予降级处分；情节严重的，给予撤职处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）故意拖延或者拒报统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（二）明知统计数据不实，不履行职责调查核实，造成不良后果的。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第七条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">统计调查对象中的单位有下列行为之一，情节较重的，对有关责任人员，给予警告、记过或者记大过处分；情节严重的，给予降级或者撤职处分；情节特别严重的，给予开除处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）虚报、瞒报统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（二）伪造、篡改统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（三）拒报或者屡次迟报统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（四）拒绝提供情况、提供虚假情况或者转移、隐匿、毁弃原始统计记录、统计台账、统计报表以及与统计有关的其他资料的。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第八条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">违反国家规定的权限和程序公布统计资料，造成不良后果的，对有关责任人员，给予警告或者记过处分；情节较重的，给予记大过或者降级处分；情节严重的，给予撤职处分。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第九条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">有下列行为之一，造成不良后果的，对有关责任人员，给予警告、记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：</font></font></p><p><font color="#333333"><font size="4">　　（一）泄露属于国家秘密的统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（二）未经本人同意，泄露统计调查对象个人、家庭资料的；</font></font></p><p><font color="#333333"><font size="4">　　（三）泄露统计调查中知悉的统计调查对象商业秘密的。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">包庇、纵容统计违法违纪行为的，对有关责任人员，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十一条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">受到处分的人员对处分决定不服的，依照《中华人民共和国行政监察法》、《中华人民共和国公务员法》、《行政机关公务员处分条例》等有关规定，可以申请复核或者申诉。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十二条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">任免机关、监察机关和人民政府统计机构建立案件移送制度。</font></font></p><p><font color="#333333"><font size="4">　　任免机关、监察机关查处统计违法违纪案件，认为应当由人民政府统计机构给予行政处罚的，应当将有关案件材料移送人民政府统计机构。人民政府统计机构应当依法及时查处，并将处理结果书面告知任免机关、监察机关。</font></font></p><p><font color="#333333"><font size="4">　　人民政府统计机构查处统计行政违法案件，认为应当由任免机关或者监察机关给予处分的，应当及时将有关案件材料移送任免机关或者监察机关。任免机关或者监察机关应当依法及时查处，并将处理结果书面告知人民政府统计机构。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十三条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">有统计违法违纪行为，应当给予党纪处分的，移送党的纪律检查机关处理。涉嫌犯罪的，移送司法机关依法追究刑事责任。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十四条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">本规定由监察部、人力资源社会保障部、国家统计局负责解释。</font></font></p><p><font color="#333333"><font size="4">　　</font></font><font color="#333333"><font size="4"><b>第十五条</b></font></font><font color="#333333"><font size="4"></font></font><font color="#333333"><font size="4">本规定自</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">2009</font></font></font></span></font><font color="#333333"><font size="4">年</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">5</font></font></font></span></font><font color="#333333"><font size="4">月</font></font><font face="Calibri, serif"><span lang="en-US"><font color="#333333"><font face="serif"><font size="4">1</font></font></font></span></font><font color="#333333"><font size="4">日起施行。</font></font></p>', 1488046347, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(129, 45, '统计执法检查规定', '<p align="CENTER"><font color="#333333"><font size="4"><b>中华人民共和国国家统计局令</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">9</font></font></span></font></font><font color="#333333"><font size="4">号</font></font></p><p><font color="#333333"><font size="4">　　《统计执法检查规定》已经</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2006</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">7</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">12</font></font></span></font></font><font color="#333333"><font size="4">日国家统计局第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">8</font></font></span></font></font><font color="#333333"><font size="4">次局务会议修改通过，现予公布，自公布之日起实施。</font></font></p><p><font color="#333333"> </font></p><p><font color="#333333"><font size="4">局长　邱晓华  </font></font></p><p><font color="#333333"><font size="4">二〇〇六年七月十七日</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>统计执法检查规定</b></font></font></p><p align="CENTER"><font color="#333333"><font size="4">（</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2001</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">6</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">20</font></font></span></font></font><font color="#333333"><font size="4">日国家统计局制定</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2006</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">7</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">12</font></font></span></font></font><font color="#333333"><font size="4">日国家统计局修订）</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第一章　总　则</font></font></p><p><font color="#333333"><font size="4"><b>　　第一条　</b></font></font><font color="#333333"><font size="4">为了科学有效地组织统计执法检查工作，保障统计法和统计制度的贯彻实施，维护和提高统计数据质量，根据《中华人民共和国统计法》及其实施细则，制定本规定。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二条　</b></font></font><font color="#333333"><font size="4">县级以上各级人民政府统计机构、国家统计局派出的各级调查队是统计执法检查机关，负责监督检查统计法和统计制度的实施，依法查处违反统计法和统计制度的行为。</font></font></p><p><font color="#333333"><font size="4">　　县级以上地方各级人民政府统计机构、国家统计局派出的各级调查队应当分工协作、加强沟通，避免重复检查。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府各有关部门在同级人民政府统计机构的组织指导下，负责监督检查本部门管辖系统内统计法和统计制度的贯彻实施，协助同级人民政府统计机构查处本部门管辖系统内的统计违法行为。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四条　</b></font></font><font color="#333333"><font size="4">各级统计执法检查机关应当建立行政执法责任制，切实保障统计执法检查所需的人员、经费和其他工作条件。</font></font></p><p><font color="#333333"><font size="4"><b>　　第五条　</b></font></font><font color="#333333"><font size="4">统计执法检查应当贯彻有法必依、执法必严、违法必究的方针，坚持预防、查处和整改相结合，坚持处罚与教育相结合，合法、公正、公开、高效地进行。</font></font></p><p><font color="#333333"><font size="4"><b>　　第六条　</b></font></font><font color="#333333"><font size="4">各级统计执法检查机关鼓励对统计法贯彻实施情况的社会监督。国家统计局设立举报中心，受理社会各界对统计违法行为的举报。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第二章　统计执法检查机构和统计执法检查员</font></font></p><p><font color="#333333"><font size="4"><b>　　第七条　</b></font></font><font color="#333333"><font size="4">国家统计局法制工作机构负责统一组织、管理全国的统计执法检查工作。</font></font></p><p><font color="#333333"><font size="4">　　省及地市级统计执法检查机关应当设置专门的统计执法检查机构，配备专职统计执法检查员。</font></font></p><p><font color="#333333"><font size="4">　　县级统计执法检查机关可以根据工作需要，设置专门的统计执法检查机构。未设机构的，应当配备必要的统计执法检查员。</font></font></p><p><font color="#333333"><font size="4">　　县级以上人民政府各有关部门可以根据工作需要，配备统计执法检查员。</font></font></p><p><font color="#333333"><font size="4"><b>　　第八条　</b></font></font><font color="#333333"><font size="4">统计执法检查机构的主要职责是：</font></font></p><p><font color="#333333"><font size="4">　　（一）宣传、贯彻统计法；</font></font></p><p><font color="#333333"><font size="4">　　（二）组织、指导、监督、管理统计执法检查工作；</font></font></p><p><font color="#333333"><font size="4">　　（三）受理统计违法举报，查办、转办、督办统计违法案件；</font></font></p><p><font color="#333333"><font size="4">　　（四）办理统计行政复议和行政应诉事项；</font></font></p><p><font color="#333333"><font size="4">　　（五）法律、法规和规章赋予的其他职责。</font></font></p><p><font color="#333333"><font size="4"><b>　　第九条　</b></font></font><font color="#333333"><font size="4">统计执法检查员应当具备下列条件：</font></font></p><p><font color="#333333"><font size="4">　　（一）坚持原则、作风正派、忠于职守、遵纪守法</font></font></p><p><font color="#333333"><font size="4">　　（二）具有大专以上学历；</font></font></p><p><font color="#333333"><font size="4">　　（三）具备相关法律知识，熟悉统计业务；</font></font></p><p><font color="#333333"><font size="4">　　（四）参加统计执法检查员资格培训，经考试合格，取得统计执法检查证。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十条　</b></font></font><font color="#333333"><font size="4">统计执法检查员的资格培训及考核由国家统计局统一规划、组织和管理，省级统计执法检查机关负责实施。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十一条　</b></font></font><font color="#333333"><font size="4">各级统计执法检查机关应当加强对所属统计执法检查员的职业道德教育和业务技能培训，健全管理、考核和奖惩制度</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第三章　统计执法检查的一般规定</font></font></p><p><font color="#333333"><font size="4"><b>　　第十二条　</b></font></font><font color="#333333"><font size="4">各级统计执法检查机关和有关部门应当建立统计执法检查制度，综合运用全面检查、专项检查、重点检查等方式，进行经常性的统计执法检查工作。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十三条　</b></font></font><font color="#333333"><font size="4">统计执法检查事项包括：</font></font></p><p><font color="#333333"><font size="4">　　（一）是否存在侵犯统计机构和统计人员独立行使统计调查、统计报告、统计监督职权的行为；</font></font></p><p><font color="#333333"><font size="4">　　（二）是否存在违反法定程序和统计制度修改统计数据的行为；</font></font></p><p><font color="#333333"><font size="4">　　（三）是否存在虚报、瞒报、伪造、篡改、拒报和迟报统计资料的行为；</font></font></p><p><font color="#333333"><font size="4">　　（四）是否依法设立统计机构或配备统计人员；</font></font></p><p><font color="#333333"><font size="4">　　（五）是否设置原始记录、统计台帐；</font></font></p><p><font color="#333333"><font size="4">　　（六）统计人员是否具备统计从业资格；</font></font></p><p><font color="#333333"><font size="4">　　（七）统计调查项目是否依据法定程序报批，是否在统计调查表的右上角标明法定标识；</font></font></p><p><font color="#333333"><font size="4">　　（八）是否严格按照经批准的统计调查方案进行调查，有无随意改变调查内容、调查对象和调查时间等问题；</font></font></p><p><font color="#333333"><font size="4">　　（九）统计资料的管理和公布是否符合有关规定，有无泄露国家秘密、统计调查对象的商业秘密和私人、家庭的单项调查资料的行为；</font></font></p><p><font color="#333333"><font size="4">　　（十）是否依法进行涉外调查；</font></font></p><p><font color="#333333"><font size="4">　　（十一）法律、法规和规章规定的其他事项。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十四条　</b></font></font><font color="#333333"><font size="4">统计执法检查机关在组织实施统计执法检查前应当先拟定检查计划。检查计划包括检查的依据、时间、对象、内容和组织形式等。</font></font></p><p><font color="#333333"><font size="4">　　对未发现统计违法嫌疑的单位，同一统计执法检查机关每年对其实施统计执法检查不得超过一次。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十五条　</b></font></font><font color="#333333"><font size="4">实施统计执法检查，应当提前通知被检查对象，告知统计执法检查机关的名称，检查的依据、范围、内容、方式和时间，对被检查对象的具体要求等。</font></font></p><p><font color="#333333"><font size="4">　　对有统计违法嫌疑的单位实施检查，检查通知可于统计执法检查机关认为适当的时间下达。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十六条　</b></font></font><font color="#333333"><font size="4">检查人员进行统计执法检查时，应当先向被检查对象出示统计执法检查证或法律、法规、规章规定的其他执法证件。未出示合法执法证件的，有关单位和个人有权拒绝接受检查。</font></font></p><p><font color="#333333"><font size="4">　　统计执法检查证是实施统计执法检查的有效证件，由国家统计局统一印制，国家统计局和省级统计执法检查机关负责核发。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十七条　</b></font></font><font color="#333333"><font size="4">统计执法检查机关和检查人员具有下列职权：</font></font></p><p><font color="#333333"><font size="4">　　（一）依法发出统计检查查询书，向被检查对象查询有关事项；</font></font></p><p><font color="#333333"><font size="4">　　（二）要求被检查对象提供与检查事项有关的原始记录和凭证、统计台账、统计调查表、会计资料以及其他相关证明、资料，进入被检查对象运用电子计算机管理的数据录入、处理系统检查有关资料；</font></font></p><p><font color="#333333"><font size="4">　　（三）进入被检查对象的业务场所及货物存放地进行实地检查、核对；</font></font></p><p><font color="#333333"><font size="4">　　（四）经统计执法检查机关负责人批准，登记保存被检查对象的原始记录和凭证、统计台账、统计调查表、会计资料及其他相关证明和资料；</font></font></p><p><font color="#333333"><font size="4">　　（五）就与统计执法检查有关的事项，询问统计人员、单位负责人和有关人员；</font></font></p><p><font color="#333333"><font size="4">　　（六）对与统计违法案件有关的情况和资料，进行记录和复制；</font></font></p><p><font color="#333333"><font size="4">　　（七）要求被检查对象将有关资料送至指定地点接受检查。</font></font></p><p><font color="#333333"><font size="4"><b>　 第十八条　</b></font></font><font color="#333333"><font size="4">统计执法检查机关和检查人员对在检查过程中知悉的被检查对象的商业秘密和私人、家庭的单项调查资料，负有保密义务。</font></font></p><p><font color="#333333"><font size="4"><b>　　第十九条　</b></font></font><font color="#333333"><font size="4">被检查对象和有关人员不得拒绝提供情况或提供虚假情况，不得使用暴力或者威胁的方法阻挠、抗拒检查，对统计检查查询书应当按期据实答复。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十条　</b></font></font><font color="#333333"><font size="4">检查人员应当及时向统计执法检查机关提交检查报告，对检查中发现的问题提出处理意见或建议。</font></font></p><p><font color="#333333"><font size="4">　　统计执法检查机关对发现的统计违法行为应当分别以下情况予以处理：</font></font></p><p><font color="#333333"><font size="4">　　（一）统计违法行为轻微的，责令被检查对象改正，或者提出统计执法检查意见；</font></font></p><p><font color="#333333"><font size="4">　　（二）统计违法行为需要立案查处的，依照法定程序办理。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第四章　统计违法案件的查处</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十一条　</b></font></font><font color="#333333"><font size="4">统计违法案件由各级统计执法检查机关负责查处。</font></font></p><p><font color="#333333"><font size="4">　　各级统计执法检查机关可以委托依法成立的统计执法队（室、所）等组织查处统计违法案件。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十二条　</b></font></font><font color="#333333"><font size="4">各级统计执法检查机关及其直属事业单位工作人员的统计违法行为，由该机关或监察机关依干部管理权限负责处理。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十三条　</b></font></font><font color="#333333"><font size="4">县级以上人民政府各有关部门对在统计执法检查中发现的统计违法行为，认为应当给予行政处罚的，应当及时移交给同级人民政府统计机构处理。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十四条　</b></font></font><font color="#333333"><font size="4">查处统计违法案件应当做到事实清楚，证据确凿，定性准确，处理恰当，适用法律正确，符合法定程序。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十五条　</b></font></font><font color="#333333"><font size="4">查处统计违法案件的一般程序为：立案、调查、处理、结案。</font></font></p><p><font color="#333333"><font size="4">　　对在统计执法检查中发现并已调查清楚的统计违法行为，需要立案查处的，应当补充立案。 </font></font></p><p><font color="#333333"><font size="4"><b>　　第二十六条　</b></font></font><font color="#333333"><font size="4">符合《中华人民共和国行政处罚法》第三十三条规定，统计违法事实确凿并有法定依据，应对公民处以五十元以下、对法人或者其他组织处以一千元以下罚款或者警告的行政处罚的，可以适用简易处罚程序，当场作出统计行政处罚决定。 </font></font></p><p><font color="#333333"><font size="4"><b>　　第二十七条　</b></font></font><font color="#333333"><font size="4">对下列统计违法行为，统计执法检查机关应当依法进行查处： </font></font></p><p><font color="#333333"><font size="4">　　（一）地方、部门、单位的领导人自行修改统计资料、编造虚假数据或者强令、授意统计机构、统计人员篡改统计资料、编造虚假数据的；</font></font></p><p><font color="#333333"><font size="4">　　（二）地方、部门、单位的领导人对统计人员进行打击报复的；</font></font></p><p><font color="#333333"><font size="4">　　（三）统计机构、统计人员参与篡改统计资料、编造虚假数据的；</font></font></p><p><font color="#333333"><font size="4">　　（四）虚报、瞒报、伪造、篡改统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（五）拒报、屡次迟报统计资料的；</font></font></p><p><font color="#333333"><font size="4">　　（六）提供不真实、不完整的普查资料的；</font></font></p><p><font color="#333333"><font size="4">　　（七）在接受统计执法检查时，拒绝提供情况、提供虚假情况或者转移、隐匿、毁弃原始记录、统计台帐、统计报表以及与统计有关的其他资料的；</font></font></p><p><font color="#333333"><font size="4">　　（八）使用暴力或者威胁的方法，阻挠、抗拒统计执法检查的；</font></font></p><p><font color="#333333"><font size="4">　　（九）国家机关擅自制发统计调查表的；</font></font></p><p><font color="#333333"><font size="4">　　（十）违反统计法和统计制度规定，泄露国家秘密、统计调查对象商业秘密或者私人、家庭单项调查资料的；</font></font></p><p><font color="#333333"><font size="4">　　（十一）利用统计调查窃取国家秘密、损害社会公共利益或者进行欺诈活动的；</font></font></p><p><font color="#333333"><font size="4">　　（十二）违反《统计从业资格认定办法》，聘请、任用未取得统计从业资格证书的人员从事统计工作的；</font></font></p><p><font color="#333333"><font size="4">　　（十三）违法进行涉外调查的；</font></font></p><p><font color="#333333"><font size="4">　　（十四）法律、法规和规章规定的其他违法行为。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十八条　</b></font></font><font color="#333333"><font size="4">县级以上地方各级人民政府统计机构管辖发生在本行政区域内的统计违法案件。其中，在国家统计局派出的各级调查队组织实施的统计调查中发生的统计违法案件，由国家统计局派出的调查队管辖。</font></font></p><p><font color="#333333"><font size="4">　　国家统计局管辖在全国范围内有重大影响的或认为应当由其查处的统计违法案件。</font></font></p><p><font color="#333333"><font size="4"><b>　　第二十九条　</b></font></font><font color="#333333"><font size="4">决定立案查处的案件，应当及时组织调查。一般案件调查人员不得少于二人，重大案件应当组成调查组。</font></font></p><p><font color="#333333"><font size="4">　　调查人员应当合法、客观、全面地收集证据，不得主观臆断、偏听偏信，不得篡改、伪造证据。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十条　</b></font></font><font color="#333333"><font size="4">调查结束后，调查人员应当将调查情况及处理意见报领导审批。重大案件的处理由统计执法检查机关的负责人集体讨论决定。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十一条　</b></font></font><font color="#333333"><font size="4">统计违法案件审理终结，应当分别以下情况作出处理：</font></font></p><p><font color="#333333"><font size="4">　　（一）违反统计法律、法规、规章证据不足，或者违法事实情节轻微，依法不应追究法律责任的，即行销案；</font></font></p><p><font color="#333333"><font size="4">　　（二）违反统计法律、法规、规章事实清楚、证据确凿，尚未构成犯罪的，由统计执法检查机关依法作出处理；</font></font></p><p><font color="#333333"><font size="4">　　（三）违反统计法，涉嫌犯罪的，移送司法机关依法追究刑事责任。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十二条　</b></font></font><font color="#333333"><font size="4">统计执法检查机关在依法作出统计行政处罚决定前，应当告知当事人作出处罚的事实、理由、依据及拟作出的行政处罚决定，并告知当事人依法享有的权利。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十三条　</b></font></font><font color="#333333"><font size="4">统计执法检查机关在作出对法人或者其他组织二万元以上的罚款，对公民二千元以上的罚款的行政处罚决定前，应当告知当事人有要求举行听证的权利。当事人要求听证的，统计执法检查机关应当依法组织听证。</font></font></p><p><font color="#333333"><font size="4">　　当事人对县级以上地方各级人民政府统计机构查处的统计违法案件要求听证，省、自治区、直辖市人大常委会或人民政府对较大数额罚款的额度有具体规定的，从其规定。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十四条　</b></font></font><font color="#333333"><font size="4">立案查处的统计违法案件，应当在立案后三个月内处理完毕；因特殊情况需要延长办理期限的，应当按规定报经批准，但延长期不得超过三个月。</font></font></p><p><font color="#333333"><font size="4">　　统计违法案件处理决定执行后，予以结案。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第五章　备案与报告</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十五条　</b></font></font><font color="#333333"><font size="4">下列统计违法案件应当在立案后十日内报上一级统计执法检查机关：</font></font></p><p><font color="#333333"><font size="4">　　（一）统计违法责任人涉及科级以上党政领导干部的；</font></font></p><p><font color="#333333"><font size="4">　　（二）对拒绝、抵制篡改统计资料、编造虚假数据行为的统计人员进行打击报复的；</font></font></p><p><font color="#333333"><font size="4">　　（三）使用暴力或威胁的方法阻挠、抗拒统计执法检查的；</font></font></p><p><font color="#333333"><font size="4">　　（四）群众集体署名举报或新闻媒介公开报道，在社会上造成较大影响的；</font></font></p><p><font color="#333333"><font size="4">　　（五）检查机关认为应当报告的其他案件。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十六条　</b></font></font><font color="#333333"><font size="4">下列统计违法案件应当在结案后十日内向上一级统计执法检查机关备案：</font></font></p><p><font color="#333333"><font size="4">　　（一）给予科级以上党政领导干部行政处分的；</font></font></p><p><font color="#333333"><font size="4">　　（二）举行听证的；</font></font></p><p><font color="#333333"><font size="4">　　（三）经复议变更或撤销具体统计行政行为的；</font></font></p><p><font color="#333333"><font size="4">　　（四）统计行政诉讼案件；</font></font></p><p><font color="#333333"><font size="4">　　（五）经新闻媒介曝光的；</font></font></p><p><font color="#333333"><font size="4">　　（六）罚款数额三万元以上的；</font></font></p><p><font color="#333333"><font size="4">　　（七）立案后已上报上一级统计执法检查机关的各类案件</font></font></p><p><font color="#333333"><font size="4">　　前款所列（一）（三）（四）项案件，应当在结案后三十日内由省级统计执法检查机关报国家统计局备案。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十七条　</b></font></font><font color="#333333"><font size="4">国家统计局建立统计违法案件查处情况定期统计制度。</font></font></p><p><font color="#333333"><font size="4">　　统计执法检查机关应当定期向上一级统计执法检查机关报告统计执法检查和统计违法案件查处情况。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第六章　法律责任</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十八条　</b></font></font><font color="#333333"><font size="4">任何单位和个人有下列行为之一的，由统计执法检查机关责令改正，予以通报批评，并可以对负有直接责任的主管人员和其他直接责任人员依法给予行政处分或提请有关机关给予行政处分；违反《中华人民共和国治安管理处罚法》的，由公安机关依法给予行政处罚；涉嫌犯罪的，移送司法机关依法追究刑事责任：</font></font></p><p><font color="#333333"><font size="4">　　（一）在接受统计执法检查时，拒绝提供情况、提供虚假情况或者转移、隐匿、毁弃原始记录、统计台帐、统计报表以及与统计有关的其他资料的；</font></font></p><p><font color="#333333"><font size="4">　　（二）使用暴力或者威胁的方法阻挠、抗拒统计执法检查的；</font></font></p><p><font color="#333333"><font size="4">　　（三）不按期据实答复统计检查查询书的。</font></font></p><p><font color="#333333"><font size="4">　　企业事业组织有前款违法行为之一的，根据《中华人民共和国统计法实施细则》的规定，由统计执法检查机关予以警告，并可以处五万元以下的罚款。个体工商户有前款违法行为之一的，由统计执法检查机关予以警告，并可以处一万元以下的罚款。</font></font></p><p><font color="#333333"><font size="4"><b>　　第三十九条　</b></font></font><font color="#333333"><font size="4">地方、部门、单位的领导人及其他责任人员有下列行为之一的，由统计执法检查机关予以通报批评，并可以提请主管单位或监察机关依法给予行政处分；涉嫌犯罪的，移送司法机关依法追究刑事责任：</font></font></p><p><font color="#333333"><font size="4">　　（一）不接受或不按规定组织实施统计执法检查，造成本地区、本部门、本单位重要统计数据失实的；</font></font></p><p><font color="#333333"><font size="4">　　（二）对抵制、揭发统计违法行为的单位和个人进行打击报复的；</font></font></p><p><font color="#333333"><font size="4">　　（三）包庇、纵容统计违法行为的。</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十条　</b></font></font><font color="#333333"><font size="4">统计执法检查机关有下列行为之一的，对负有直接责任的主管人员和直接责任人员，由主管单位或监察机关给予批评教育；情节严重的，依法给予行政处分；涉嫌犯罪的，移送司法机关依法追究刑事责任：</font></font></p><p><font color="#333333"><font size="4">　　（一）瞒案不报，压案不查，包庇、纵容统计违法行为的；</font></font></p><p><font color="#333333"><font size="4">　　（二）不按法定权限、程序和要求执行公务，造成不利后果的；</font></font></p><p><font color="#333333"><font size="4">　　（三）违反保密规定，泄露举报人或案情的；</font></font></p><p><font color="#333333"><font size="4">　　（四）滥用职权，徇私舞弊的；</font></font></p><p><font color="#333333"><font size="4">　　（五）其他违法违纪行为。</font></font></p><p><font color="#333333"><font size="4">　　统计执法检查人员泄露在检查过程中知悉的被检查对象商业秘密和私人、家庭的单项调查资料，造成损害的，依法给予行政处分，并依法承担民事责任。</font></font></p><p align="CENTER"><font color="#333333"><font size="4">第七章　附　则</font></font></p><p><font color="#333333"><font size="4"><b>　　第四十一条　</b></font></font><font color="#333333"><font size="4">本规定由国家统计局负责解释。</font></font></p><p><font color="#333333"><font size="4"><b>第四十二条　</b></font></font><font color="#333333"><font size="4">本规定自公布之日起实施。《统计法规检查暂行规定》和《统计违法案件查处工作暂行规定》同时废止。</font></font></p>', 1488046434, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(130, 45, '土地调查条例', '<p align="CENTER"><font color="#333333"><font size="4">（</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2008</font></font></span></font></font><font color="#333333"><font size="4">年</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">2</font></font></span></font></font><font color="#333333"><font size="4">月</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">7</font></font></span></font></font><font color="#333333"><font size="4">日国务院令第</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">518</font></font></span></font></font><font color="#333333"><font size="4">号公布）</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第一章 总则</b></font></font></p><p><font color="#333333"><font size="4"><b>第一条</b></font></font><font color="#333333"><font size="4">为了科学、有效地组织实施土地调查，保障土地调查数据的真实性、准确性和及时性，根据《中华人民共和国土地管理法》和《中华人民共和国统计法》，制定本条例。</font></font></p><p><font color="#333333"><font size="4"><b>第二条</b></font></font><font color="#333333"><font size="4">土地调查的目的，是全面查清土地资源和利用状况，掌握真实准确的土地基础数据，为科学规划、合理利用、有效保护土地资源，实施最严格的耕地保护制度，加强和改善宏观调控提供依据，促进经济社会全面协调可持续发展。</font></font></p><p><font color="#333333"><font size="4"><b>第三条</b></font></font><font color="#333333"><font size="4"> 土地调查工作按照全国统一领导、部门分工协作、地方分级负责、各方共同参与的原则组织实施。</font></font></p><p><font color="#333333"><font size="4"><b>第四条</b></font></font><font color="#333333"><font size="4"> 土地调查所需经费，由中央和地方各级人民政府共同负担，列入相应年度的财政预算，按时拨付，确保足额到位。</font></font></p><p><font color="#333333"><font size="4">土地调查经费应当统一管理、专款专用、从严控制支出。</font></font></p><p><font color="#333333"><font size="4"><b>第五条</b></font></font><font color="#333333"><font size="4"> 报刊、广播、电视和互联网等新闻媒体，应当及时开展土地调查工作的宣传报道。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第二章 土地调查的内容和方法</b></font></font></p><p><font color="#333333"><font size="4"><b>第六条</b></font></font><font color="#333333"><font size="4"> 国家根据国民经济和社会发展需要，每</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">10</font></font></span></font></font><font color="#333333"><font size="4">年进行一次全国土地调查；根据土地管理工作的需要，每年进行土地变更调查。</font></font></p><p><font color="#333333"><font size="4"><b>第七条</b></font></font><font color="#333333"><font size="4"> 土地调查包括下列内容：</font></font></p><p><font color="#333333"><font size="4">（一）土地利用现状及变化情况，包括地类、位置、面积、分布等状况；</font></font></p><p><font color="#333333"><font size="4">（二）土地权属及变化情况，包括土地的所有权和使用权状况；</font></font></p><p><font color="#333333"><font size="4">（三）土地条件，包括土地的自然条件、社会经济条件等状况。</font></font></p><p><font color="#333333"><font size="4">进行土地利用现状及变化情况调查时，应当重点调查基本农田现状及变化情况，包括基本农田的数量、分布和保护状况。</font></font></p><p><font color="#333333"><font size="4"><b>第八条</b></font></font><font color="#333333"><font size="4"> 土地调查采用全面调查的方法，综合运用实地调查统计、遥感监测等手段。</font></font></p><p><font color="#333333"><font size="4"><b>第九条</b></font></font><font color="#333333"><font size="4"> 土地调查采用《土地利用现状分类》国家标准、统一的技术规程和按照国家统一标准制作的调查基础图件。</font></font></p><p><font color="#333333"><font size="4">土地调查技术规程，由国务院国土资源主管部门会同国务院有关部门制定。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第三章  土地调查的组织实施</b></font></font></p><p><font color="#333333"><font size="4"><b>第十条</b></font></font><font color="#333333"><font size="4"> 县级以上人民政府国土资源主管部门会同同级有关部门进行土地调查。</font></font></p><p><font color="#333333"><font size="4">乡（镇）人民政府、街道办事处和村（居）民委员会应当广泛动员和组织社会力量积极参与土地调查工作。</font></font></p><p><font color="#333333"><font size="4"><b>第十一条</b></font></font><font color="#333333"><font size="4"> 县级以上人民政府有关部门应当积极参与和密切配合土地调查工作，依法提供土地调查需要的相关资料。</font></font></p><p><font color="#333333"><font size="4">社会团体以及与土地调查有关的单位和个人应当依照本条例的规定，配合土地调查工作。</font></font></p><p><font color="#333333"><font size="4"><b>第十二条 </b></font></font><font color="#333333"><font size="4">全国土地调查总体方案由国务院国土资源主管部门会同国务院有关部门拟订，报国务院批准。县级以上地方人民政府国土资源主管部门会同同级有关部门按照国家统一要求，根据本行政区域的土地利用特点，编制地方土地调查实施方案，报上一级人民政府国土资源主管部门会同同级有关部门核准后施行。</font></font></p><p><font color="#333333"><font size="4"><b>第十三条</b></font></font><font color="#333333"><font size="4"> 在土地调查中，需要面向社会选择专业调查队伍承担的土地调查任务，应当通过招标投标方式组织实施。</font></font></p><p><font color="#333333"><font size="4">承担土地调查任务的单位应当具备以下条件：</font></font></p><p><font color="#333333"><font size="4">（一）具有法人资格；</font></font></p><p><font color="#333333"><font size="4">（二）有与土地调查相关的资质和工作业绩；</font></font></p><p><font color="#333333"><font size="4">（三）有完备的技术和质量管理制度；</font></font></p><p><font color="#333333"><font size="4">（四）有经过培训且考核合格的专业技术人员。</font></font></p><p><font color="#333333"><font size="4">国务院国土资源主管部门应当会同国务院有关部门加强对承担土地调查任务单位的管理，并公布符合本条第二款规定条件的单位名录。</font></font></p><p><font color="#333333"><font size="4"><b>第十四条</b></font></font><font color="#333333"><font size="4"> 土地调查人员应当坚持实事求是，恪守职业道德，具有执行调查任务所需要的专业知识。</font></font></p><p><font color="#333333"><font size="4">土地调查人员应当接受业务培训，经考核合格领取全国统一的土地调查员工作证。</font></font></p><p><font color="#333333"><font size="4"><b>第十五条</b></font></font><font color="#333333"><font size="4">土地调查人员应当严格执行全国土地调查总体方案和地方土地调查实施方案、《土地利用现状分类》国家标准和统一的技术规程，不得伪造、篡改调查资料，不得强令、授意调查对象提供虚假的调查资料。</font></font></p><p><font color="#333333"><font size="4">土地调查人员应当对其登记、审核、录入的调查资料与现场调查资料的一致性负责。</font></font></p><p><font color="#333333"><font size="4"><b>第十六条</b></font></font><font color="#333333"><font size="4"> 土地调查人员依法独立行使调查、报告、监督和检查职权，有权根据工作需要进行现场调查，并按照技术规程进行现场作业。</font></font></p><p><font color="#333333"><font size="4">土地调查人员有权就与调查有关的问题询问有关单位和个人，要求有关单位和个人如实提供相关资料。</font></font></p><p><font color="#333333"><font size="4">土地调查人员进行现场调查、现场作业以及询问有关单位和个人时，应当出示土地调查员工作证。</font></font></p><p><font color="#333333"><font size="4"><b>第十七条</b></font></font><font color="#333333"><font size="4">接受调查的有关单位和个人应当如实回答询问，履行现场指界义务，按照要求提供相关资料，不得转移、隐匿、篡改、毁弃原始记录和土地登记簿等相关资料。</font></font></p><p><font color="#333333"><font size="4"><b>第十八条</b></font></font><font color="#333333"><font size="4">各地方、各部门、各单位的负责人不得擅自修改土地调查资料、数据，不得强令或者授意土地调查人员篡改调查资料、数据或者编造虚假数据，不得对拒绝、抵制篡改调查资料、数据或者编造虚假数据的土地调查人员打击报复。</font></font></p><p><font color="#333333"><font size="4"><b>第四章 调查成果处理和质量控制</b></font></font></p><p><font color="#333333"><font size="4"><b>第十九条</b></font></font><font color="#333333"><font size="4"> 土地调查形成下列调查成果：</font></font></p><p><font color="#333333"><font size="4">（一）数据成果；</font></font></p><p><font color="#333333"><font size="4">（二）图件成果；</font></font></p><p><font color="#333333"><font size="4">（三）文字成果；</font></font></p><p><font color="#333333"><font size="4">（四）数据库成果。</font></font></p><p><font color="#333333"><font size="4"><b>第二十条 </b></font></font><font color="#333333"><font size="4">土地调查成果实行逐级汇交、汇总统计制度。</font></font></p><p><font color="#333333"><font size="4">土地调查数据的处理和上报应当按照全国土地调查总体方案和有关标准进行。</font></font></p><p><font color="#333333"><font size="4"><b>第二十一条</b></font></font><font color="#333333"><font size="4"> 县级以上地方人民政府对本行政区域的土地调查成果质量负总责，主要负责人是第一责任人。</font></font></p><p><font color="#333333"><font size="4">县级以上人民政府国土资源主管部门会同同级有关部门对调查的各个环节实行质量控制，建立土地调查成果质量控制岗位责任制，切实保证调查的数据、图件和被调查土地实际状况三者一致，并对其加工、整理、汇总的调查成果的准确性负责。</font></font></p><p><font color="#333333"><font size="4"><b>第二十二条</b></font></font><font color="#333333"><font size="4"> 国务院国土资源主管部门会同国务院有关部门统一组织土地调查成果质量的抽查工作。抽查结果作为评价土地调查成果质量的重要依据。</font></font></p><p><font color="#333333"><font size="4"><b>第二十三条</b></font></font><font color="#333333"><font size="4"> 土地调查成果实行分阶段、分级检查验收制度。前一阶段土地调查成果经检查验收合格后，方可开展下一阶段的调查工作。</font></font></p><p><font color="#333333"><font size="4">土地调查成果检查验收办法，由国务院国土资源主管部门会同国务院有关部门制定。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第五章 调查成果公布和应用</b></font></font></p><p><font color="#333333"><font size="4"><b>第二十四条</b></font></font><font color="#333333"><font size="4"> 国家建立土地调查成果公布制度。</font></font></p><p><font color="#333333"><font size="4">土地调查成果应当向社会公布，并接受公开查询，但依法应当保密的除外。</font></font></p><p><font color="#333333"><font size="4"><b>第二十五条</b></font></font><font color="#333333"><font size="4"> 全国土地调查成果，报国务院批准后公布。</font></font></p><p><font color="#333333"><font size="4">地方土地调查成果，经本级人民政府审核，报上一级人民政府批准后公布。</font></font></p><p><font color="#333333"><font size="4">全国土地调查成果公布后，县级以上地方人民政府方可逐级依次公布本行政区域的土地调查成果。</font></font></p><p><font color="#333333"><font size="4"><b>第二十六条</b></font></font><font color="#333333"><font size="4"> 县级以上人民政府国土资源主管部门会同同级有关部门做好土地调查成果的保存、管理、开发、应用和为社会公众提供服务等工作。</font></font></p><p><font color="#333333"><font size="4">国家通过土地调查，建立互联共享的土地调查数据库，并做好维护、更新工作。</font></font></p><p><font color="#333333"><font size="4"><b>第二十七条</b></font></font><font color="#333333"><font size="4"> 土地调查成果是编制国民经济和社会发展规划以及从事国土资源规划、管理、保护和利用的重要依据。</font></font></p><p><font color="#333333"><font size="4"><b>第二十八条</b></font></font><font color="#333333"><font size="4"> 土地调查成果应当严格管理和规范使用，不作为依照其他法律、行政法规对调查对象实施行政处罚的依据，不作为划分部门职责分工和管理范围的依据。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第六章 表彰和处罚</b></font></font></p><p><font color="#333333"><font size="4"><b>第二十九条</b></font></font><font color="#333333"><font size="4"> 对在土地调查工作中做出突出贡献的单位和个人，应当按照国家有关规定给予表彰或者奖励。</font></font></p><p><font color="#333333"><font size="4"><b>第三十条</b></font></font><font color="#333333"><font size="4"> 地方、部门、单位的负责人有下列行为之一的，依法给予处分；构成犯罪的，依法追究刑事责任：</font></font></p><p><font color="#333333"><font size="4">（一）擅自修改调查资料、数据的；</font></font></p><p><font color="#333333"><font size="4">（二）强令、授意土地调查人员篡改调查资料、数据或者编造虚假数据的；</font></font></p><p><font color="#333333"><font size="4">（三）对拒绝、抵制篡改调查资料、数据或者编造虚假数据的土地调查人员打击报复的。</font></font></p><p><font color="#333333"><font size="4"><b>第三十一条 </b></font></font><font color="#333333"><font size="4">土地调查人员不执行全国土地调查总体方案和地方土地调查实施方案、《土地利用现状分类》国家标准和统一的技术规程，或者伪造、篡改调查资料，或者强令、授意接受调查的有关单位和个人提供虚假调查资料的，依法给予处分，并由县级以上人民政府国土资源主管部门、统计机构予以通报批评。</font></font></p><p><font color="#333333"><font size="4"><b>第三十二条</b></font></font><font color="#333333"><font size="4">接受调查的单位和个人有下列行为之一的，由县级以上人民政府国土资源主管部门责令限期改正，可以处</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">5</font></font></span></font></font><font color="#333333"><font size="4">万元以下的罚款；构成违反治安管理行为的，由公安机关依法给予治安管理处罚；构成犯罪的，依法追究刑事责任：</font></font></p><p><font color="#333333"><font size="4">（一）拒绝或者阻挠土地调查人员依法进行调查的；</font></font></p><p><font color="#333333"><font size="4">（二）提供虚假调查资料的；</font></font></p><p><font color="#333333"><font size="4">（三）拒绝提供调查资料的；</font></font></p><p><font color="#333333"><font size="4">（四）转移、隐匿、篡改、毁弃原始记录、土地登记簿等相关资料的。</font></font></p><p><font color="#333333"><font size="4"><b>第三十三条</b></font></font><font color="#333333"><font size="4"> 县级以上地方人民政府有下列行为之一的，由上级人民政府予以通报批评；情节严重的，对直接负责的主管人员和其他直接责任人员依法给予处分：</font></font></p><p><font color="#333333"><font size="4">（一）未按期完成土地调查工作，被责令限期完成，逾期仍未完成的；</font></font></p><p><font color="#333333"><font size="4">（二）提供的土地调查数据失真，被责令限期改正，逾期仍未改正的。</font></font></p><p align="CENTER"><font color="#333333"><font size="4"><b>第七章 附则</b></font></font></p><p><font color="#333333"><font size="4"><b>第三十四条</b></font></font><font color="#333333"><font size="4"> 军用土地调查</font><font face="Calibri, serif"><span lang="en-US"><font face="serif"><font size="4">,</font></font></span></font></font><font color="#333333"><font size="4">由国务院国土资源主管部门会同军队有关部门按照国家统一规定和要求制定具体办法。</font></font></p><p><font color="#333333"><font size="4">中央单位使用土地的调查数据汇总内容的确定和成果的应用管理，由国务院国土资源主管部门会同国务院管理机关事务工作的机构负责。</font></font></p><p><font color="#333333"><font size="4"><b>第三十五条</b></font></font><font color="#333333"><font size="4">县级以上人民政府可以按照全国土地调查总体方案和地方土地调查实施方案成立土地调查领导小组，组织和领导土地调查工作。必要时，可以设立土地调查领导小组办公室负责土地调查日常工作。</font></font></p><p><font color="#333333"><font size="4"><b>第三十六条</b></font></font><font color="#333333"><font size="4"> 本条例自公布之日起施行</font></font></p><p><br></p>', 1488046460, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(131, 49, '四川省人口和计划生育违法违纪行为责任追究办法（试行）', '<p class="MsoNormal"><b>第一条&nbsp;</b><span>为认真贯彻执行计划生育基本国策，严肃处理违反人口和计划生育政策法规的行为，依据《中华人民共和国人口与计划生育法》等法律法规和《中国共产党纪律处分条例》等党内法规及有关政策规定，结合我省实际，制定本办法。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第二条&nbsp;</b><span>实行计划生育是国家的基本国策，是每个公民和全社会的共同责任。国家机关、社会团体实行人口和计划生育工作责任制，企事业单位及其他社会经济组织实行人口和计划生育工作法定代表人责任制。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>共产党员、干部和社会公众人物应带头模范遵守人口和计划生育法律法规和政策，有配合调查、如实说明自身婚育情况的义务，有教育、督促家庭成员自觉实行计划生育的责任。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第三条</b><span>&nbsp;任何组织和个人都有权对违反人口和计划生育政策法规的行为进行检举、控告，有关机关和部门应当认真受理、调查和处理。对检举有功人员，应当给予奖励。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><b>第四条</b><span>&nbsp;有下列行为之一的，给予责任人员中的共产党员（下同）党内严重警告至开除党籍处分；对其中的党和国家机关工作人员，从重处理：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）不符合法定再生育条件生育子女的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）对本人的超生子女实行假“收养”的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>有配偶者与他人生育子女或者再次违法生育的，给予开除党籍处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第五条</b><span>&nbsp;有下列行为之一的，给予批评教育或者诫勉处理；情节严重的，给予党内警告或者严重警告处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）夫妻符合再生育的法定条件但未经批准生育的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）收养他人子女满六个月未依法办理收养手续又没有正当理由的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<o:p></o:p></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）双方皆无配偶和子女的男女未履行婚姻登记手续第一次生育，三个月内未补办婚姻登记手续的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）双方皆无配偶，一方有一个子女、另一方无子女的男女未履行婚姻登记手续生育的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第六条&nbsp;</b><span>有下列行为之一的，给予撤销党内职务至开除党籍处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）非法为他人施行计划生育手术的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）利用超声技术或者其他技术手段为他人进行非医学需要的胎儿性别鉴定的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）进行非医学需要的选择性别的人工终止妊娠的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）实施假节育手术、进行假医学鉴定、出具假节育手术证明的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）组织、介绍妊娠妇女进行非法鉴定胎儿性别或者施行选择性别的终止妊娠手术的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（六）违法将终止妊娠药品销售给未获准施行终止妊娠的机构、个人或者药品零售企业的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第七条</b><span>&nbsp;有下列行为之一的，给予党内警告或者严重警告处分；情节严重的，给予撤销党内职务至开除党籍处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）以不正当手段取得或者伪造、变造、买卖生育服务证、生育证、出生医学证明、独生子女父母光荣证、奖励扶助证明、流动人口计划生育婚育证明等计划生育证明材料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）阻挠、干涉他人实行计划生育的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）协助、包庇、纵容他人违法生育的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）拒绝、阻碍有关机关和部门依法调查取证、征收社会抚养费等执法行为的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）以编造、散播谣言和封建迷信活动等形式，干扰、破坏人口和计划生育工作开展的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><b>第八条</b><span>&nbsp;有下列行为之一的，对直接负责的主管人员和其他直接责任人员，给予党内警告或者严重警告处分；情节严重的，给予撤销党内职务至开除党籍处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）违法审批人口和计划生育有关申请的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）出具计划生育证明材料有过错的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）违反社会抚养费征收、管理、使用规定的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）截留、挪用、骗取、贪污奖励经费、免费计划生育技术服务经费等计划生育经费以及倒卖、变卖政府免费提供的避孕药具的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）虚报、瞒报、伪造、篡改或者拒报人口和计划生育统计数据，或者有其他弄虚作假行为的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（六）不落实人口和计划生育工作财政保障政策的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（七）拒不落实独生子女父母奖励和优待政策的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（八）有其他滥用职权、玩忽职守、徇私舞弊行为的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><b>第九条</b><span>&nbsp;有下列行为之一的，对直接负责的主管人员和其他直接责任人员，给予党内警告或者严重警告处分；情节严重的，给予撤销党内职务至开除党籍处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）计划生育免费技术服务定点机构拒绝按规定提供免费技术服务的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）在婚姻登记、孕产期保健、户口登记、物业管理、房屋出租管理、资质准入、营业执照办理等工作中，不履行法定查验职责，或者不按规定向人口和计划生育行政管理部门提供常规信息并及时通报特定情况的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）拒不提供违法生育当事人的收入证明、纳税证明等相关证明材料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）在流动人口计划生育管理工作中不履行相关法定职责的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）拒不配合有关人口和计划生育工作的考核、评估、检查、督查、督办和案件查处的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（六）有其他不依法履行有关法定职责行为的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十条</b><span>&nbsp;有下列情形之一的，对该地区、部门（单位）实行“一票否决”：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（一）未完成年度人口和计划生育工作目标任务的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（二）本单位人员（含聘用人员）违法生育的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（三）在人口和计划生育工作中因违法行政引发恶性案件或者造成恶劣影响的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（四）在人口和计划生育技术服务工作中发生重大责任事故的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>（五）拒不履行人口和计划生育法定职责，引发群体性事件的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十一条</b><span>&nbsp;对受到“一票否决”的地区、部门（单位），取消其当年综合性先进、荣誉称号的评选资格。对市（州）和纳入省委、省政府目标管理的省直部门，在年度综合目标考核时，按照目标管理的有关规定降低一个获奖等次。各地、各部门参照上述原则对受到“一票否决”的单位给予相应处理。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对受到“一票否决”的地区、部门（单位）的主要负责人、人口和计划生育工作的分管负责人和直接责任人，当年年度考核不得确定为优秀等次，并取消其各类先进、荣誉称号的评选资格；任期内被否决两次以上的，责令辞职或者免职；其中出现重大失误的，三年内不得提拔和晋升职务。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;<b>&nbsp;&nbsp;</b></span><b>第十二条</b><span>&nbsp;对地方党委、政府实行“一票否决”，由上一级人口和计划生育行政管理部门提出否决建议方案，报请同级党委、政府决定，有关部门执行；对有关部门（单位）实行“一票否决”，由本级人口和计划生育行政管理部门提出否决建议方案，报请同级党委、政府决定，有关部门执行；对人口和计划生育行政管理部门实行“一票否决”，由同级党委、政府决定，有关部门执行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>发现有关责任人员调任、转任后五年内有应予“一票否决”情形的，由有管辖权的人口和计划生育行政管理部门对其提出否决建议，并按前款规定执行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十三条</b><span>&nbsp;把遵守人口和计划生育法律法规和政策作为提拔任用干部、推荐各级中国共产党代表大会代表、人大代表、政协委员、青联委员候选人、工商联执委和评选各类劳动模范、先进个人的一项基本要求。党代表、人大代表、政协委员、青联委员、工商联执委、劳动模范和先进个人违法生育的，按照规定程序罢免其代表职务或者撤销其委员资格、荣誉称号。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对违法生育人员，视为不具备参选村（居）民委员会主任或者委员，以及录用、聘用为财政供养人员、国有及国有控股企业领导人员的条件。在任村（居）民委员会主任或委员违法生育的，责令辞职。在任国有及国有控股企业领导人员违法生育的，予以免职。企业法定代表人或主要负责人违法生育的，其所在企业不得评为先进、模范企业。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对违法生育造成恶劣影响的行为，可予以公开揭露。有关部门对查处的违法生育典型案件，可予以通报或者向社会公布。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十四条&nbsp;</b><span>依照本办法规定给予党纪处分的，可视情节同时给予相应的组织处理。情节较轻的，责令作出书面检查、给予通报批评、诫勉等处理；情节较重的，予以责令辞职、免职等处理；情节严重的，予以辞退、解聘等处理。不是共产党员的，应当单独给予组织处理。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对违反本办法规定的责任人员，应当给予政纪处分的，行政机关公务员按照《行政机关公务员处分条例》处理；事业单位工作人员，在国家对事业单位及其工作人员的专门管理规定出台前，可参照《行政机关公务员处分条例》处理；其他人员，应参照本办法规定的党纪处分档次给予相应的政纪处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>涉嫌犯罪的，依法移送司法机关处理。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十五条</b><span>&nbsp;对违反本办法规定的责任人员，应当给予纪律处分的，由纪检监察机关、任免机关按照党组织隶属关系或者人事管理权限实施；组织处理由组织人事部门、任免机关按照人事管理权限实施。需按本办法第十三条进行处理的，由人口和计划生育行政管理部门提出建议。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>对违反本办法的行为，有关机关或部门在职权范围内作出处理后，认为需要给予其他处理的，应当及时提出建议，被建议机关或部门无正当理由不得拒绝。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十六条</b><span>&nbsp;各级纪检监察机关、组织人事部门，以及人口和计划生育行政管理部门等职能部门应当对本办法执行情况进行监督检查，加强协调配合，建立健全情况通报、联合执法、资格审查、案件移送等工作机制。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><b>第十七条&nbsp;</b><span>本办法由中共四川省纪委、四川省监察厅商四川省人口和计划生育委员会负责解释。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;<b>&nbsp;</b></span><b>第十八条&nbsp;</b><span>本办法自发布之日起施行。本办法实施前发生的人口和计划生育违法违纪行为，尚未处理的，按本办法的规定处理；已经处理的，不再重新处理。中共四川省纪委、四川省监察厅《关于共产党员和国家机关工作人员违反计划生育法规纪律处分规定》（川纪发〔<span lang="EN-US">1995</span>〕<span lang="EN-US">31</span>号）、《关于修改〈共产党员和国家机关工作人员违反计划生育法规纪律处分规定〉部分条款的通知》（川纪发〔<span lang="EN-US">1998</span>〕<span lang="EN-US">4</span>号）同时废止。</span></p>', 1488046960, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(132, 50, '行政机关公务员处分条例', '<p class="MsoNormal" align="center" style="text-align: center;"><font size="3">第一章　总　则</font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第一条　为了严肃行政机关纪律，规范行政机关公务员的行为，保证行政机关及其公务员依法履行职责，根据《中华人民共和国公务员法》和《中华人民共和国行政监察法》，制定本条例。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二条　行政机关公务员违反法律、法规、规章以及行政机关的决定和命令，应当承担纪律责任的，依照本条例给予处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>法律、其他行政法规、国务院决定对行政机关公务员处分有规定的，依照该法律、行政法规、国务院决定的规定执行；法律、其他行政法规、国务院决定对行政机关公务员应当受到处分的违法违纪行为做了规定，但是未对处分幅度做规定的，适用本条例第三章与其最相类似的条款有关处分幅度的规定。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>地方性法规、部门规章、地方政府规章可以补充规定本条例第三章未作规定的应当给予处分的违法违纪行为以及相应的处分幅度。除国务院监察机关、国务院人事部门外，国务院其他部门制定处分规章，应当与国务院监察机关、国务院人事部门联合制定。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>除法律、法规、规章以及国务院决定外，行政机关不得以其他形式设定行政机关公务员处分事项。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三条　行政机关公务员依法履行职务的行为受法律保护，非因法定事由，非经法定程序，不受处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四条　给予行政机关公务员处分，应当坚持公正、公平和教育与惩处相结合的原则。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>给予行政机关公务员处分，应当与其违法违纪行为的性质、情节、危害程度相适应。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>给予行政机关公务员处分，应当事实清楚、证据确凿、定性准确、处理恰当、程序合法、手续完备。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第五条　行政机关公务员违法违纪涉嫌犯罪的，应当移送司法机关依法追究刑事责任。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><font size="3">第二章　处分的种类和适用</font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第六条　行政机关公务员处分的种类为：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）警告；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）记过；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）记大过；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）降级；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（五）撤职；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（六）开除。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第七条　行政机关公务员受处分的期间为：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）警告，<span lang="EN-US">6</span>个月；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）记过，<span lang="EN-US">12</span>个月；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）记大过，<span lang="EN-US">18</span>个月；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）降级、撤职，<span lang="EN-US">24</span>个月。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第八条　行政机关公务员在受处分期间不得晋升职务和级别，其中，受记过、记大过、降级、撤职处分的，不得晋升工资档次；受撤职处分的，应当按照规定降低级别。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第九条　行政机关公务员受开除处分的，自处分决定生效之日起，解除其与单位的人事关系，不得再担任公务员职务。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>行政机关公务员受开除以外的处分，在受处分期间有悔改表现，并且没有再发生违法违纪行为的，处分期满后，应当解除处分。解除处分后，晋升工资档次、级别和职务不再受原处分的影响。但是，解除降级、撤职处分的，不视为恢复原级别、原职务。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十条　行政机关公务员同时有两种以上需要给予处分的行为的，应当分别确定其处分。应当给予的处分种类不同的，执行其中最重的处分；应当给予撤职以下多个相同种类处分的，执行该处分，并在一个处分期以上、多个处分期之和以下，决定处分期。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>行政机关公务员在受处分期间受到新的处分的，其处分期为原处分期尚未执行的期限与新处分期限之和。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>处分期最长不得超过<span lang="EN-US">48</span>个月。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十一条　行政机关公务员<span lang="EN-US">2</span>人以上共同违法违纪，需要给予处分的，根据各自应当承担的纪律责任，分别给予处分。<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十二条　有下列情形之一的，应当从重处分：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）在<span lang="EN-US">2</span>人以上的共同违法违纪行为中起主要作用的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）隐匿、伪造、销毁证据的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）串供或者阻止他人揭发检举、提供证据材料的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）包庇同案人员的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（五）法律、法规、规章规定的其他从重情节。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十三条　有下列情形之一的，应当从轻处分：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）主动交代违法违纪行为的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）主动采取措施，有效避免或者挽回损失的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）检举他人重大违法违纪行为，情况属实的。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十四条　行政机关公务员主动交代违法违纪行为，并主动采取措施有效避免或者挽回损失的，应当减轻处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>行政机关公务员违纪行为情节轻微，经过批评教育后改正的，可以免予处分。<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十五条　行政机关公务员有本条例第十二条、第十三条规定情形之一的，应当在本条例第三章规定的处分幅度以内从重或者从轻给予处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>行政机关公务员有本条例第十四条第一款规定情形的，应当在本条例第三章规定的处分幅度以外，减轻一个处分的档次给予处分。应当给予警告处分，又有减轻处分的情形的，免予处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十六条　行政机关经人民法院、监察机关、行政复议机关或者上级行政机关依法认定有行政违法行为或者其他违法违纪行为，需要追究纪律责任的，对负有责任的领导人员和直接责任人员给予处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十七条　违法违纪的行政机关公务员在行政机关对其作出处分决定前，已经依法被判处刑罚、罢免、免职或者已经辞去领导职务，依法应当给予处分的，由行政机关根据其违法违纪事实，给予处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>行政机关公务员依法被判处刑罚的，给予开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><font size="3">第三章　违法违纪行为及其适用的处分</font></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十八条　有下列行为之一的，给予记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）散布有损国家声誉的言论，组织或者参加旨在反对国家的集会、游行、示威等活动的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）组织或者参加非法组织，组织或者参加罢工的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）违反国家的民族宗教政策，造成不良后果的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）以暴力、威胁、贿赂、欺骗等手段，破坏选举的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（五）在对外交往中损害国家荣誉和利益的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（六）非法出境，或者违反规定滞留境外不归的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（七）未经批准获取境外永久居留资格，或者取得外国国籍的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（八）其他违反政治纪律的行为。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>有前款第（六）项规定行为的，给予开除处分；有前款第（一）项、第（二）项或者第（三）项规定的行为，属于不明真相被裹挟参加，经批评教育后确有悔改表现的，可以减轻或者免予处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第十九条　有下列行为之一的，给予警告、记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）负有领导责任的公务员违反议事规则，个人或者少数人决定重大事项，或者改变集体作出的重大决定的；</font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）拒绝执行上级依法作出的决定、命令的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）拒不执行机关的交流决定的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）拒不执行人民法院对行政案件的判决、裁定或者监察机关、审计机关、行政复议机关作出的决定的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（五）违反规定应当回避而不回避，影响公正执行公务，造成不良后果的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（六）离任、辞职或者被辞退时，拒不办理公务交接手续或者拒不接受审计的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（七）旷工或者因公外出、请假期满无正当理由逾期不归，造成不良影响的；<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（八）其他违反组织纪律的行为。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十条　有下列行为之一的，给予记过、记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）不依法履行职责，致使可以避免的爆炸、火灾、传染病传播流行、严重环境污染、严重人员伤亡等重大事故或者群体性事件发生的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）发生重大事故、灾害、事件或者重大刑事案件、治安案件，不按规定报告、处理的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）对救灾、抢险、防汛、防疫、优抚、扶贫、移民、救济、社会保险、征地补偿等专项款物疏于管理，致使款物被贪污、挪用，或者毁损、灭失的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）其他玩忽职守、贻误工作的行为。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十一条　有下列行为之一的，给予警告或者记过处分；情节较重的，给予记大过或者降级处分；情节严重的，给予撤职处分：<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）在行政许可工作中违反法定权限、条件和程序设定或者实施行政许可的；<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）违法设定或者实施行政强制措施的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）违法设定或者实施行政处罚的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）违反法律、法规规定进行行政委托的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（五）对需要政府、政府部门决定的招标投标、征收征用、城市房屋拆迁、拍卖等事项违反规定办理的。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十二条　弄虚作假，误导、欺骗领导和公众，造成不良后果的，给予警告、记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十三条　有贪污、索贿、受贿、行贿、介绍贿赂、挪用公款、利用职务之便为自己或者他人谋取私利、巨额财产来源不明等违反廉政纪律行为的，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十四条　违反财经纪律，挥霍浪费国家资财的，给予警告处分；情节较重的，给予记过或者记大过处分；情节严重的，给予降级或者撤职处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十五条　有下列行为之一的，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）以殴打、体罚、非法拘禁等方式侵犯公民人身权利的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）压制批评，打击报复，扣压、销毁举报信件，或者向被举报人透露举报情况的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）违反规定向公民、法人或者其他组织摊派或者收取财物的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）妨碍执行公务或者违反规定干预执行公务的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（五）其他滥用职权，侵害公民、法人或者其他组织合法权益的行为。<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十六条　泄露国家秘密、工作秘密，或者泄露因履行职责掌握的商业秘密、个人隐私，造成不良后果的，给予警告、记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十七条　从事或者参与营利性活动，在企业或者其他营利性组织中兼任职务的，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十八条　严重违反公务员职业道德，工作作风懈怠、工作态度恶劣，造成不良影响的，给予警告、记过或者记大过处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第二十九条　有下列行为之一的，给予警告、记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）拒不承担赡养、抚养、扶养义务的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）虐待、遗弃家庭成员的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）包养情人的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）严重违反社会公德的行为。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>有前款第（三）项行为的，给予撤职或者开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十条　参与迷信活动，造成不良影响的，给予警告、记过或者记大过处分；组织迷信活动的，给予降级或者撤职处分，情节严重的，给予开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十一条　吸食、注射毒品或者组织、支持、参与卖淫、嫖娼、色情淫乱活动的，给予撤职或者开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十二条　参与赌博的，给予警告或者记过处分；情节较重的，给予记大过或者降级处分；情节严重的，给予撤职或者开除处分。<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>为赌博活动提供场所或者其他便利条件的，给予警告、记过或者记大过处分；情节严重的，给予撤职或者开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>在工作时间赌博的，给予记过、记大过或者降级处分；屡教不改的，给予撤职或者开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>挪用公款赌博的，给予撤职或者开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>利用赌博索贿、受贿或者行贿的，依照本条例第二十三条的规定给予处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十三条　违反规定超计划生育的，给予降级或者撤职处分；情节严重的，给予开除处分。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><font size="3">第四章　处分的权限</font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十四条　对行政机关公务员给予处分，由任免机关或者监察机关（以下统称处分决定机关）按照管理权限决定。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十五条　对经全国人民代表大会及其常务委员会决定任命的国务院组成人员给予处分，由国务院决定。其中，拟给予撤职、开除处分的，由国务院向全国人民代表大会提出罢免建议，或者向全国人民代表大会常务委员会提出免职建议。罢免或者免职前，国务院可以决定暂停其履行职务。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十六条　对经地方各级人民代表大会及其常务委员会选举或者决定任命的地方各级人民政府领导人员给予处分，由上一级人民政府决定。<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>拟给予经县级以上地方人民代表大会及其常务委员会选举或者决定任命的县级以上地方人民政府领导人员撤职、开除处分的，应当先由本级人民政府向同级人民代表大会提出罢免建议。其中，拟给予县级以上地方人民政府副职领导人员撤职、开除处分的，也可以向同级人民代表大会常务委员会提出撤销职务的建议。拟给予乡镇人民政府领导人员撤职、开除处分的，应当先由本级人民政府向同级人民代表大会提出罢免建议。罢免或者撤销职务前，上级人民政府可以决定暂停其履行职务；遇有特殊紧急情况，省级以上人民政府认为必要时，也可以对其作出撤职或者开除的处分，同时报告同级人民代表大会常务委员会，并通报下级人民代表大会常务委员会。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十七条　对地方各级人民政府工作部门正职领导人员给予处分，由本级人民政府决定。其中，拟给予撤职、开除处分的，由本级人民政府向同级人民代表大会常务委员会提出免职建议。免去职务前，本级人民政府或者上级人民政府可以决定暂停其履行职务。<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十八条　行政机关公务员违法违纪，已经被立案调查，不宜继续履行职责的，任免机关可以决定暂停其履行职务。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>被调查的公务员在违法违纪案件立案调查期间，不得交流、出境、辞去公职或者办理退休手续。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><font size="3">第五章　处分的程序</font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第三十九条　任免机关对涉嫌违法违纪的行政机关公务员的调查、处理，按照下列程序办理：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）经任免机关负责人同意，由任免机关有关部门对需要调查处理的事项进行初步调查；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）任免机关有关部门经初步调查认为该公务员涉嫌违法违纪，需要进一步查证的，报任免机关负责人批准后立案；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）任免机关有关部门负责对该公务员违法违纪事实做进一步调查，包括收集、查证有关证据材料，听取被调查的公务员所在单位的领导成员、有关工作人员以及所在单位监察机构的意见，向其他有关单位和人员了解情况，并形成书面调查材料，向任免机关负责人报告；<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）任免机关有关部门将调查认定的事实及拟给予处分的依据告知被调查的公务员本人，听取其陈述和申辩，并对其所提出的事实、理由和证据进行复核，记录在案。被调查的公务员提出的事实、理由和证据成立的，应予采信；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（五）经任免机关领导成员集体讨论，作出对该公务员给予处分、免予处分或者撤销案件的决定；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（六）任免机关应当将处分决定以书面形式通知受处分的公务员本人，并在一定范围内宣布；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（七）任免机关有关部门应当将处分决定归入受处分的公务员本人档案，同时汇集有关材料形成该处分案件的工作档案。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>受处分的行政机关公务员处分期满解除处分的程序，参照前款第（五）项、第（六）项和第（七）项的规定办理。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>任免机关应当按照管理权限，及时将处分决定或者解除处分决定报公务员主管部门备案。<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十条　监察机关对违法违纪的行政机关公务员的调查、处理，依照《中华人民共和国行政监察法》规定的程序办理。<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十一条　对行政机关公务员违法违纪案件进行调查，应当由２名以上办案人员进行；接受调查的单位和个人应当如实提供情况。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>严禁以暴力、威胁、引诱、欺骗等非法方式收集证据；非法收集的证据不得作为定案的依据。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十二条　参与行政机关公务员违法违纪案件调查、处理的人员有下列情形之一的，应当提出回避申请；被调查的公务员以及与案件有利害关系的公民、法人或者其他组织有权要求其回避：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）与被调查的公务员是近亲属关系的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）与被调查的案件有利害关系的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）与被调查的公务员有其他关系，可能影响案件公正处理的。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十三条　处分决定机关负责人的回避，由处分决定机关的上一级行政机关负责人决定；其他违法违纪案件调查、处理人员的回避，由处分决定机关负责人决定。<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>处分决定机关或者处分决定机关的上一级行政机关，发现违法违纪案件调查、处理人员有应当回避的情形，可以直接决定该人员回避。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十四条　给予行政机关公务员处分，应当自批准立案之日起<span lang="EN-US">6</span>个月内作出决定；案情复杂或者遇有其他特殊情形的，办案期限可以延长，但是最长不得超过<span lang="EN-US">12</span>个月。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十五条　处分决定应当包括下列内容：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）被处分人员的姓名、职务、级别、工作单位等基本情况；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）经查证的违法违纪事实；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）处分的种类和依据；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（四）不服处分决定的申诉途径和期限；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（五）处分决定机关的名称、印章和作出决定的日期。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>解除处分决定除包括前款第（一）项、第（二）项和第（五）项规定的内容外，还应当包括原处分的种类和解除处分的依据，以及受处分的行政机关公务员在受处分期间的表现情况。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十六条　处分决定、解除处分决定自作出之日起生效。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十七条　行政机关公务员受到开除处分后，有新工作单位的，其本人档案转由新工作单位管理；没有新工作单位的，其本人档案转由其户籍所在地人事部门所属的人才服务机构管理。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><font size="3">第六章　不服处分的申诉</font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十八条　受到处分的行政机关公务员对处分决定不服的，依照《中华人民共和国公务员法》和《中华人民共和国行政监察法》的有关规定，可以申请复核或者申诉。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>复核、申诉期间不停止处分的执行。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>行政机关公务员不因提出复核、申诉而被加重处分。<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第四十九条　有下列情形之一的，受理公务员复核、申诉的机关应当撤销处分决定，重新作出决定或者责令原处分决定机关重新作出决定：<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）处分所依据的违法违纪事实证据不足的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）违反法定程序，影响案件公正处理的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）作出处分决定超越职权或者滥用职权的。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第五十条　有下列情形之一的，受理公务员复核、申诉的机关应当变更处分决定，或者责令原处分决定机关变更处分决定：<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（一）适用法律、法规、规章或者国务院决定错误的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（二）对违法违纪行为的情节认定有误的；<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>（三）处分不当的。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第五十一条　行政机关公务员的处分决定被变更，需要调整该公务员的职务、级别或者工资档次的，应当按照规定予以调整；行政机关公务员的处分决定被撤销的，应当恢复该公务员的级别、工资档次，按照原职务安排相应的职务，并在适当范围内为其恢复名誉。<span lang="EN-US">&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>被撤销处分或者被减轻处分的行政机关公务员工资福利受到损失的，应当予以补偿。<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><font size="3">第七章　附　则</font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第五十二条　有违法违纪行为应当受到处分的行政机关公务员，在处分决定机关作出处分决定前已经退休的，不再给予处分；但是，依法应当给予降级、撤职、开除处分的，应当按照规定相应降低或者取消其享受的待遇。<span lang="EN-US">&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第五十三条　行政机关公务员违法违纪取得的财物和用于违法违纪的财物，除依法应当由其他机关没收、追缴或者责令退赔的，由处分决定机关没收、追缴或者责令退赔。违法违纪取得的财物应当退还原所有人或者原持有人的，退还原所有人或者原持有人；属于国家财产以及不应当退还或者无法退还原所有人或者原持有人的，上缴国库。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p class="MsoNormal"><font size="3"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>第五十四条　对法律、法规授权的具有公共事务管理职能的事业单位中经批准参照《中华人民共和国公务员法》管理的工作人员给予处分，参照本条例的有关规定办理。<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span></font></p><p class="MsoNormal"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p><p><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>第五十五条　本条例自</span><st1:chsdate w:st="on" isrocdate="False" islunardate="False" day="1" month="6" year="2007"><span lang="EN-US">2007</span><span>年</span><span lang="EN-US">6</span><span>月</span><span lang="EN-US">1</span><span>日</span></st1:chsdate><span>起施行。</span><st1:chsdate w:st="on" isrocdate="False" islunardate="False" day="13" month="9" year="1988"><span lang="EN-US">1988</span><span>年</span><span lang="EN-US">9</span><span>月</span><span lang="EN-US">13</span><span>日</span></st1:chsdate><span>国务院发布的《国家行政机关工作人员贪污贿赂行政处分暂行规定》同时废止。</span></p>', 1488046963, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(133, 50, '统计违法违纪行为处分规定', '<p class="MsoNormal" align="center" style="text-align: center;"><span>统计违法违纪行为处分规定统计违法违纪行为处分规定<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span>中<span lang="EN-US">&nbsp;&nbsp;</span>华 人<span lang="EN-US">&nbsp;&nbsp;</span>民 共 和<span lang="EN-US">&nbsp;&nbsp;</span>国<span lang="EN-US">&nbsp;&nbsp;</span>监 察 部<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span>中华人民共和国人力资源和社会保障部 令<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span>国　<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>家<span lang="EN-US">&nbsp;&nbsp;</span>　　统<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>计<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>局<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span>第<span lang="EN-US">18</span>号<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span>　　《统计违法违纪行为处分规定》已经监察部<st1:chsdate isrocdate="False" islunardate="False" day="9" month="2" year="2009" w:st="on"><span lang="EN-US">2009</span>年<span lang="EN-US">2</span>月<span lang="EN-US">9</span>日</st1:chsdate>第一次部长办公会议、人力资源社会保障部<st1:chsdate isrocdate="False" islunardate="False" day="30" month="12" year="2008" w:st="on"><span lang="EN-US">2008</span>年<span lang="EN-US">12</span>月<span lang="EN-US">30</span>日</st1:chsdate>第十六次部务会议、国家统计局<st1:chsdate isrocdate="False" islunardate="False" day="6" month="11" year="2008" w:st="on"><span lang="EN-US">2008</span>年<span lang="EN-US">11</span>月<span lang="EN-US">6</span>日</st1:chsdate>第十八次局务会议审议通过。现予公布，自<st1:chsdate isrocdate="False" islunardate="False" day="1" month="5" year="2009" w:st="on"><span lang="EN-US">2009</span>年<span lang="EN-US">5</span>月<span lang="EN-US">1</span>日起</st1:chsdate>施行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal" align="right" style="text-align: right;"><span>监<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span>察<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span>部<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span>部<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span>长<span lang="EN-US">&nbsp;&nbsp;</span>马<span lang="EN-US">&nbsp;&nbsp;</span>馼<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="right" style="text-align: right;"><span>人力资源社会保障部部长<span lang="EN-US">&nbsp;&nbsp;</span>尹蔚民<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="right" style="text-align: right;"><span>国 家 统 计 局<span lang="EN-US">&nbsp;&nbsp;</span>局<span lang="EN-US">&nbsp;&nbsp;</span>长<span lang="EN-US">&nbsp;&nbsp;</span>马建堂<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="right" style="text-align: right;"><span>　二○○九年三月二十五日<span lang="EN-US">&nbsp;&nbsp;<o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal" align="center" style="text-align: center;"><span>统计违法违纪行为处分规定<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span>　　第一条<span lang="EN-US">&nbsp;&nbsp;</span>为了加强统计工作，提高统计数据的准确性和及时性，惩处和预防统计违法违纪行为，促进统计法律法规的贯彻实施，根据《中华人民共和国统计法》、《中华人民共和国行政监察法》、《中华人民共和国公务员法》、《行政机关公务员处分条例》及其他有关法律、行政法规，制定本规定。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第二条<span lang="EN-US">&nbsp;&nbsp;</span>有统计违法违纪行为的单位中负有责任的领导人员和直接责任人员，以及有统计违法违纪行为的个人，应当承担纪律责任。属于下列人员的（以下统称有关责任人员），由任免机关或者监察机关按照管理权限依法给予处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（一）行政机关公务员；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（二）法律、法规授权的具有公共事务管理职能的事业单位中经批准参照《中华人民共和国公务员法》管理的工作人员；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（三）行政机关依法委托的组织中除工勤人员以外的工作人员；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（四）企业、事业单位、社会团体中由行政机关任命的人员。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　法律、行政法规、国务院决定和国务院监察机关、国务院人力资源社会保障部门制定的处分规章对统计违法违纪行为的处分另有规定的，从其规定。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第三条<span lang="EN-US">&nbsp;&nbsp;</span>地方、部门以及企业、事业单位、社会团体的领导人员有下列行为之一的，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（一）自行修改统计资料、编造虚假数据的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（二）强令、授意本地区、本部门、本单位统计机构、统计人员或者其他有关机构、人员拒报、虚报、瞒报或者篡改统计资料、编造虚假数据的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（三）对拒绝、抵制篡改统计资料或者对拒绝、抵制编造虚假数据的人员进行打击报复的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（四）对揭发、检举统计违法违纪行为的人员进行打击报复的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　有前款第（三）项、第（四）项规定行为的，应当从重处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第四条<span lang="EN-US">&nbsp;&nbsp;</span>地方、部门以及企业、事业单位、社会团体的领导人员，对本地区、本部门、本单位严重失实的统计数据，应当发现而未发现或者发现后不予纠正，造成不良后果的，给予警告或者记过处分；造成严重后果的，给予记大过或者降级处分；造成特别严重后果的，给予撤职或者开除处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　 第五条<span lang="EN-US">&nbsp;&nbsp;</span>各级人民政府统计机构、有关部门及其工作人员在实施统计调查活动中，有下列行为之一的，对有关责任人员，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（一）强令、授意统计调查对象虚报、瞒报或者伪造、篡改统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（二）参与篡改统计资料、编造虚假数据的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第六条<span lang="EN-US">&nbsp;&nbsp;</span>各级人民政府统计机构、有关部门及其工作人员在实施统计调查活动中，有下列行为之一的，对有关责任人员，给予警告、记过或者记大过处分；情节较重的，给予降级处分；情节严重的，给予撤职处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（一）故意拖延或者拒报统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（二）明知统计数据不实，不履行职责调查核实，造成不良后果的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第七条<span lang="EN-US">&nbsp;&nbsp;</span>统计调查对象中的单位有下列行为之一，情节较重的，对有关责任人员，给予警告、记过或者记大过处分；情节严重的，给予降级或者撤职处分；情节特别严重的，给予开除处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（一）虚报、瞒报统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（二）伪造、篡改统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（三）拒报或者屡次迟报统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（四）拒绝提供情况、提供虚假情况或者转移、隐匿、毁弃原始统计记录、统计台账、统计报表以及与统计有关的其他资料的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第八条<span lang="EN-US">&nbsp;&nbsp;</span>违反国家规定的权限和程序公布统计资料，造成不良后果的，对有关责任人员，给予警告或者记过处分；情节较重的，给予记大过或者降级处分；情节严重的，给予撤职处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第九条<span lang="EN-US">&nbsp;&nbsp;</span>有下列行为之一，造成不良后果的，对有关责任人员，给予警告、记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（一）泄露属于国家秘密的统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（二）未经本人同意，泄露统计调查对象个人、家庭资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　（三）泄露统计调查中知悉的统计调查对象商业秘密的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第十条<span lang="EN-US">&nbsp;&nbsp;</span>包庇、纵容统计违法违纪行为的，对有关责任人员，给予记过或者记大过处分；情节较重的，给予降级或者撤职处分；情节严重的，给予开除处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第十一条<span lang="EN-US">&nbsp;&nbsp;</span>受到处分的人员对处分决定不服的，依照《中华人民共和国行政监察法》、《中华人民共和国公务员法》、《行政机关公务员处分条例》等有关规定，可以申请复核或者申诉。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第十二条<span lang="EN-US">&nbsp;&nbsp;</span>任免机关、监察机关和人民政府统计机构建立案件移送制度。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　任免机关、监察机关查处统计违法违纪案件，认为应当由人民政府统计机构给予行政处罚的，应当将有关案件材料移送人民政府统计机构。人民政府统计机构应当依法及时查处，并将处理结果书面告知任免机关、监察机关。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　人民政府统计机构查处统计行政违法案件，认为应当由任免机关或者监察机关给予处分的，应当及时将有关案件材料移送任免机关或者监察机关。任免机关或者监察机关应当依法及时查处，并将处理结果书面告知人民政府统计机构。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第十三条<span lang="EN-US">&nbsp;&nbsp;</span>有统计违法违纪行为，应当给予党纪处分的，移送党的纪律检查机关处理。涉嫌犯罪的，移送司法机关依法追究刑事责任。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第十四条<span lang="EN-US">&nbsp;&nbsp;</span>本规定由监察部、人力资源社会保障部、国家统计局负责解释。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>　　第十五条<span lang="EN-US">&nbsp;&nbsp;</span>本规定自<st1:chsdate isrocdate="False" islunardate="False" day="1" month="5" year="2009" w:st="on"><span lang="EN-US">2009</span>年<span lang="EN-US">5</span>月<span lang="EN-US">1</span>日起</st1:chsdate>施行。</span></p>', 1488046990, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(134, 50, '《统计上严重失信企业信息公示暂行办法》', '<p class="MsoNormal" align="center" style="text-align: center;"><b><span>中华人民共和国国家统计局公告</span></b></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">2014</span><span>年第<span lang="EN-US">3</span>号</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　为了进一步加强依法统计，大力推进诚信统计，建立保障企业独立真实报送统计信息的长效机制，切实提高统计数据质量和政府统计公信力，国家统计局制定了《统计上严重失信企业信息公示暂行办法》，现予以公告。</span></p><p class="MsoNormal" align="right" style="text-align: right;"><span>国家统计局</span></p><p class="MsoNormal" align="right" style="text-align: right;"><st1:chsdate year="2014" month="11" day="27" islunardate="False" isrocdate="False" w:st="on"><span lang="EN-US">2014</span><span>年<span lang="EN-US">11</span>月<span lang="EN-US">27</span>日</span></st1:chsdate></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span></span></b>&nbsp;</p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>统计上严重失信企业信息公示暂行办法</span></b></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第一条　</span></b><span>为贯彻落实《中共中央关于全面推进依法治国若干重大问题的决定》和《统计法》《企业信息公示暂行条例》《社会信用体系建设规划纲要（<span lang="EN-US">2014</span>—<span lang="EN-US">2020</span>年）》，加强依法统计，推进诚信统计，建立保障企业独立真实报送统计信息长效机制，提高统计数据质量和政府统计公信力，制定本办法。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第二条　</span></b><span>本办法所称统计上严重失信企业，是指在依法开展的政府统计调查中，有下列情形之一的企业：</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　（一）编造虚假统计数据；</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　（二）虚报、瞒报统计数据数额较大或者虚报率、瞒报率较高；</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　（三）有其他严重统计违法行为，应当受到行政处罚。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第三条　</span></b><span>对依法认定的统计上严重失信企业，政府统计机构应当通过中国统计信息网，向社会公示失信企业信息。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　公示的失信企业信息包括企业名称、地址、法定代表人或者主要负责人、统计违法行为、依法处理情况等。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第四条　</span></b><span>公示失信企业信息应当遵循合法真实、公正及时、鼓励诚信的原则。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第五条　</span></b><span>国家统计局组织管理全国统计上严重失信企业信息公示工作，在中国统计信息网上直接公示特别严重的失信企业信息。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　省级统计机构在其门户网站建立统计上严重失信企业信息公示专栏，统一链接到中国统计信息网。省、市、县级统计机构按照《统计法》规定的查处统计违法行为职责分工，在本级或者上级统计机构门户网站向社会公示失信企业信息，同时加载到省级统计机构失信企业信息公示专栏。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第六条　</span></b><span>政府统计机构自依法认定失信企业之日起<span lang="EN-US">20</span>个工作日内公示失信企业信息。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　对严重失信企业和严重干预企业独立真实报送统计数据的单位、个人，依法追究责任。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第七条　</span></b><span>失信企业信息公示期限为<span lang="EN-US">1</span>年。在公示期间，企业认真整改到位，经企业申请，履行公示职责的政府统计机构核实后，可以从公示网站提前移除失信企业信息，但公示时间不得少于<span lang="EN-US">6</span>个月。企业整改不到位，公示期限延长至<span lang="EN-US">2</span>年。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　在失信企业信息公示期间，政府统计机构应当重点检查企业遵守统计法情况，再次发现企业有统计违法行为的，公示期限延长至<span lang="EN-US">2</span>年。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第八条　</span></b><span>政府统计机构应当按照《企业信息公示暂行条例》《社会信用体系建设规划纲要（<span lang="EN-US">2014</span>—<span lang="EN-US">2020</span>年）》等国家有关规定，将统计上严重失信企业信息纳入金融、工商等行业和部门信用信息系统，与企业融资、政府补贴、工商注册管理等挂钩。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第九条　</span></b><span>下级统计机构未按照本办法履行职责的，由上级统计机构责令改正；情节严重的，对负有责任的主管人员和其他直接责任人员依法给予处分。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第十条　</span></b><span>政府统计机构发现公示的失信企业信息不准确的，应当及时更正。公民、法人或者其他组织有证据证明政府统计机构公示的失信企业信息不准确的，有权要求政府统计机构予以更正。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第十一条　</span></b><span>鼓励公民、法人或者其他组织举报统计上严重失信企业和其他统计违法行为，对举报者依法予以保护。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第十二条　</span></b><span>公民、法人或者其他组织认为政府统计机构在公示失信企业信息工作中的具体行政行为侵犯其合法权益的，可以依法申请行政复议或者提起行政诉讼。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第十三条　</span></b><span>企业报送的统计资料异常且不能做合理解释的，由政府统计机构列入统计信用异常企业名录，告诫企业自我检查更正。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　企业认真整改到位，由政府统计机构从统计信用异常企业名录移除；企业整改不到位，经依法查实具有严重统计违法行为的，将作为统计上严重失信企业，由政府统计机构公示失信企业信息。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第十四条　</span></b><span>本办法适用于在各级人民政府、县级以上人民政府统计机构组织实施的统计活动中，统计上严重失信企业的信息公示。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><span>　　在上述统计活动中严重失信的其他组织，其信息公示参照本办法执行。</span></p><p class="MsoNormal" align="left" style="text-align: left;"><b><span>　　第十五条　</span></b><span>本办法由国家统计局负责解释。</span></p><p><span lang="EN-US"></span><span></span></p><p class="MsoNormal" align="left"><b>第十六条　</b><span>本办法自<st1:chsdate year="2015" month="1" day="1" islunardate="False" isrocdate="False" w:st="on"><span lang="EN-US">2015</span>年<span lang="EN-US">1</span>月<span lang="EN-US">1</span>日起</st1:chsdate>施</span></p>', 1488047039, 0, 1, NULL, '', '', 0, '信息部', '本站', 0),
(135, 49, '四川省基本单位名录库管理办法', '<p class="MsoNormal" align="center" style="text-align: center;"><b><span>第一章<span lang="EN-US">&nbsp;&nbsp;</span>总<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>则<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal" align="left"><span>第一条<span lang="EN-US">&nbsp;&nbsp;</span>为加强基本单位名录库（以下简称名录库）管理，充分发挥名录库在政府统计调查和经济社会管理方面的基础作用，根据《中华人民共和国统计法》、《全国经济普查条例》和《四川省统计管理条例》等有关法律法规和规定，结合四川实际，制定本办法。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第二条<span lang="EN-US">&nbsp;&nbsp;</span>本办法所称名录库，是指四川省行政区域内从事经济社会活动基本单位的基础信息库。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>本办法所称基本单位，是指四川省行政区域内的国家机关、社会团体、企业、事业单位、其他组织和个体经营户。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第三条<span lang="EN-US">&nbsp;&nbsp;</span>本办法适用于四川省地方各级人民政府、县级以上人民政府统计机构和有关部门组织实施的名录库工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第四条<span lang="EN-US">&nbsp;&nbsp;</span>全省建立统一规范的名录库管理体系，实行统一领导、分级管理。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第五条<span lang="EN-US">&nbsp;&nbsp;</span>县级以上地方人民政府统计机构是名录库的主管部门，负责依法组织、协调和指导本行政区域内的名录库工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>县级以上地方人民政府有关部门应当协助、配合人民政府统计机构共同做好名录库管理工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第六条<span lang="EN-US">&nbsp;&nbsp;</span>地方各级人民政府和有关部门应当加强对名录库工作的组织领导，为名录库工作提供必要条件，保证名录库工作正常进行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>地方各级人民政府应当将名录库工作所需经费列入部门综合预算。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第七条<span lang="EN-US">&nbsp;&nbsp;</span>全省进行统一规划，按照统一标准和工作流程，建立地方各级人民政府和有关部门互联互通的网络平台，实行名录库资源共享。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第八条<span lang="EN-US">&nbsp;&nbsp;</span>县级以上地方各级人民政府对下级人民政府、本级人民政府统计机构和有关部门执行本办法情况实施监督。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第二章<span lang="EN-US">&nbsp;&nbsp;</span>名录库管理分工与职责<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal" align="left"><span>第九条<span lang="EN-US">&nbsp;&nbsp;</span>名录库管理工作，按照县级以上地方人民政府统计机构主管、部门协管，乡镇（街道）具体实施的原则进行分工，各负其责。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第十条<span lang="EN-US">&nbsp;&nbsp;</span>县级以上地方人民政府统计机构履行下列职责：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（一）组织完成上级人民政府统计机构布置的基本单位统计调查任务；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（二）制订本行政区域内的名录库建设规划、管理制度；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（三）审核、整理部门提供的行政登记基本单位资料；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（四）对名录库进行维护更新；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（五）建立名录库信息共享平台，依法公布和提供有关信息；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（六）对部门名录库管理工作进行指导，开展业务培训。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第十一条<span lang="EN-US">&nbsp;&nbsp;</span>县级以上地方人民政府各部门配合同级人民政府统计机构开展基本单位调查工作，履行下列职责：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（一）完成人民政府统计机构布置的基本单位统计调查任务；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（二）制订本部门名录库管理制度；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（三）按照人民政府统计机构的要求，提供本部门行政登记的基本单位资料；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（四）加强部门名录库基础建设，开展有关业务培训。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第十二条<span lang="EN-US">&nbsp;&nbsp;</span>乡镇人民政府、街道办事处明确具有统计从业资格的名录库管理人员，负责基本单位统计调查工作，管理和维护本行政区域名录库。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第三章<span lang="EN-US">&nbsp;&nbsp;</span>名录库资料来源与提供<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal" align="left"><span>第十三条<span lang="EN-US">&nbsp;&nbsp;</span>名录库资料来源于普查、行政登记和基本单位调查。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>普查年份，名录库所需资料由人民政府统计机构从普查资料中取得；非普查年份，名录库所需资料由人民政府统计机构从部门行政登记资料中取得。行政登记资料缺口部分，先由部门按统计标准提供基本单位名录，再由人民政府统计机构根据基本单位名录组织调查取得。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第十四条<span lang="EN-US">&nbsp;&nbsp;</span>有关部门向同级人民政府统计机构提供以下行政登记资料：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（一）工商行政管理部门提供企业和个体经营户登记资料；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（二）机构编制管理部门提供纳入本级机构编制管理范围内的党政群机关统计和事业单位登记资料；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（三）民政部门提供社会团体登记资料；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（四）税务机关提供税务登记资料；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（五）质量技术监督部门提供组织机构代码登记资料；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>（六）行业主管部门提供行业及其他行政登记资料。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第十五条<span lang="EN-US">&nbsp;&nbsp;</span>普查资料和基本单位调查资料，由人民政府统计机构提供。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第四章<span lang="EN-US">&nbsp;&nbsp;</span>名录库资料整理与更新<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal" align="left"><span>第十六条<span lang="EN-US">&nbsp;&nbsp;</span>从普查中取得的基本单位资料，由人民政府统计机构直接整理；从部门行政登记中取得的基本单位资料，由登记部门整理；从基本单位调查中取得的资料，由人民政府统计机构直接整理。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第十七条<span lang="EN-US">&nbsp;&nbsp;</span>整理后的基本单位资料，需要纳入统计调查的企业，按照国家和省有关规定进行核定。有关部门负责企业入库申报的资格审查、资料审核等基础工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第十八条<span lang="EN-US">&nbsp;&nbsp;</span>名录库的维护更新，按分级负责的原则，由人民政府统计机构负责。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>鼓励和支持社会组织、公众参与名录库维护更新及质量监督工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第五章<span lang="EN-US">&nbsp;&nbsp;</span>名录库使用与保密<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal" align="left"><span>第十九条<span lang="EN-US">&nbsp;&nbsp;</span>各地、各部门进行统计调查和社会管理应当使用统一的名录库，确保准确性和一致性。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第二十条<span lang="EN-US">&nbsp;&nbsp;</span>各地、各部门进行社会管理使用的基本单位资料，应当以统一的名录库资料为准。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第二十一条<span lang="EN-US">&nbsp;&nbsp;</span>县级以上人民政府统计机构定期向部门反馈对应的名录库资料，供部门使用；定期向社会公布名录库综合资料，供社会公众使用。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第二十二条<span lang="EN-US">&nbsp;&nbsp;</span>单位、个人对名录库资料有特殊需要的，可以向人民政府统计机构申请查询；需要对名录库资料进行再加工的，可以通过有偿方式委托人民政府统计机构进行再加工。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第二十三条<span lang="EN-US">&nbsp;&nbsp;</span>部门不得将人民政府统计机构反馈的名录库资料用于除政府统计和部门管理以外的目的，也不得转给第三方使用。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第二十四条<span lang="EN-US">&nbsp;&nbsp;</span>统计机构和统计人员对在名录库工作中知悉的国家秘密、商业秘密和个人信息，应当予以保密。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第二十五条<span lang="EN-US">&nbsp;&nbsp;</span>名录库中能够识别或者推断某个基本单位的资料，任何单位和个人不得对外提供、泄露。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span>第二十六条<span lang="EN-US">&nbsp;&nbsp;</span>违反保密规定，擅自对外提供名录库资料，或将名录库资料用于除政府统计和部门管理以外的目的，人民政府统计机构将依据有关法律法规追究法律责任。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第六章<span lang="EN-US">&nbsp;&nbsp;</span>附<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>则<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal" align="left"><span>第二十七条<span lang="EN-US">&nbsp;&nbsp;</span>本办法自<span lang="EN-US">&nbsp;2013&nbsp;</span>年<span lang="EN-US">&nbsp;3&nbsp;</span>月<span lang="EN-US">&nbsp;1&nbsp;</span>日起施行，有效期<span lang="EN-US">&nbsp;5&nbsp;</span>年。<span lang="EN-US">1996&nbsp;</span>年四川省人民政府发布的《四川省统计登记办法》同时废止。</span></p>', 1488047090, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(136, 49, '《四川省统计管理条例》', '<p class="MsoNormal"><span>（<st1:chsdate w:st="on" isrocdate="False" islunardate="False" day="20" month="12" year="1995"><span lang="EN-US">1995</span>年<span lang="EN-US">12</span>月<span lang="EN-US">20</span>日</st1:chsdate>四川省第八届人民代表大会常务委员会第十八次会议通过根据<st1:chsdate w:st="on" isrocdate="False" islunardate="False" day="17" month="10" year="1997"><span lang="EN-US">1997</span>年<span lang="EN-US">10</span>月<span lang="EN-US">17</span>日</st1:chsdate>四川省第八届人民代表大会常务委员会第二十九次会议《关于修改〈四川省统计管理条例〉的决定》第一次修正根据<st1:chsdate w:st="on" isrocdate="False" islunardate="False" day="30" month="11" year="2002"><span lang="EN-US">2002</span>年<span lang="EN-US">11</span>月<span lang="EN-US">30</span>日</st1:chsdate>四川省第九届人民代表大会常务委员会第三十二次会议《关于修改〈四川省统计管理条例〉的决定》第二次修正，<st1:chsdate w:st="on" isrocdate="False" islunardate="False" day="27" month="5" year="2011"><span lang="EN-US">2011</span>年<span lang="EN-US">5</span>月<span lang="EN-US">27</span>日</st1:chsdate>四川省第十一届人民代表大会常务委员会第二十三次会议修订）<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第一章 总 则<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal"><b>第一条</b><span>&nbsp;为了加强统计管理，保障统计资料的真实性、准确性、完整性和及时性，充分发挥统计在指导国民经济和社会发展中的重要作用，根据《中华人民共和国统计法》和国家有关规定，结合四川省实际，制定本条例。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二条</b><span>&nbsp;四川省行政区域内的国家机关、社会团体、企业、事业单位、其他组织和个人，必须遵守国家统计法律、行政法规和本条例。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三条</b><span>&nbsp;统计的基本任务是对经济社会发展情况进行统计调查、统计分析，提供统计资料和统计咨询意见，实行统计监督。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第四条</b><span>&nbsp;统计机构和统计人员实行工作责任制，按照统计法律、法规和统计制度，如实提供统计资料，准确及时完成统计工作任务，保守国家秘密。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>统计机构和统计人员依法独立行使统计调查、统计报告、统计监督的职权，不受侵犯。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第五条</b><span>&nbsp;县级以上地方各级人民政府统计机构主管本行政区域内的统计工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第六条</b><span>&nbsp;地方各级人民政府和有关部门应当加强对统计工作的领导，为统计工作提供必要条件，保证统计工作的正常进行。对在统计工作中作出突出成绩的单位和个人，应当依照国家或者企业事业组织的规定给予表彰或者奖励。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>地方各级人民政府应当将统计工作所需经费列入同级财政预算。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第七条</b><span>&nbsp;统计工作应当接受社会公众的监督。任何单位和个人有权检举统计中弄虚作假等违法行为。对检举有功的单位和个人应当给予表彰和奖励。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第八条</b><span>&nbsp;全省应当加强统计科学研究，完善统计指标体系、统计标准体系和统计调查体系，提高统计的科学性。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>全省对统计信息化建设进行统一规划，有计划地推进统计信息搜集、处理、传输、存储技术和统计数据库体系的现代化。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>全省搭建统一的统计信息平台，建立运转高效的信息资源共享机制。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第二章 统计机构和统计人员<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal"><b>第九条</b><span>&nbsp;四川省统计局是全省统计工作的主管机构，对全省统计工作履行组织、协调、管理、监督的职责，负责组织实施全省国民经济核算。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>市（州）、县<span lang="EN-US">(</span>市、区<span lang="EN-US">)</span>人民政府的统计机构对本行政区域内的统计工作履行组织、协调、管理、监督的职责，负责组织实施本行政区域国民经济核算。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>地方各级人民政府统计机构受同级人民政府和上一级人民政府统计机构的双重领导，统计业务以上一级人民政府统计机构领导为主。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十条</b><span>&nbsp;乡镇人民政府、街道办事处设置统计机构或者配置专职综合统计人员，负责本乡镇、街道的统计工作。确有困难的经县级人民政府同意可以配置兼职综合统计人员。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>居民委员会和村民委员会指定人员负责本居住地区的统计工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十一条</b><span>&nbsp;县级以上地方各级人民政府各工作部门和行业主管部门根据各自的职责和统计任务的需要，设置综合统计机构，配置统计人员并指定统计负责人，在当地人民政府统计机构的指导下负责本部门、行业的统计工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>企业、事业单位、其他组织根据统计任务的需要，设置综合统计机构，为统计工作提供必要条件；也可配置专职或者兼职综合统计人员，或者委托统计代理机构负责本单位的统计工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十二条</b><span>&nbsp;统计人员应当具备与其从事的统计工作相适应的专业知识，取得统计从业资格。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>县级以上地方各级人民政府统计机构和有关部门应当定期开展对统计人员的专业培训和职业道德教育。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>统计人员所在单位应当支持统计人员定期参加统计继续教育培训。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十三条</b><span>&nbsp;统计人员应当保持基本稳定。各部门、各单位统计机构负责人或者统计工作负责人的变动，应当事先征求同级人民政府统计机构的意见。各级人民政府统计机构主要负责人的变动，应当事先征得上一级人民政府统计机构的同意。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第三章 统计调查<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal"><b>第十四条</b><span>&nbsp;搜集、整理统计资料，应当以周期性普查为基础，以经常性抽样调查为主体，综合运用全面调查、重点调查等方法，并充分利用行政记录等资料。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十五条</b><span>&nbsp;工商、编制、民政等部门应当定期向同级人民政府统计机构提供基本单位行政登记资料，建立统一的基本单位名录库。基本单位名录库的具体管理办法，由四川省人民政府另行制定。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>各级人民政府统计机构在获取基本单位名录库资料后，应当及时书面告知统计调查对象到人民政府统计机构领取统计报表。统计调查对象应当自告知之日起三十日内到当地人民政府统计机构领取统计报表。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十六条</b><span>&nbsp;国家机关、社会团体、企业、事业单位、其他组织和个体工商户，按照统计法律、法规和统计制度，准确及时地向统计机构报送统计资料。不得迟报、拒报统计资料。不得提供不真实、不完整的统计资料。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>国家机关、社会团体、企业、事业单位和其他组织应当建立原始记录、统计台账，建立、健全统计资料的审核、签署、交接、归档等管理制度。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>基层群众自治组织和公民有义务如实提供国家统计调查所需要的情况。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十七条</b><span>&nbsp;地方各级人民政府统计机构，依法下达给各单位的统计调查任务，由各单位的综合统计机构或者综合统计人员负责组织实施。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>国家明确规定的统计制度、统计方法，各地方、各部门、各单位不得改变。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十八条&nbsp;</b><span>统计调查项目包括国家统计调查项目、部门统计调查项目和地方统计调查项目。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>地方统计调查项目应当与国家统计调查项目和部门统计调查项目相衔接。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（一）地方统计调查项目不得与国家统计调查项目和部门统计调查项目重复；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（二）下级地方统计调查项目不得与上级地方统计调查项目重复；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（三）新增地方统计调查项目不得与原有地方统计调查项目重复。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>地方统计调查项目的审批程序依照国家有关规定执行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第十九条</b><span>&nbsp;经批准的统计调查项目，在统计调查表右上角必须标明制表机关名称、表号、批准机关名称、批准号、有效期限等标志。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>不符合本条前款规定或者超过了有效期限的统计调查表，调查对象有权拒绝填报。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十条</b><span>&nbsp;违反统计法律、法规和统计制度进行的统计调查，有关单位和个人有权拒绝提供统计资料。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>不得利用统计调查窃取国家秘密、损害社会公共利益或者进行欺诈性、误导性的评价、咨询活动。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十一条</b><span>&nbsp;国家普查，由地方各级人民政府统一领导并组织统计机构和有关部门共同实施。普查以外的国家统计调查和地方统计调查，由地方各级人民政府统计机构统一领导并组织有关部门实施。国家和地方的统计调查按行政区域进行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第四章 统计资料的提供和使用<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal"><b>第二十二条</b><span>&nbsp;各地方、各部门、各单位向上一级或者本级人民政府统计机构报送统计资料，由本地方、本部门、本单位的综合统计机构或者综合统计人员负责。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>各地方、各部门、各单位的负责人对本地方、本部门、本单位统计机构或统计人员提供的统计资料，不得自行修改，也不得强令或者授意他人修改。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>统计机构、统计人员对本地方、本部门、本单位负责人强令或授意篡改统计资料、弄虚作假的行为，应当拒绝、抵制。任何地方、部门、单位的负责人不得对拒绝、抵制篡改统计资料、弄虚作假行为的统计人员进行打击报复。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十三条</b><span>&nbsp;县级以上地方各级人民政府，对经济效益、工作实绩进行评价，属国家统计管理指标范围内的数据，必须统一使用经同级人民政府统计机构核准的统计数据。国家另有规定的除外。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>各部门进行经济效益、工作评价，属统计管理指标范围内的数据，必须统一使用由本部门综合统计机构或者综合统计人员提供的统计数据。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十四条</b><span>&nbsp;公开发布统计资料应当真实可靠并遵守下列规定：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">(</span><span>一<span lang="EN-US">)</span>地方各级人民政府统计机构发布统计公报，按照国家有关规定执行；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">(</span><span>二<span lang="EN-US">)</span>县级以上地方各级人民政府各工作部门和行业主管部门发布统计资料前应当抄送同级人民政府统计机构并与统计机构核准的统计资料一致；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">(</span><span>三<span lang="EN-US">)</span>有关单位发布的本单位的统计资料，应当与上级主管部门核准的统计资料一致；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">(</span><span>四<span lang="EN-US">)</span>新闻媒介需要发表尚未公布的国民经济、社会发展基本统计资料，应当经同级人民政府统计机构审核同意，并注明资料来源；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span lang="EN-US">(</span><span>五<span lang="EN-US">)</span>统计信息服务机构发布统计资料，按照国家有关规定执行。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十五条</b><span>&nbsp;统计机构和统计人员对在统计工作中知悉的国家秘密、商业秘密和个人信息，应当予以保密。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十六条</b><span>&nbsp;县级以上地方各级人民政府统计机构应当做好统计信息咨询服务工作，充分利用可以公开的统计信息为社会公众服务。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>国家机关、社会团体、企业、事业单位及其他组织和个人需要了解有关统计资料的，可以向当地人民政府统计机构查询。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span>第五章 统计监督检查<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十七条</b><span>&nbsp;县级以上地方各级人民政府及其监察机关对下级人民政府、本级人民政府统计机构和有关部门执行本条例的情况实施监督。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>县级以上地方各级人民政府根据统计工作需要，可以对有关部门和下级人民政府的统计工作进行巡查。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>县级以上地方各级人民政府统计机构和有关部门应当对其组织的统计工作进行监督检查，对统计数据进行稽查核实。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十八条</b><span>&nbsp;县级以上地方各级人民政府统计机构设置统计检查机构，配备监督检查人员，具体负责本行政区域内的统计监督检查工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第二十九条</b><span>&nbsp;统计监督检查人员由省统计局组织培训，经考核合格取得统计检查证，方能从事统计监督检查工作。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>县级以上地方各级人民政府统计机构进行监督检查时，监督检查人员不得少于二人，并应当出示执法证件；未出示的，有关单位和个人有权拒绝检查。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十条&nbsp;</b><span>县级以上地方各级人民政府统计机构履行监督检查职责时，有权要求被检查的单位和个人提供统计原始记录、统计台账及有关材料，在检查期内必要时可以按照程序登记保存有关的统计资料、会计资料和其他资料。被检查的单位和个人应当支持、协助统计检查机构或者统计检查员的工作，如实反映情况，提供证明材料，不得转移、隐匿、篡改、毁损统计资料。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>县级以上地方各级人民政府统计机构有权向被检查的单位和个人发出统计检查查询书。被检查的单位和个人，在接到统计检查查询书之日起十五日内应当作出书面答复。逾期不答复的视为拒报。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十一条</b><span>&nbsp;县级以上地方各级人民政府统计机构负责查处本行政区域内的统计违法案件，上级人民政府统计机构可以直接查处下级人民政府统计机构管辖范围内的统计违法案件。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>人民政府各工作部门、行业主管部门的统计违法案件，由同级人民政府统计机构查处；人民政府统计机构的统计违法案件，由上一级人民政府统计机构查处；人民政府的统计违法案件由上一级人民政府责成其统计机构查处或者组织专门力量查处。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十二条</b><span>&nbsp;地方各级人民政府统计机构立案查处的重大统计违法案件，在立案的同时应当报上一级人民政府统计机构备案。处理结案后，应当向上一级人民政府统计机构报送处理结果。上级人民政府统计机构有权纠正下级人民政府统计违法案件的不适当的处理决定。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第六章 法律责任<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal"><b>第三十三条</b><span>&nbsp;统计调查对象有下列行为之一的，由县级以上地方人民政府统计机构责令改正，给予警告，可以予以通报；其直接负责的主管人员和其他直接责任人员属于国家工作人员的，由任免机关或者监察机关依法给予处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（一）拒绝提供统计资料或者经催报后仍未按时提供统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（二）提供不真实或者不完整的统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（三）拒绝答复或者不如实答复统计检查查询书的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（四）拒绝、阻碍统计调查、统计检查的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（五）转移、隐匿、篡改、毁弃或者拒绝提供原始记录和凭证、统计台账、统计调查表及其他相关证明和资料的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>企业事业单位或者其他组织有前款所列行为之一的，可以并处五万元以下的罚款；情节严重的，并处五万元以上二十万元以下的罚款。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>个体工商户有本条第一款所列行为之一的，由县级以上地方人民政府统计机构责令改正，给予警告，可以并处一万元以下的罚款。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>统计代理机构违反国家有关法律法规和统计制度，造成委托人统计管理混乱、损害国家和委托人利益的<span lang="EN-US">,</span>伪造篡改委托单位提供的统计资料的，委托人故意向统计代理机构隐瞒真实情况或者委托人会同统计代理机构共同提供不真实统计资料的<span lang="EN-US">,</span>应当承担法律责任。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十四条</b><span>&nbsp;统计调查对象有下列行为之一的，由县级以上地方人民政府统计机构责令改正，予以通报批评；有关负责人员和直接责任人员属于国家工作人员的，依法给予行政处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（一）不按规定建立原始记录、统计台账的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（二）安排未取得统计从业资格证书的人员从事统计工作的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（三）迟报统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（四）未按规定领取统计报表的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>企业事业组织有前款违法行为之一的，由县级以上人民政府统计机构予以警告，可以并处一万元以下罚款。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>个体工商户迟报统计资料的，由县级以上地方人民政府统计机构予以警告，可以并处一千元以下罚款。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十五条</b><span>&nbsp;地方人民政府、政府统计机构或者有关部门、单位的负责人有下列行为之一的，由任免机关或者监察机关依法给予处分，并由县级以上地方人民政府统计机构予以通报：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（一）自行修改统计资料、编造虚假统计数据的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（二）要求统计机构、统计人员或者其他机构、人员伪造、篡改统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（三）对依法履行职责或者拒绝、抵制统计违法行为的统计人员打击报复的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（四）对本地方、本部门、本单位发生的严重统计违法行为失察的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十六条</b><span>&nbsp;县级以上地方各级人民政府统计机构或者有关部门在组织实施统计调查活动中有下列行为之一的，由本级人民政府、上级人民政府统计机构或者本级人民政府统计机构责令改正，予以通报；对直接负责的主管人员和其他直接责任人员，由任免机关或者监察机关依法给予处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（一）未经批准擅自组织实施统计调查的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（二）未经批准擅自变更统计调查制度的内容的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（三）伪造、篡改统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（四）要求统计调查对象或者其他机构、人员提供不真实的统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（五）未按照统计调查制度的规定报送有关资料的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>统计人员有前款第三项至第五项所列行为之一的，责令改正，依法给予处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十七条</b><span>&nbsp;各级人民政府有关部门未按规定向同级人民政府统计机构提供行政登记资料的，由本级人民政府、上级人民政府统计机构责令改正，予以通报；对直接负责的主管人员和其他直接责任人员，由任免机关或者监察机关依法给予处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十八条</b><span>&nbsp;国家机关以外的组织或者个人违反统计法律、行政法规和本条例的规定，擅自进行依法应当由国家机关实施的统计调查的，由人民政府统计机构责令改正，宣布其统计调查无效，没收违法统计资料，并可处以一千元以上、五万元以下罚款；构成犯罪的，依法追究刑事责任。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第三十九条</b><span>&nbsp;县级以上地方各级人民政府统计机构或者有关部门有下列行为之一的，对直接负责的主管人员和其他直接责任人员由任免机关或者监察机关依法给予处分：<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（一）违法公布统计资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（二）泄露统计调查对象的商业秘密、个人信息或者提供、泄露在统计调查中获得的能够识别或者推断单个统计调查对象身份的资料的；<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>（三）违反国家有关规定，造成统计资料毁损、灭失的。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><span>统计人员有前款所列行为之一的，依法给予处分。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第四十条</b><span>&nbsp;县级以上地方各级人民政府统计机构查处统计违法行为时，认为对有关国家工作人员依法应当给予处分的，应当提出给予处分的建议；该国家工作人员的任免机关或者监察机关应当依法及时作出决定，并将结果书面通报县级以上地方人民政府统计机构。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第四十一条</b><span>&nbsp;因弄虚作假、提供不真实统计资料骗取荣誉称号、物质奖励或者职务晋升的，由人民政府统计机构提请做出有关决定的单位或者其上级单位、监察机关取消其荣誉称号、追缴获得的物质利益或者撤销其晋升的职务。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第四十二条</b><span>&nbsp;违反法律法规规定，构成犯罪的，依法追究刑事责任。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第四十三条</b><span>&nbsp;人民政府统计机构的统计人员玩忽职守、徇私舞弊、滥用职权的，由所在单位或者有关部门依法给予行政处分；构成犯罪的，依法追究刑事责任。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal"><b>第四十四条</b><span>&nbsp;当事人对行政处罚决定不服的，可以依法申请行政复议或者提起行政诉讼。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>第七章 附则<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal"><b>第四十五条</b><span>&nbsp;本条例自公布之日起施行。</span></p>', 1488047122, 0, 1, NULL, '', '', 0, '信息部', '本站', 0);
INSERT INTO `jl_news` (`id`, `c_id`, `title`, `comment`, `time`, `click`, `outside`, `dept_id`, `imgsrc`, `desc`, `s_id`, `author`, `origin`, `leader_id`) VALUES
(137, 16, '达州市第二次全国农业普查主要数据公报', '<p align="center"><b><span>达州市第二次全国农业普查主要数据公报<span lang="EN-US"><o:p></o:p></span></span></b></p><p></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span>达州市人民政府农业普查领导小组办公室<span lang="EN-US"><o:p></o:p></span></span></b></p><p class="MsoNormal" align="center" style="text-align: center;"><b>达<span lang="EN-US">&nbsp;&nbsp;&nbsp;</span>州<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>市<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>统<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>计<span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span>局</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span></p><p class="MsoNormal" align="center" style="text-align: center;"><b><span lang="EN-US">2009</span></b><b>年<span lang="EN-US">1</span>月</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;</span><span>根据国务院的决定，继<span lang="EN-US">1996</span>年第一次全国农业普查之后，我市和全国、全省同步于<span lang="EN-US">2006</span>年开展了第二次全国农业普查，目的是全面掌握我市农业、农村和农民的基本情况，为研究制定经济社会发展战略、规划、政策和科学决策提供依据，并为农业生产经营者和社会公众提供统计信息服务。普查的标准时点为<st1:chsdate w:st="on" year="2006" month="12" day="31" islunardate="False" isrocdate="False"><span lang="EN-US">2006</span>年<span lang="EN-US">12</span>月<span lang="EN-US">31</span>日</st1:chsdate>，时期资料为<span lang="EN-US">2006</span>年。这次普查对我市所有住在农村或从事农业的单位和个人进行了调查，涉及<span lang="EN-US">104.6</span>万住户、<span lang="EN-US">1224</span>个农业生产经营单位、<span lang="EN-US">2862</span>个村居委会和<span lang="EN-US">311</span>个乡镇。本次普查对象之多、涉及范围之广、工作难度之大，都是前所未有的。通过普查，获取了大量常规统计难以取得的统计数据，摸清了农业的家底，全面掌握了我市“三农”发展的新情况；丰富和完善了常规统计调查的内容，进一步认识了我市“三农”发展的新变化，更加准确地把握了我市“三农”发展的新趋势。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;</span><span>按照国际通行做法和国务院农业普查机构的要求，达州市农业普查机构对农业普查各阶段的质量进行了严格验收并组织了事后质量抽查和对现场调查数据的审核分析，客观、全面地评价了普查登记范围的完整性和登记内容的准确性。<span>综合抽查结果显示，</span>农业普查应登记户的净漏报率为<span lang="EN-US">0.25%</span>，原始数据差错率<span lang="EN-US">0.2%</span>，达到了设计标准。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;</span><span>根据《全国农业普查条例》的有关规定，达州市人民政府农业普查领导小组办公室和达州市统计局向社会公布本次普查的主要结果。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal"><span>一、农业、农村、农民的基本情况</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal"><span>在本次普查中，达州市共调查了<span lang="EN-US">311</span>个乡级行政单位，其中乡<span lang="EN-US">209</span>个，镇<span lang="EN-US">102</span>个；<span lang="EN-US">2862</span>个村级组织，其中<span lang="EN-US">2828</span>个村；<span lang="EN-US">104.6</span>万个住户，其中在农村居住<span lang="EN-US">1</span>年以上的家庭户<span lang="EN-US">104.5</span>万个。<span lang="EN-US"><o:p></o:p></span></span></p><p class="MsoNormal" align="left"><b><span lang="EN-US">&nbsp;1</span></b><b>、农业基本状况</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span lang="EN-US">2006</span><span>年末，全市共有农业生产经营户<span lang="EN-US">102</span>万户，农业生产经营单位<span lang="EN-US">1224</span>个；共有农业从业人员<span lang="EN-US">179.5</span>万人，农业技术人员<span lang="EN-US">9800</span>人。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><b><span lang="EN-US">2</span></b><b>、农村基础设施</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span lang="EN-US">2006</span><span>年末，全市<span lang="EN-US">8.7%</span>的乡镇地域内有火车站，<span lang="EN-US">28.9%</span>的乡镇地域内有二级以上公路通过，<span lang="EN-US">89.4%</span>的乡镇有邮电所，<span lang="EN-US">4.5%</span>的乡镇有公园，<span lang="EN-US">66.2%</span>的乡镇有综合市场，<span lang="EN-US">20.6%</span>的乡镇有农产品专业市场。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span>全市<span lang="EN-US">95.1%</span>的镇实施集中供水，<span lang="EN-US">22.6%</span>的镇生活污水经过集中处理，<span lang="EN-US">43.1%</span>的镇有垃圾处理站。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span>全市<span lang="EN-US">97.5%</span>的村通公路，<span lang="EN-US">100%</span>的村通电，<span lang="EN-US">96.1%</span>的村通电话，<span lang="EN-US">97.3%</span>的村能接收电视节目，<span lang="EN-US">6.1%</span>的村饮用水经过集中净化处理，<span lang="EN-US">2.8%</span>的村实施垃圾集中处理，<span lang="EN-US">31.6%</span>的村有沼气池，<span lang="EN-US">27.2%</span>的村完成改厕，<span lang="EN-US">19.0%</span>的村地域内有<st1:chmetcnv w:st="on" unitname="平方米" sourcevalue="50" hasspace="False" negative="False" numbertype="1" tcsc="0"><span lang="EN-US">50</span>平方米</st1:chmetcnv>以上的综合商店或超市。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><b><span lang="EN-US">3</span></b><b>、农村社会服务</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span lang="EN-US">2006</span><span>年末，全市<span lang="EN-US">6.1%</span>的乡镇有职业技术学校，<span lang="EN-US">97.4%</span>的乡镇有广播、电视站，<span lang="EN-US">100%</span>的乡镇有医院、卫生院，<span lang="EN-US">45.0%</span>的乡镇有敬老院，<span lang="EN-US">88.3%</span>的村在<span lang="EN-US">3</span>公里范围内有小学，<span lang="EN-US">45.8%</span>的村在<span lang="EN-US">3</span>公里范围内有中学，<span lang="EN-US">58.1%</span>的村有幼儿园、托儿所，<span lang="EN-US">3.2%</span>的村有体育健身场所，<span lang="EN-US">4.9%</span>的村有图书室、文化站，<span lang="EN-US">4.4%</span>的村有农民业余文化组织，<span lang="EN-US">76.4%</span>的村有卫生室，<span lang="EN-US">86.9%</span>的村有有行医资格证书的医生，<span lang="EN-US">25.1%</span>的村有行医资格证书的接生员。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><b><span lang="EN-US">4</span></b><b>、农村劳动力资源与就业</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span lang="EN-US">2006</span><span>年末，全市农村常住劳动力资源总量<span lang="EN-US">202.2</span>万人，其中男劳动力占<span lang="EN-US">50.0%</span>。农村常住从业人员<span lang="EN-US">189</span>万人，占农村劳动力资源总量的<span lang="EN-US">93.6%</span>。农村住户（户籍）外出从业劳动力<span lang="EN-US">103.2</span>万人，其中男劳动力占<span lang="EN-US">59.2%</span>。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><b><span lang="EN-US">5</span></b><b>、农村居民生活条件</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span lang="EN-US">2006</span><span>年末，农村常住户平均每人拥有住宅面积<span lang="EN-US">33.4</span>平方米。<span lang="EN-US">99.3%</span>的住户拥有自己的住宅，<span lang="EN-US">31.0%</span>的住户使用管道水，<span lang="EN-US">11.4%</span>的住户炊事能源以柴草为主。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span>平均每百户拥有彩电<span lang="EN-US">65.9</span>台，固定电话<span lang="EN-US">32.4</span>部，手机<span lang="EN-US">48.6</span>部，电脑<span lang="EN-US">0.4</span>台，摩托车<span lang="EN-US">6.4</span>辆，生活用汽车<span lang="EN-US">1.1</span>辆。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal"><span>二、农业基本状况和生产条件</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><b><span lang="EN-US">1</span></b><b>、农业生产经营户和农业生产经营单位</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span lang="EN-US">2006</span><span>年末，全市共有农业生产经营户<span lang="EN-US">102</span>万户，比<span lang="EN-US">1996</span>年第一次全国农业普查时减少<span lang="EN-US">19.7%</span>。全市共有农业生产经营单位<span lang="EN-US">1224</span>个，比<span lang="EN-US">1996</span>年第一次全国农业普查时减少<span lang="EN-US">39.9%</span>。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal"><b>表<span lang="EN-US">1&nbsp;&nbsp;</span>农业生产经营户和农业生产经营单位数量及构成</b><span lang="EN-US"><o:p></o:p></span></p><div align="center"><table class="MsoNormalTable layui-table" cellspacing="0" cellpadding="0" border="0"><tbody><tr><td width="192" rowspan="2"><p class="MsoNormal" align="center" style="text-align: center;"><span>　</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="216" colspan="2"><p class="MsoNormal" align="center" style="text-align: center;"><span>农业生产经营户</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="181" colspan="2"><p class="MsoNormal" align="center" style="text-align: center;"><span>农业生产经营单位</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="120"><p class="MsoNormal" align="right" style="text-align: right;"><span>数量（万户）</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="right" style="text-align: right;"><span>比重（<span lang="EN-US">%</span>）</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="right" style="text-align: right;"><span>数量（个）</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="85"><p class="MsoNormal" align="right" style="text-align: right;"><span>比重（<span lang="EN-US">%</span>）</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="192"><p class="MsoNormal" align="left"><span>合计</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="120"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">102</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">100.0</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">1224</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="85"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">100.0</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="192"><p class="MsoNormal" align="left"><span>按行业分　</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="120"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="85"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="192"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;</span><span>农作物种植业　</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="120"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">96.1</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">94.2</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">76</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="85"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">6.2</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="192"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;</span><span>林业</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="120"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">0.3</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">0.3</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">167</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="85"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">13.6</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="192"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;</span><span>畜牧业</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="120"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">5.4</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">5.3</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">56</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="85"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">4.6</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="192"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;</span><span>渔业</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="120"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">0.1</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">0.1</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">38</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="85"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">3.1</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="192"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;</span><span>农林牧渔服务业</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="120"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">0.1</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">0.1</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="96"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">887</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="85"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">72.5</span><span lang="EN-US"><o:p></o:p></span></p></td></tr></tbody></table></div><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><b><span lang="EN-US">2</span></b><b>、农业从业人员</b><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal" align="left"><span lang="EN-US">2006</span><span>年末，全市农业从业人员<span lang="EN-US">179.5</span>万人，其中，男性占<span lang="EN-US">48.1%</span>，女性占<span lang="EN-US">51.9%</span>。按年龄分，<span lang="EN-US">20</span>岁以下占<span lang="EN-US">3.4%</span>，<span lang="EN-US">21-30</span>岁占<span lang="EN-US">7.3%</span>，<span lang="EN-US">31-40</span>岁占<span lang="EN-US">20.0%</span>，<span lang="EN-US">41-50</span>岁占<span lang="EN-US">19.3%</span>，<span lang="EN-US">51</span>岁以上占<span lang="EN-US">50%</span>；按文化程度分，文盲占<span lang="EN-US">10.6%</span>，小学占<span lang="EN-US">54.2%</span>，初中占<span lang="EN-US">33.1%</span>，高中占<span lang="EN-US">2.0%</span>，大专及以上占<span lang="EN-US">0.1%</span>。</span><span lang="EN-US"><o:p></o:p></span></p><p class="MsoNormal"><b>表<span lang="EN-US">2&nbsp;&nbsp;&nbsp;</span>农业住户从业人员数量及构成</b><span lang="EN-US"><o:p></o:p></span></p><div align="center"><table class="MsoNormalTable layui-table" cellspacing="0" cellpadding="0" border="0"><tbody><tr><td width="396"><p class="MsoNormal" align="center" style="text-align: center;"><span>　</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="160"><p class="MsoNormal" align="center" style="text-align: center;"><span>达州市</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="396"><p class="MsoNormal" align="left"><span>农业从业人员数量<span lang="EN-US">(</span>万人<span lang="EN-US">)</span></span><span lang="EN-US"><o:p></o:p></span></p></td><td width="160"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">179.5</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="396"><p class="MsoNormal" align="left"><span>农业从业人员性别构成（<span lang="EN-US">%</span>）</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="160"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="396"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>男</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="160"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">48.1</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="396"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp;</span><span>女</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="160"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">51.9</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="396"><p class="MsoNormal" align="left"><span>农业从业人员年龄构成（<span lang="EN-US">%</span>）</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="160"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">&nbsp;</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="396"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp; &nbsp;20</span><span>岁以下</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="160"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">3.4</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="396"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp; &nbsp;21-30</span><span>岁</span><span lang="EN-US"><o:p></o:p></span></p></td><td width="160"><p class="MsoNormal" align="center" style="text-align: center;"><span lang="EN-US">7.3</span><span lang="EN-US"><o:p></o:p></span></p></td></tr><tr><td width="396"><p class="MsoNormal" align="left"><span lang="EN-US">&nbsp;&nbsp;&nbsp; 31-40</span><span>岁</span><span lang